INSERT INTO `quest_greeting_locale` (`ID`, `Type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES (823, 0, 'deDE', 'Guten Tag, $C. Normalerweise wrde ich jetzt meine Runde machen und die Leute von Sturmwind beschtzen, doch viele der Wachen von Sturmwind kmpfen in fremden Landen. Daher mache ich jetzt hier die Vertretung und setze Kopfgelder aus, wo ich doch eigentlich lieber auf Patrouille sein wrde...', 0);
INSERT INTO `quest_greeting_locale` (`ID`, `Type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES (240, 0, 'deDE', 'Ach ja, es ist auch ohne diese neuen Probleme schon schwer genug, hier fr Ordnung zu sorgen! Ich hoffe, Ihr bringt gute Neuigkeiten, $N...', 0);
INSERT INTO `quest_greeting_locale` (`ID`, `Type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES (253, 0, 'deDE', 'Aha! Guten Tag, guten Tag, $GMeister:Meisterin; $C! Kommt, setzt Euch und trinkt etwas. Ihr habt so ein Leuchten in den Augen, als wolltet Ihr gern etwas tun. Ich denke, Ihr solltet ein paar Wrtchen mit mir wechseln, es lohnt sich bestimmt...', 0);
INSERT INTO `quest_greeting_locale` (`ID`, `Type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES (241, 0, 'deDE', 'He, mein Freund. Man nennt mich Remy. Ich komme aus dem Rotkammgebirge im Osten und bin auf der Suche nach interessanten... Geschften, interessanten... Geschften. Habt Ihr vielleicht welche... vielleicht welche?', 0);
INSERT INTO `quest_greeting_locale` (`ID`, `Type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES (261, 0, 'deDE', 'Seid gegrt. Ihr seht aus wie $Gein Mann der:eine Frau die; wei, was $Ger:sie; will... habt ihr mit der Armee von Sturmwind zu tun?', 0);
