INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9130, "deDE", "Ja, was gibt's?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9130, "deDE", "Sathiel mchte, dass Ihr nach Silbermond geht und ihre Waren zurckbringt, was? Kein Problem, ich kann Euch schnell dorthin bringen... gegen eine kleine Gebhr, versteht sich.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9133, "deDE", "Das ist eine ganz schn lange Liste, die Ihr da habt, meine Freundin. Lasst mich mal sehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9133, "deDE", "Was soll das? Sie mchte all das!?$B$BHabt Ihr wenigstens jemanden bei Euch, der Euch beim Rcktransport behilflich ist?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9134, "deDE", "Wow, dieses Warenpaket sieht aber mchtig schwer aus! Mchtet Ihr all das auf eins meiner Prachtstcke laden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9134, "deDE", "Zurck nach Tristessa in den Geisterlanden, ja? Alles klar, ich kann Euch schnell an Euer Ziel bringen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9135, "deDE", "$C, schon zurck? Das kann aber unmglich alles sein, was auf der Liste stand!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9135, "deDE", "Hmmm, wenn er gesagt hat, dass der Rest schnell ber den Landweg nachgeliefert wird, dann will ich ihn mal beim Wort nehmen. In der kurzen Zeit, in der wir zusammen gearbeitet haben, hat er es noch immer geschafft, die Waren pnktlich zu liefern.$B$BIch danke Euch, $N. Hier, nehmt diese Mnzen... sie haben schon den ganzen Tag in meiner Tasche geklimpert.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9138, "deDE", "Hervorragend! Doch es gibt noch viel zu tun, bis auch der letzte Rest dieser Landplage namens Geiel aus dem mchtigen Quel'Thalas vertrieben wurde!$B$BHier, Eure Bezahlung... Es ist zwar nicht viel, doch Ihr habt es Euch verdient.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9139, "deDE", "Euer Erfolg in Goldnebel bringt uns wieder einen Schritt nher an die vollstndige Rckeroberung unserer Lndereien. Doch lasst Euch nicht tuschen, $C, diese Schlacht ist noch lange nicht geschlagen.$B$BHier, diese Belohnung habt Ihr Euch mehr als verdient. Sie wird sich als ntzlich erweisen, sobald Ihr nach Windluferdorf aufbrecht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9140, "deDE", "$C, wurde das Windluferdorf befreit? Diese verdammten Magister des Sanktums der Sonne fordern stndig von mir, dass ich ihnen endlich berreste der Geiel gebe, die Ihr gerade fr mich sammeln sollt.$B$BMacht Euch deshalb aber keine Sorgen! Nehmt Euch alle Zeit, die Ihr braucht, um diesen Auftrag sicher zu Ende zu fhren. Ich kmmere mich derweil um die Magister.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9140, "deDE", "Ihr habt es geschafft, $N. Ihr habt drei Drfer aus der Umklammerung der Geiel befreit! Nun knnen wir uns voll und ganz auf die Bekmpfung der Geiel in der Todesschneise und sdlich bei der Todesfestung konzentrieren.$B$BSucht Euch eine dieser Belohnungen aus. Ich hoffe, Ihr bleibt noch ein Weilchen. Wir knnen Eure Hilfe auch weiterhin gut gebrauchen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9143, "deDE", "Habt Ihr die Trollohren?$B$BWisst Ihr, wenn wir ab und an mal etwas Untersttzung aus Tristessa erhalten wrden, wre die ganze Misere hier so nie passiert. Ich bin wirklich froh ber Euer Kommen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9143, "deDE", "Ich schtze Eure Bemhungen, $C! Habt Dank. Wenigstens knnen wir jetzt sagen, dass diese ganze Expedition und die vielen Opfer nicht vergebens gewesen sind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9144, "deDE", "Helft mir, Fremder. Ich muss... Tristessa... erreichen... Ich darf nicht versagen.$B$B<Der Blick des Blutelfen entschwindet ins Leere, und er versinkt wieder in Bewusstlosigkeit.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9145, "deDE", "Es ist gut zu hren, dass Lethvalin sich in Sicherheit bringen konnte, und dass er Euch um Hilfe gebeten hat, anstatt einfach nur abzuwarten.$B$BHoffentlich konnte sich Waldluferin Salissa zur Enklave der Weltenwanderer durchschlagen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9146, "deDE", "Das sind in der Tat schlechte Nachrichten - Ich habe Tomathrens Fhrungsqualitten vllig berschtzt. Ich werde sofort einen Kampftrupp entsenden, um ihn und Valanna sicher zurckzubringen.$B$BUnglcklicherweise haben wir nichts mehr von Waldluferin Salissa gehrt, ich befrchte das Schlimmste.$B$BIhr habt den Weltenwanderern heute einen wertvollen Dienst erwiesen, $C. Bitte nehmt dies als Zeichen unserer Wertschtzung.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9147, "deDE", "Und? Habt Ihr, was ich bentige? Beeilt Euch oder mit dem hbschen $R ist es gleich aus!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9147, "deDE", "Gerade noch rechtzeitig, $N! Ich war drauf und dran fr unseren Freund hier ein schnes Loch auszuheben.$B$BMein Trank scheint zu wirken; er kommt wieder zu sich.$B$BHey, ich glaube er will Euch etwas sagen. Warum versucht Ihr nicht mal mit ihm zu sprechen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9148, "deDE", "Ihr habt den Brief?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9148, "deDE", "Endlich! Wir haben schon seit Wochen auf eine Antwort vom Lordregenten gewartet! Lasst mich mal sehen...$B$B<Vandril beginnt den Brief zu lesen.>$B$BWas soll das? Eine finstere Prsenz? Er sprt eine finstere Prsenz in den Geisterlanden?! Im Ernst, das htte ich ihm auch sagen knnen!$B$BIst das die Nachricht, auf die wir gewartet haben?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9149, "deDE", "Habt Ihr die Exemplare, um die ich Euch gebeten habe, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9149, "deDE", "Ausgezeichnet! Diese Rckgrate sind fr meine Untersuchungen von unschtzbarem Wert! Sollte ich ein Heilmittel gegen die Seuche finden und meinem Namen dadurch Ruhm verleihen, werde ich nicht vergessen, Euch zu erwhnen!$B$BBis dahin, nehmt dies als Belohnung. Ihr knntet davon Gebrauch machen, solltet Ihr noch einmal zur Kste gehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9150, "deDE", "Habt Ihr die Essenzen, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9150, "deDE", "Sie sind in einem bemerkenswert guten Zustand, $N. Ich bin mit Eurer Arbeit sehr zufrieden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9151, "deDE", "Darenis schickt Euch? Gut, ich schtze, wir knnen Euch im Kampf gegen Dar'khans Streitmchte brauchen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9152, "deDE", "Sobald ich den Rest meiner Vorrte habe, kann ich mein Geschft aufnehmen. Die Streitkrfte werden nicht weit kommen, wenn sie nicht das erhalten, womit sie regelmig ihren Handel treiben.$B$BKriegsgebiete knnen so florierend fr das Geschft sein, findet Ihr nicht auch, $C? So, habt Ihr Euch einen Weg durch all die Ghule zu meinem Karren bahnen knnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9152, "deDE", "Dank der restlichen Vorrte hier, kann ich jetzt endlich mit meinem Geschft loslegen. Ich bin mir sicher, dass der Hochexekutor erfreut sein wird, sobald er hrt, dass der Krieg gegen Dar'Khan und die Geiel beginnen kann.$B$BNatrlich gibt es da auch noch Eure Belohnung. Nun gut, $C, hier ist die von mir erwhnte Mnze. Solltet Ihr irgendwelche Handwerkswaren bentigen, schaut einfach vorbei.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9155, "deDE", "Nicht schlecht, $N. Macht weiter so! Leute wie Euch knnen wir immer gebrauchen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9156, "deDE", "Ihr habt etwas, das Ihr mir zeigen wollt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9156, "deDE", "Aha! Luzran und Faulbein! Jetzt seht ihr nicht mehr so gefhrlich aus, was? Aber immer noch genauso hsslich!$B$BNehmt dies als Belohnung, $N. Ihr habt es Euch verdient.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9157, "deDE", "Konntet Ihr die Medaillons beschaffen, $N? Die Ertrunkenen suchen immer noch diesen See heim!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9157, "deDE", "Mithilfe dieser Halsketten werden wir Aquantion erneut beschwren. Der rebellische Elementar wird bezahlen und unser Tod wird gercht werden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9158, "deDE", "Ihr habt Euch bewhrt, $N. Es ist von grter Wichtigkeit, dass die Seuche nicht auch noch die gesunde Fauna des Immersangwalds befllt. Ich habe noch weitere Aufgaben fr Euch, sofern Ihr noch auf der Suche nach Arbeit seid.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9159, "deDE", "Ihr habt Eure Aufgabe sehr ernst genommen und daher mchte ich Euch entsprechend belohnen. Wir brauchen mehr Verbndete mit Eurer Hingabe und Tapferkeit, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9160, "deDE", "Es ist enttuschend, dass es keine offensichtlichen Hinweise auf die Plne der Nachtelfen bei An'daroth gab. Wie auch immer, diese Bume, welche aussahen wie kleine magisch gewachsene Bume mit Energiekugeln auf ihrer Spitze, sind uerst interessant. Es scheint mir, als mssten wir die Art unserer Informationsbeschaffung ein wenig direkter gestalten.$B$BHier, nehmt diese Mnze und die Trnke. Vermutlich werdet Ihr sie noch brauchen, bevor wir diese Angelegenheit mit den Nachtelfen geklrt haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9161, "deDE", "Ihr blttert durch die ersten Eintrge des uralten Buchs und kommt zu dem Schluss, dass es einst der Person gehrte, die heute als Dar'khan Drathir bekannt ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9162, "deDE", "Sagt mir, $N, was habt Ihr herausgefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9162, "deDE", "Diese Information ist fr uns von hohem Wert, $N. Wir alle wussten von Dar'Khans Plnen, die Macht des Sonnenbrunnens fr die Geiel zu stehlen, doch vieles auf diesen Seiten ist vllig neu fr uns. Magister Kaendris beim Sanktum der Sonne wird sich sicherlich sehr dafr interessieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9163, "deDE", "Was machen unsere Bemhungen, die Nachtelfenverschwrung auf der Insel Shalandis aufzudecken, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9163, "deDE", "uerst beunruhigend! Die Plne zeigen die Streitkrfte der Nachtelfen bei An'daroth, von denen wir bereits wussten, doch gibt es wohl eine weitere Gruppe bei An'owyn, einem eher abgelegenen Leyliniennexus im Sdosten.$B$BEs existiert noch ein dritter Nexus im Osten, An'telas, der nicht weiter erwhnt wird. Ich habe da ein ganz mieses Gefhl bei der Sache, $C.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9164, "deDE", "Hervorragende Arbeit, $N. Ohne Eure Hilfe htten diese Gefangenen Ihren Verstand und Ihre Seele an die Geiel verloren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9164, "deDE", "Ich danke Euch, $N. Ohne Eure Hilfe htten diese Gefangenen ihren Verstand und ihre Seele an die Geiel verloren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9166, "deDE", "Gut, dass Ihr hier seid!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9166, "deDE", "Es war schrecklich! Nachtelfen! Sie haben einen Mondkristall beschworen und irgendeine Niedertracht geplant. Glcklicherweise konnte ich mich davonschleichen und einige unserer Spher und Zauberer aus dem nahegelegenen Sanktum der Sonne zu Hilfe holen. Es gelang uns, die meisten von ihnen zu tten. Die anderen sind daraufhin losgezogen, um die berlebenden zu jagen!$B$BIch bin so froh, dass Ihr gekommen seid; man hat mich hier nur mit einigen wenigen Sphern zur Verteidigung zurckgelassen!$B$BGebt mir einen Moment, um diese Plne zu lesen, mglicherweise finde ich heraus, worauf es die Nachtelfen abgesehen haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9167, "deDE", "Ist es getan? Wurde Dar'Khan vernichtet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9167, "deDE", "Am heutigen Tage habt Ihr der Geiel in dieser Region einen tdlichen Schlag versetzt.$B$BNach dem Tod des schndlichen Verrters ist die Rckeroberung unserer Lnder nur noch eine Frage der Zeit. Schon bald werden wir zu altem Ruhm und Glanz zurckgefunden haben!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9169, "deDE", "Ich bin erleichtert, $C! Seid versichert, dass wir ohne Eure Hilfe bei der Abschaltung der Seherkristalle in einer noch viel schwierigeren Lage wren, als wir es sowieso schon sind! Man stelle sich nur vor, umgeben von Feinden und einer von ihnen bese die Fhigkeit, uns auf Schritt und Tritt zu beobachten!$B$BDank Euch mssen wir uns darber keine Sorgen mehr machen! Ich habe hier zufllig noch einige Gegenstnde herumliegen, fr die ich keine weitere Verwendung mehr habe; sicherlich werdet Ihr davon etwas gebrauchen knnen. Hier, nehmt das als Belohnung.$B$BNochmals vielen Dank, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9170, "deDE", "Dar'Khans Armeen fallen! Die Geiel kann nicht gegen die vereinte Macht der Sin'dorei und der Verlassenen bestehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9171, "deDE", "Ich glaube ich kann den Magen der Dame sogar von hier aus knurren hren! Da Ihr hier seid und mich sprechen wollt, gehe ich davon aus, dass Ihr mir meine Zutaten bringt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9171, "deDE", "Sehr schn, das drfte den Zweck erfllen. Nein, erzhlt mir nicht, wie die Dinger rumgekrabbelt sind, ich will es nicht wissen! Ich werde etwas Magie verwenden, dann werden diese Beinchen schon schmackhaft. Ein paar Gewrze, eine Messerspitze Kruter und natrlich ein paar arkane Zutaten  et voil, ein passendes Mahl fr eine Knigin... oder in diesem Fall, eine Dame.$B$BEure Dienste waren zufriedenstellend, $C. Hier, nehmt dieses Rezept und ein paar Portionen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9172, "deDE", "Ihr wnscht mit mir zu sprechen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9172, "deDE", "Dieses Buch... das ist unmglich! Es ist Dar'Khans eigene Handschrift. Irgendwo in diesem Buch muss es einen Hinweis geben, der uns eine Schwche des Gegners aufzeigt.$B$BIhr habt gut daran getan, uns aufzusuchen, $N. Indem sie diese Angelegenheit in unsere fhigen Hnde gaben, haben die Weltenwanderer ihre Weitsicht bewiesen. Diese Rstung sollte Euch helfen, unsere Sache weiterzufhren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9173, "deDE", "Ich bin sicher, Frstin Sylvanas wird ber unsere Fortschritte in dieser Sache erfreut sein.$B$BHier, nehmt dies als Bezahlung fr Eure Mhen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9174, "deDE", "Wurde Aquantion schon zerstrt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9174, "deDE", "Ihr habt unseren Tod gercht und Aquantions Macht ber unsere Seelen gebrochen. Endlich knnen wir ruhen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9175, "deDE", "Was habt Ihr da, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9175, "deDE", "Ihr sagt, Ihr fandet dies bei einem Schergen der Geiel am Windluferturm, und dass es eine Inschrift gibt? Lasst mich sehen!$B$BHier, nehmt dies fr Eure gute Arbeit.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9176, "deDE", "Habt Ihr die Kristalle schon geborgen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9176, "deDE", "Die Geschichten sind also wahr! Der Stein des Lichts und der Stein der Flamme existieren tatschlich! Wir werden ihre Macht nutzen, um wirkungsvolle Waffen gegen Dar'Khan zu schmieden.$B$BWenn Ihr die in diesen Artefakten schlummernden Energien zum richtigen Zeitpunkt freisetzt, werden sie Dar'Khan vernichten. Einst wollte er uns diese Macht nehmen, doch jetzt wird sie sein Untergang sein!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9192, "deDE", "Da habt Ihr mal ordentlich Blut flieen lassen, $C. Die Minenarbeiter knnen jetzt wieder an die Arbeit. Sie sind sicher enttuscht, dass ihre Pause jetzt vorbei ist, aber wir brauchen nun mal mehr Erz, um Rstungen und Waffen herzustellen.$B$BHier, nehmt dies als Lohn fr Eure pflichtbewusste Tat. Lasst Rstung und Waffen reparieren und trinkt einen auf mich.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9193, "deDE", "Das ist eine uerst makabre und wrdelose Angelegenheit, $C. Ich kann Euch doch vertrauen, dass Ihr diesen Auftrag erfllt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9193, "deDE", "Abscheulich, aber glaubt mir, es war notwendig. Ihr habt mit Euren Nachforschungen eine gute Tat vollbracht, $C. Bitte, nehmt dies als Zeichen unserer Grozgigkeit und Anerkennung an.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9199, "deDE", "Ich muss Euch wohl nicht erst sagen, dass wir uns diesen rger mit den von den Toten auferstandenen Trollen hier bei uns kaum leisten knnen! Wir haben schon genug Probleme mit der Todesfestung im Sden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9199, "deDE", "Mumien, die von den Trollpriestern wiedererweckt werden... clever! Die Waldschattentrolle wollen ihre Reihen wohl mithilfe nekromantischer Mittel verstrken. Ich wette, dass diese Orakel das Juju bei ihrem Wiedererweckungsritual verwendet haben.$B$BIhr habt Eure Pflicht wieder einmal so erfllt, wie es sich fr jemanden mit stndig wachsendem Ansehen geziemt. Nehmt diesen Lohn als Dank.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9207, "deDE", "Wie kann ich Euch helfen, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9207, "deDE", "Mein Lehrling konnte sich nicht selbst um diese Angelegenheit kmmern? Ich werde noch ein Wrtchen mit ihr zu reden haben, sobald sie wieder da ist, Gnolle hin oder her. Auerdem, warum ist sie nicht mit Euch zurckgekommen?$B$B<Der Magister seufzt.>$B$BSie ist eine schwierige Persnlichkeit und ihre Ausbildung wird mir noch helle Freude bereiten. Danke, dass Ihr mir diese Proben gebracht habt, $C. Wir hoffen ein paar spezielle Eigenschaften darin zu entdecken, die uns im Kampf gegen die Geiel von Nutzen sein knnen.$B$BBitte nehmt diesen Lohn als Zeichen meiner Wertschtzung.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9212, "deDE", "Ihr seid ein ziemlicher Held, $C, sie da lebend herausbekommen zu haben. Ich denke, wir mssen einen Gegenschlag planen, um diese Katakomben auszuruchern. Als wenn wir nicht schon genug rger mit den lebenden Trollen htten!$B$BIhr habt heute auerordentlich gute Arbeit geleistet, Ihr verdient eine Belohnung. Sucht Euch etwas aus.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9214, "deDE", "Habt Ihr die Waffen beschafft? Sobald wir die Reihen der Waldschattentrolle dezimiert haben, knnen wir uns wieder dem Kampf gegen die Geiel widmen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9214, "deDE", "Ich werde umgehend einige Waldlufer entsenden, um den Schaden, den Ihr unter den Waldschattentrollen angerichtet habt, zu begutachten. Wenn die von Euch mitgebrachte Waffensammlung auch nur ansatzweise den Erfolg Eures Angriffs gegen die Trolle widerspiegelt, sollten wir mit den berlebenden leichtes Spiel haben. Das wird uns einen baldigen Angriff auf die Geiel ermglichen.$B$BHier, nehmt diese Mnze als Zeichen meiner Dankbarkeit, das schafft wieder etwas Platz in meinem Geldbeutel.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9215, "deDE", "Was ist das fr ein widerlicher Gestank?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9215, "deDE", "Ah, Kel'gashs Kopf ist der Quell dieses wundervollen Aromas. Ihr habt offensichtlich ganze Arbeit geleistet, $C!$B$BDer Tod ihres Anfhrers hat die Waldschattentrolle ins Chaos gestrzt. Bercksichtigt man jetzt noch Eure letzten Einstze zur Dezimierung ihrer Anzahl, denke ich, dass jetzt der Zeitpunkt fr unsere Gegenoffensive gekommen ist, um sie ein fr alle Mal zu vernichten.$B$BIhr habt meinen Dank, $N. Kann ich Euch vielleicht fr einen dieser Gegenstnde begeistern?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9216, "deDE", "Habt Ihr die Zombieherzen, nach denen ich verlangt habe, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9216, "deDE", "Nehmt dieses Gebru, $N. Sobald Ihr es getrunken habt, erfhrt Eure krperliche Strke im Kampf gegen die Geiel einen Schub. Wenn Ihr noch mehr von dieser Probe brauchen solltet, bringt mir mehr Herzen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9217, "deDE", "Ihr habt mehr faulende Herzen gebracht? Ich kann alle gebrauchen die Ihr sammeln knnt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9217, "deDE", "Dies wird Euch mit mehr Strke im Kampf gegen die Geiel versorgen. Bringt mir mehr Herzen, falls Ihr welche findet, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9218, "deDE", "Habt Ihr die Proben, nach denen ich verlangt habe, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9218, "deDE", "Hier, fr Euch, $N. Ihr werdet den Unterschied sofort spren!$B$BIhr knnt mir jederzeit mehr Wirbelknochenstaub bringen, ich kann so viel davon verarbeiten, wie Ihr mir besorgen knnt. Nebenwirkungen? Naja, Eure Stimme klingt ein bisschen hher, aber das sind lediglich die Rckstnde des Staubs, die verrckt spielen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9219, "deDE", "Ihr habt mehr Wirbelknochenstaub beschafft? Ich kann alles gebrauchen was Ihr mir bringt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9219, "deDE", "Dieser Wirbelknochenstaub ist nach wie vor mchtig, $N. Bringt mir auch weiterhin welchen, wenn Ihr Verstrkung fr Eure magischen Fhigkeiten im Kampf gegen die Geiel bentigt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9220, "deDE", "Die Geiel hat unsere Rache zu spren bekommen, $N. Nicht mehr lange und ihre verfluchte Zitadelle wird in Trmmern liegen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9274, "deDE", "Ich bin dankbar fr Eure Hilfe, $N. Es schmerzt mich, die Geister meiner Ahnen in diesem jmmerlichen Zustand zu sehen. Mgen sie in Frieden ruhen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9275, "deDE", "Ah, hervorragend! Ich wusste es, ein $R war die richtige Wahl, um das Gift hinreichend zu verteilen. Mit etwas Glck werden die Waldschattentrolle schn fett, bevor sie durch die Wirkung des Gifts in den Nether eingehen. Damit drften die Weltenwanderer ihre Ruhe haben und ich kann endlich meine wohlverdiente Heimreise nach Tristessa antreten.$B$BEs war mir eine Freude mit Euch zu arbeiten, $C!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9276, "deDE", "Gut, sehr gut! Das habt Ihr hervorragend gemeistert! Dann wollen wir doch mal sehen, ob Ihr auch der nchsten Herausforderung gewachsen seid.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9277, "deDE", "Unglaublich! Ihr solltet Euch unbedingt mit unserem Hauptmann ber einen Beitritt in die Reihen der Weltenwanderer unterhalten.$B$BNun gut, $N, Ihr habt uns die dringende Atempause zum Sammeln unserer Streitkrfte verschafft. Genau genommen scheinen unserer letzten Zhlung nach nur wenige Trolle brig zu sein! Vielleicht knnen wir uns nun mit den Streitkrften Tristessas vereinen, um gemeinsam die Todesfestung anzugreifen, anstatt unsere Zeit weiter mit den hiesigen Trollen zu verschwenden.$B$BIch denke, Ihr habt Euch eine kleine Belohnung verdient. Greift zu!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9281, "deDE", "Vielen Dank fr Eure Untersttzung, $N. Mir schaudert bei dem Gedanken, mich so nahe bei der Todesfestung in den Wald zu wagen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9282, "deDE", "Kaendris hat Euch geschickt? Mglicherweise habe ich eine Aufgabe fr Euch, $C.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9315, "deDE", "Ich muss mich fr die schlechte Aufklrung entschuldigen, $C. Htte ich vorher etwas von der Existenz dieser Anok'suten gewusst, htte ich Euch natrlich umgehend davon in Kenntnis gesetzt.$B$BUnsere Krfte sind einfach zu weit verteilt und manche Dinge schlpfen uns unter diesen Umstnden einfach durchs Netz.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9327, "deDE", "Ich wei, wir Verlassenen mgen in Euren Augen monstrs erscheinen, aber ich versichere Euch, wir sind fr Euer Volk keine Bedrohung. Wir sind hier, weil wir einen gemeinsamen Feind haben: Dar'Khan den Verrter! Er ist zurckgekehrt und befehligt die Geiel von der Todesfestung im Sden aus.$B$BUnsere Anfhrerin, Frstin Sylvanas Windlufer, stammt aus dieser Gegend und war frher eine Elfe. Sie hatte schon mit Dar'Khan zu tun und mchte ihn genauso gern tot sehen, wie Euer Volk.$B$BWir werden ihn zusammen besiegen, $C!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9328, "deDE", "Ihr wnscht eine Audienz bei mir, $C? Ich kann mich nicht an eine Verabredung erinnern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9328, "deDE", "Nun, das ist ein Gesicht, das ich noch nicht so frh hier erwartet htte.$B$BDas sind ziemlich gute Neuigkeiten, $N, in mehr Hinsichten, wie Ihr Euch denken knnt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9329, "deDE", "Die Verlassenen sind hier, weil wir und die Blutelfen einen gemeinsamen Feind haben: Dar'Khan! Er ist zurckgekehrt und befehligt die Geiel von der Todesfestung im Sden aus.$B$BFrstin Sylvanas Windlufer, die ursprnglich aus dieser Gegend hier stammt, hatte schon frher mit Dar'Khan zu tun und mchte ihn tot sehen!$B$BWir werden ihn besiegen, $N, und Ihr werdet uns dabei helfen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9487, "deDE", "Ihr seid den Rohlingen nicht zu Nahe gekommen, hoffe ich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9487, "deDE", "Welche Verschwendung, Magie zu nutzen um Wesen von krperlicher Gewalt zu erschaffen. Ich dachte immer, das wre der Grund, warum wir uns mit den niederen Vlkern verbndet htten.$B$BEs war gut von Euch, sie zu zerstren. Nun knnen wir ihre berreste fr angemessenere Zwecke verwenden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9488, "deDE", "Die Robe kann ohne die notwendigen Materialien nicht fertiggestellt werden, aber das ist Euch sicherlich bekannt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9488, "deDE", "Die Details der Herstellung von magischen Roben brauchen Euch nicht zu interessieren, $B, Ihr msst nur Wissen, was Ihr mir zu bringen habt.$B$BUm ehrlich zu sein, Ihr habt mir genug fr mehrere Roben gebracht. Der Profit, den ich damit machen kann, sollte ausreichen, um die Herstellungskosten Eurer Robe zu decken, und mir selbst noch etwas Nettes zu kaufen. Habt Dank!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9621, "deDE", "Ah, ein Besucher aus Quel'Thalas! Welche Neuigkeiten bringt Ihr?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9621, "deDE", "Dann ist es also geschehen. Der gemeine Verrter hat bekommen, was er verdient hat.$B$BIhr habt das selbst getan? Eine beeindruckende Tat, die beweist, dass Euer Volk immer noch wrdig ist, $N.$B$BWie ich sehe, hat Lor'themar weitere Nachrichten, die seine Beziehungen zu den Trollen und ihrem Kriegshuptling deutlich verbessern werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9626, "deDE", "Ihr seid weit gereist, um mich zu treffen. Was bringt Euch nach Orgrimmar?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9626, "deDE", "<Saurfang beginnt, den Brief zu lesen.>$B$BSylvanas ist hartnckig. Sie geht sogar so weit, einen Champion von Silbermond zu schicken... aber was soll das ndern?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9627, "deDE", "Eure Taten waren den Zielen unseres Volkes sehr dienlich.$B$BBereitet Euch auf groe Vernderungen vor, $N. Wir sind nun offiziell ein Teil der Horde.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9758, "deDE", "Ah, gut, da seid Ihr ja. Und gerade zur rechten Zeit, es gibt viel zu tun!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9811, "deDE", "Ihr wnscht eine Audienz bei mir, $C? Ich kann mich nicht an eine Verabredung erinnern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9811, "deDE", "Ihr habt Dar'Khan also gettet, $R? Beeindruckend. Ich habe das Gefhl, dass unsere und Eure Leute bald gute Freunde sein werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9877, "deDE", "Janeda schickt Euch, um die Gefangenen der Todesfestung zu retten? Sie muss eine hohe Meinung von Euch haben, $N.", 18019);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9290, "deDE", "Es ehrt mich, dass Ihr mich wegen meines Wissens aufsucht. Es gibt viel zu tun, wenn sich Eure magischen Fhigkeiten gnzlich entfalten sollen.$B$BSeit mehr als hundert Jahren lehre ich die Knste der arkanen Magie, Frost- und Feuerzauber. Auch auf dieser Welt kann ich ihre Macht spren. Mchtet Ihr, dass ich Euch an meinen Kenntnissen teilhaben lasse?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9279, "deDE", "$N, bei meiner ewigen Seele, es ist gut, Euch zu sehen! Ich wei nicht, warum die Exodar abgestrzt ist. Nur diejenigen von uns, die in diesem Teil des Schiffs waren, sind noch am Leben.$B$BUns bleibt nicht viel Zeit, wenn wir so viele wie mglich retten wollen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9280, "deDE", "$N, habt Ihr schon das bentigte Mottenblut fr die Heilkristalle? berlebende sind ber das gesamte Tal verstreut und die Zeit drngt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9280, "deDE", "Wie bedauernswert, dass diese Kreaturen sterben mussten, damit wir berleben knnen - wahlloses Tten entspricht nicht dem Weg der $R. Wie auch immer, das von Euch gesammelte Blut wird unsere Heilkristalle wieder aufladen, somit war ihr Opfer nicht umsonst.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9283, "deDE", "Dem Licht sei Dank! Ihr habt getan, was nur eine wahre Heldin vollbringen kann, $N! Jeder $R, den Ihr gerettet habt, verdankt Eurer Selbstlosigkeit sein Leben!$B$BBitte, nehmt diesen Vorratsbeutel. Ihr knnt ihn sicher besser gebrauchen als ich.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9289, "deDE", "Es ist gut, dass Ihr meine Weisung ersucht. Ich habe seit Jahrhunderten junge Draeneikrieger ausgebildet, also werde ich Euch schon auf die eine oder andere Weise auf den richtigen Weg eines Kriegers verhelfen knnen. Ich sehe eine Zukunft als mchtiger $C fr Euch voraus, aber nur, wenn Ihr auch wirklich beherzigt, was ich Euch beibringe.$B$BSeid Ihr bereit, junger $C?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9293, "deDE", "Der Zustand aller Proben muss einwandfrei sein, damit wir eine Chance haben, den See zu entseuchen.$B$BUns bleibt nicht viel Zeit, $C!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9293, "deDE", "Genau das habe ich gebraucht. Danke, $C!$B$BWhrend Ihr unterwegs wart, habe ich bereits meine Ausrstung aufgebaut, um die Proben auswerten zu knnen. Ich sollte lediglich ein paar Augenblicke bentigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9294, "deDE", "Euren Schilderungen zufolge hat der Neutralisierungswirkstoff funktioniert! Ich wnschte nur, ich wsste, wie lange er anhlt. Wir mssen den Energiekristall gnzlich aus dem See entfernen.$B$BDanke, $N! Ich werde Tedon bitten, den Neutralisierungswirkstoff sofort an einem der gefangenen Tiere auszuprobieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9303, "deDE", "Je mehr wir tun knnen, um den Nistelwald aufzurumen, desto schneller kann Zhanaa zurck an die Arbeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9303, "deDE", "Da jetzt viele der Eulkin immunisiert wurden, knnen wir uns auf unseren Aufbruch von hier konzentrieren, ohne die gesamte Eulkinpopulation ausrotten zu mssen.$B$BHier, sucht Euch etwas aus. Ihr habt eine so wunderbare Arbeit geleistet, Ihr verdient eine Belohnung!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9305, "deDE", "Ich bin mir sicher, dass wir den Emitter mit den Ersatzteilen reparieren knnen! Mit etwas Glck, werden wir da drauen noch andere finden, mit denen wir uns in Verbindung setzen knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9305, "deDE", "Lasst mich mal sehen.$B$BJa, ich denke, das wird gehen; gute Arbeit, $N! Der Emitter ist ziemlich komplex und deshalb kann die Reparatur ein Weilchen dauern. Ich sage Euch Bescheid, sobald ich fertig bin.$B$BSchaut in der Zwischenzeit bei Verteidiger Aldar vorbei und fragt ihn, ob er Euch brauchen kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9309, "deDE", "Hilfe... Blutelfen! Ein... ein Hinterhalt. Ich habe... nicht mehr lange...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9311, "deDE", "Danke, dass Ihr Euch um die Gutachterin gekmmert habt. Sie war ganz sicher ihre Anfhrerin. Ich versichere Euch, dass wir uns um den restlichen Haufen, der sich eventuell noch hier in der Nhe aufhlt, kmmern werden.$B$BBitte, nehmt eine dieser Waffen als Zeichen unserer Dankbarkeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9312, "deDE", "Ja, ja! Ich glaube, ich habe den Emitter reparieren knnen! Hier, ich muss lediglich den letzten Kristall aktivieren und das sollte es gewesen sein.$B$BSeht, $N, der Emitter. Er scheint zu funktionieren... es erscheint jemand", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9313, "deDE", "Ah, es ist gut, Euch wiederzusehen! Ich bin froh, dass wir beide unsere Emitter reparieren konnten, sonst htten wir uns wohl nie gefunden. Sorgt Euch nicht, wir werden natrlich Hilfe und Vorrte ins Am'mental schicken.$B$BDa wir gerade von Hilfe sprechen... jetzt wo Ihr hier seid, knnt Ihr Euch ja mal umsehen und Euch bei den anderen vorstellen. Ich wei, dass es jede Menge zu tun gibt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9314, "deDE", "Oh, Ihr msst einer der berlebenden sein, von denen wir gehrt haben!$B$BIch bin froh, dass Ihr gekommen seid. Keine Sorge, wir schicken jemanden los, der sich Aeuns verletztes Bein ansehen wird.$B$BIch will unbedingt alles ber Eure aufregende berlebensgeschichte hren! Wenn Ihr bis hierhergekommen seid, msst Ihr sehr tapfer sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9371, "deDE", "Oh, welch Glck, dass Ihr hier seid! Wir haben Einiges zu tun.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9369, "deDE", "$N, habt Ihr schon das bentigte Mottenblut fr die Heilkristalle? berlebende sind ber das gesamte Tal verstreut und die Zeit drngt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9369, "deDE", "Wie bedauernswert, dass diese Kreaturen sterben mussten, damit wir berleben knnen - wahlloses Tten entspricht nicht dem Weg der $R. Wie auch immer, das von Euch gesammelte Blut wird unsere Heilkristalle wieder aufladen, somit war ihr Opfer nicht umsonst.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9409, "deDE", "Hallo, $C. Was habt Ihr da? Ist das fr mich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9409, "deDE", "Gute Arbeit! Das wird die erschpften Heilkristalle wieder aufladen.$B$BDa Ihr nun schon einmal hier seid... ich htte da noch eine Aufgabe fr Euch, solltet Ihr gewillt sein?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9421, "deDE", "Ich bin Firmanvaar und mich aufzusuchen war weise von Euch. Ich bin dazu berufen, Schamanen den richtigen Weg zu weisen, besonders denen, die wie Ihr noch unerfahren sind.$B$BDer Schamanismus wird von uns Draenei gerade erst wiederentdeckt, $N. Ihr seid wahrhaft mutig, dieser Lehre zu folgen, denn unter Unseresgleichen wird sie mit Argwohn betrachtet.$B$BIch bin hier, solltet Ihr meine Unterweisung wnschen. ", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9449, "deDE", "Es ist sehr khn von Euch, meinen Rat zu ersuchen, nach all dem, was Eure Leute diesen Inseln angetan haben, $GFremder:Fremde;! Dennoch, Euer Mut spricht fr sich, also werde ich Teile meines Wissens preisgeben.$B$BDie Erde unter Euren Fen ist die Grundlage aller Dinge. Der Himmel, das Wasser, sogar groe Feuer - alle lasten auf ihren Schultern. Whrend die anderen oft in wilden Strmen toben, hlt die Erde geduldig aus. Sie verleiht Eurem Innern Strke und Kraft.$B$BJetzt werdet Ihr Euch beweisen, $C.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9450, "deDE", "Das Gleichgewicht der Elemente scheint noch immer gestrt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9450, "deDE", "Ihr habt Euch gut geschlagen, $C. Indem Ihr die Zahl der ruhelosen Geister reduziert habt, habt Ihr geholfen, das Gleichgewicht der Elemente wiederherzustellen, das Eure Leute unbeabsichtigterweise zerstrt hatten.$B$BDas Gleichgewicht muss immer gewahrt bleiben. Auf Eurem Lebensweg als Schamane, drft Ihr diese Lehre nie vergessen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9451, "deDE", "Zusammen werden die Zerschlagenen und die Draenei ein tieferes Verstndniss fr die Mysterien der Elemente wiedererlangen.$B$BSeid Ihr mit der fr die Herstellung Eures Erdtotems notwendigen Komponenten zurckgekehrt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9451, "deDE", "Ausgezeichnet, $N. Indem Ihr das Gleichgewicht der Elemente im Hain wieder hergestellt habt, habt Ihr auch fr einen Einklang zwischen den Erdelementen dieser Welt und den Draenei gesorgt. Mglicherweise zeigen diejenigen unter uns, die unseren Weg ablehnen, nun mehr Verstndnis?$B$BIch werde ein Totem fr Euch erschaffen, das mit den Elementen der Erde verbunden ist. Mit dessen Hilfe werdet Ihr die Krfte der Erde auf Euren Wunsch herbeirufen knnen. Mit mehr Weisheit, werden sich Euch auch noch mehr Geheimnisse der Erde erschlieen.$B$BHier, nehmt Euer Totem, $C.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9452, "deDE", "Nehmt Euch vor den Murlocs in Acht: seltsame und extrem lstige kleine Kreaturen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9452, "deDE", "Ich hoffe Ihr hattet nicht zu viele Scherereien wegen der Murlocs, $N. Ich bin Euch sehr dankbar fr alles, was Ihr getan habt.$B$BSoll ich Euch vielleicht das Angeln beibringen? Ich kann Euch sogar eine Angelrute und ein glnzendes Schmuckstck geben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9453, "deDE", "Was haltet Ihr von meiner Rstung? Habe sie selbst hergestellt! Sie unterscheidet sich zwar etwas von den traditionellen Rstungen, die wir normalerweise tragen, ist aber sehr bequem.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9453, "deDE", "Arme Diktynna... der Murloc hat ihr wohl einen ziemlichen Schrecken eingejagt.$B$B<Acteon seufzt.>$B$BHoffentlich heilen ihre Verletzungen bald...$B$BIhr haltet also nach Arbeit Ausschau, nun, davon gibt es bei der Azurwacht jede Menge!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9454, "deDE", "Die Blge der Hirsche taugen nichts, dafr ist ihr Fleisch kstlich zart.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9454, "deDE", "Gut gemacht, $N, gut gemacht!$B$BHier, whrend Ihr fort wart, habe ich ein paar der Lenden, die Ihr mir gebracht habt, zubereitet. Vielleicht wollt Ihr ja auch das Rezept haben?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9455, "deDE", "Was habt Ihr gefunden? Wo?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9455, "deDE", "Wie konnte solch ein Ding nur im Magen eines Nachtpirschers enden? Knnten die Trmmer des Absturzes noch mehr Kreaturen verseucht haben?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9456, "deDE", "Ich hoffe nur, dass es nicht schon zu spt ist...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9461, "deDE", "Hallo, $N, ich habe Euch erwartet. Heute scheint Euer Tag zu sein, nicht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9462, "deDE", "Hallo, $N, ich habe Euch erwartet. Ich hoffe, Ihr seid bereit, Eure Ausbildung fortzusetzen. Die nchste Aufgabe wird schwierig werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9463, "deDE", "Ich frchte, ihr Zustand verschlimmert sich zusehends.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9463, "deDE", "Lasst uns hoffen, dass diese Salbe ihren Lebenswillen neu belebt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9464, "deDE", "Sehe ich richtig, dass das Funkeln in Euren Augen Euren Wunsch heranzuwachsen und zu verstehen widerspiegelt? Gut!$B$BTuluun hat Euch hierher geschickt, da er erkannt hat, dass Ihr fhig seid zu lernen und uns bei einem Problem zu helfen, das durch den Absturz der Exodar verursacht wurde.$B$BWir werden sehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9465, "deDE", "Habt Ihr die Fackel?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9465, "deDE", "Seht Ihr? Das Feuer geht niemals aus. Das ist das Werk Hauteurs. Es ist das Symbol seines Hochmuts. Flammen sollen verzehren, was sie selbst brennen lsst und dann sterben, um ein andermal wieder entfacht zu werden.$B$BIch wrde Euch ja beim Lschen der Fackel helfen, aber sie muss noch brennen, damit Ihr Euch Hauteur stellen knnt. Lassen wir sie noch ein wenig lnger am Leben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9467, "deDE", "Sprecht erst wieder mit mir, wenn Ihr Hauteurs Asche habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9467, "deDE", "Ihr habt heute eine besonders wichtige Lektion gelernt. Es ist die Geschichte vom Ende und vom neuanfang.$B$BAls wir und das erste Mal unterhielten, sagte ich Euch, dass das Feuer nicht nur Zerstrung verkrpert, sondern auch die erneuernde Kraft der Elemente. Durch Euer Handeln habt Ihr Hauteur zerstrt und gleichzeitig gerettet, auf das er aus seiner Asche wiedergeboren wird.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9468, "deDE", "Ihr scheint Euch an ein paar Ecken versengt zu haben. Wie erging es Euch bei der Zusammenkunft mit der Flamme, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9468, "deDE", "Ihr habt das reinigende Feuer der Zerstrung erfahren und seid erneuert worden, $N. Dies ist nichts im Gegensatz zu dem Elend, das die Draenei derzeit erleiden. Ihr habt Hauteur vor sich selbst gerettet und somit Eure Bindung zu dem Element des Feuers in dieser Welt gesichert.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9473, "deDE", "Verschwendet keine Zeit, Junge.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9473, "deDE", "Seht, wie die Pflanze ihre wundersame Wirkung entfaltet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9505, "deDE", "Ihr sagt, dass Ihr eines unserer Besatzungsmitglieder gefunden habt und dass sie schwer verletzt ist? Ich werde umgehend ein Mitglied meiner Besatzung in Euer Dorf entsenden!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9506, "deDE", "Hattet Ihr Erfolg, Fremder?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9506, "deDE", "Ihr seid verdammt einfallsreich! Jetzt, da wir unsere Navigationsinstrumente zurckerhalten haben, sollte es leicht sein, herauszufinden, wie weit wir vom Kurs abgekommen sind.$B$BAber wartet, was ist das? Da ist etwas in der Karte eingerollt. Lasst mich mal sehen...$B$BDas sind Befehle von Mogul Raztunk der Venture Company. Laut diesen Plnen sind sie hier, um die Insel ihrer Kristalle zu berauben und unsere Schiffe zu kapern, wenn sie fertig sind!$B$BBei Bronzebarts buschiger Braue! Deswegen haben sie uns an Land gezwungen? Wie konnten sie von unseren Plnen erfahren?$B$BDas riecht nach einem Verrter...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9512, "deDE", "Arrr... Jarr... beim Klabautermann... Eh, 'tschuldigung Junge, ich muss mal wieder 'n bisschen Pirat spielen, bevor ich's ganz vergesse...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9512, "deDE", "Erste Sahne, $GFremder:Fremde;. Das wird helfen, die Moral der Mannschaft wieder mchtig aufzumbeln.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9513, "deDE", "Gelobt sei Elune! Mgen die ruhelosen Geister endlich Frieden finden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9514, "deDE", "Ihr habt etwas entdeckt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9514, "deDE", "Ich kann diese Runen leider nicht entziffern, doch ich kenne jemanden, der dies zu tun vermag...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9515, "deDE", "Nach dem Tod von Kriegsherr Sriss'tiz drfte die Gefahr einer Nagainvasion frs Erste gebannt sein...$B$BIch muss gestehen, es war falsch von mir, so schlecht ber Euch zu denken, $N. Ihr habt bewiesen, dass Ihr weit entfernt seid vom Abschaum, der sich Archimonde nannte. Bitte nehmt dieses Geschenk der Nachtelfen als ein Zeichen der Freundschaft entgegen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9523, "deDE", "Kommt nicht ohne uralte Relikte zurck.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9523, "deDE", "Mein Paps wre stolz! Sein Junge hat bei den ersten Nachtelfenruinen, auf die er gestoen ist, das groe Los gezogen... dank Eurer Hilfe natrlich. Ja, macht Euch keine Sorgen, ich werde Euch nicht von der Entdeckung ausschlieen. Euer Name ist $N, stimmt's? In Eisenschmiede werden alle von Euch hren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9527, "deDE", "Meine Frau hie Thalrisa und meine Tochter Magwin.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9527, "deDE", "Ich danke Euch, $N. Ihre Geister knnen nun in Frieden ruhen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9528, "deDE", "<Freudentrnen strmen ber Cowlens Gesicht.>$B$BWie kann ich Euch dafr jemals genug danken, $N? Bitte, nehmt dieses Erbstck. Es symbolisiert die heiligen Bande der Freundschaft sowie das Vertrauen und die Verbundenheit zwischen den Nachtelfen. Ihr seid wie ein Bruder fr mich - vom heutigen Tag bis in alle Ewigkeit...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9530, "deDE", "Ich liebe es, wenn ein Plan aufgeht...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9530, "deDE", "Ich muss Euch warnen: Das, was ich als Nchstes von Euch verlangen werde, ist sehr gefhrlich. Ich wrde es Euch nicht verdenken, wenn Ihr mir dabei lieber nicht aushelfen wollt, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9531, "deDE", "Der Verrter wird noch bereuen, dass er sich mit dem Marinekommando der Allianz angelegt hat!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9531, "deDE", "Ich kann es nicht glauben! Der Gnom war es, die ganze Zeit ber... Ich htte es wissen sollen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9537, "deDE", "Habt Ihr den elenden Lgner gefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9537, "deDE", "Unglaublich! Das gehrte alles zu ihrem perfiden Plan!$B$BIhr msst diese Informationen dem Knig und Euren eigenen Anfhrern zukommen lassen. Die Folgen sind erschtternd.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9538, "deDE", "Das Totem weist zahlreiche Symbole der Fibel auf. Ihr knnt Eulen, Bren, Wlfe und Hirsche erkennen, die darauf eingraviert wurden.$B$BEuch fllt eine seltsame Ansammlung von Symbolen auf, die Eure Lippen das Wort A-K-I-D-A formen lassen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9539, "deDE", "Das Totem weist zahlreiche Symbole der Fibel auf. Ihr knnt Eulen, Bren, Wlfe und Hirsche erkennen, die darauf eingraviert wurden.$B$BEuch fllt eine seltsame Ansammlung von Symbolen auf, die Eure Lippen das Wort C-O-O formen lassen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9540, "deDE", "Das Totem weist zahlreiche Symbole der Fibel auf. Ihr knnt Eulen, Bren, Wlfe und Hirsche erkennen, die darauf eingraviert wurden.$B$BEuch fllt eine seltsame Ansammlung von Symbolen auf, die Eure Lippen das Wort T-I-K-T-I formen lassen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9541, "deDE", "Das Totem weist zahlreiche Symbole der Fibel auf. Ihr knnt Eulen, Bren, Wlfe und Hirsche erkennen, die darauf eingraviert wurden.$B$BEuch fllt eine seltsame Ansammlung von Symbolen auf, die Eure Lippen das Wort Y-O-R formen lassen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9542, "deDE", "Beim Lesen des Totems formen Eure Lippen das Wort V-A-R-K. Ihr versteht die Bedeutung, sie lautet Gerechtigkeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9544, "deDE", "[Furbolg] Die Prophezeiung sprach von jemandem, der sich erheben und uns aus dem gewaltsamen Griff der Sichelklauen befreien wrde; dass die Geister selbst die Ankunft dieses Helden verknden wrden.$B$BJetzt seid Ihr gekommen.$B$BIch beuge mich vor Euch in Demut, Groartiger.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9555, "deDE", "Seid gegrt. Es ist stets ermutigend, einen Draenei zu treffen, der sich dem Weg des Schamanismus erffnet. Es gibt leider nur wenige.$B$BIhr habt nun gelernt, das Feuer nicht nach seinem ueren zu beurteilen, genauso, wie Ihr mein ueres nicht beurteilen solltet.$B$BIhr seid hier, weil Ihr bereit seid, Euer Feuertotem zu erschaffen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9559, "deDE", "Jahrhunderte lang lebten wir in verhltnismigem Frieden mit den Kreaturen des Landes. Vor einigen Monaten begann Kurz, dunkle Vorzeichen zu sehen. Der Frieden, den wir so lange genossen hatten, schien in Gefahr. Keiner konnte die Ausmae dieser Katastrophe erahnen!$B$BUnd so kam es, dass die Gtter unser Land straften und sowohl Feinde als auch Freunde gegen uns richteten. Aber es gab da noch etwas... inmitten des Chaos sollte sich ein Held erheben: ein Held, in dessen Adern nicht das Blut der Tannenruh fliet. Der Auserwhlte wrde uns vor der Zerstrung erretten.$B$BDer prophezeite Held wart Ihr.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9560, "deDE", "Es sind hssliche kleine Kreaturen. Sogar der blinde Behter knnte sie sehen... und er ist blind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9560, "deDE", "Nicht nur, dass die Prophezeiung wahr ist, nein, diese Rstung ist ebenso erstaunlich! Eine Rstung fr einen Helden! Ich werde sie Euch fr einen besonders gnstigen Preis verkaufen, Fellloser.$B$BDas war ein Scherz... das erste Teil gibt's umsonst.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9562, "deDE", "Ein Murloc hat mir in den Hintern getreten und mich angespuckt... und das am helllichten Tag!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9562, "deDE", "Mmmmm... kstliches Getreide... Parkat wird sich freuen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9564, "deDE", "Ist das etwa das, was ich vermute?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9564, "deDE", "<Gurf spuckt auf den Fellfetzen und versucht ihn auf die kahle Stelle auf seinem Hintern anzubringen.>$B$BDas muss vorerst halten! Ich hoffe, dass einer der Schamanen den Schaden wieder beheben kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9565, "deDE", "Der Blutkristall verstrahlt eine krnkliche Aura, die in das Wasser im Keller sickert.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9566, "deDE", "Von den Blutkristallen, wie Ihr sie gesehen habt, wurden von meinen Sphern noch viele weitere im Norden gesichtet.$B$BIch danke Euch fr die Hilfe, muss Euch nun aber bitten zu gehen, weil ich mich noch mit den ltesten beraten muss. Ich werde nach Euch rufen lassen, sobald ich mit den anderen ltesten des Dorfes gesprochen habe.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9570, "deDE", "<Kurz schnaubt Euch an.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9570, "deDE", "Der Held ist erfolgreich zurckgekehrt!$B$BDie Prophezeihung hat sich fast vollstndig bewahrheitet. Jetzt zu dem Balg...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9571, "deDE", "Hallo, Held!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9571, "deDE", "Das ist die strkste Rstung, die ich jemals angefertigt habe! Nutzt sie weise, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9573, "deDE", "Das knnte die Gelegenheit sein, auf die wir so lange gewartet haben! Ich werde sofort angreifen lassen! Der Stamm der Tannenruhfeste dankt Euch fr Euren Einsatz, $N!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9602, "deDE", "Ihr seid zurck!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9602, "deDE", "<Exarch Menelaous nimmt die Mitteilung von Euch entgegen und beginnt sie zu lesen.>$B$BVerdammt... verdammt sie alle in den Nether. Sie hatten einen Spion, der sie ber all unsere Aktivitten informiert hat! Und seit wann?$B$BIch werde sofort Velen informieren!$B$BIhr habt uns einen groen Dienst erwiesen, $N. Haltet die Augen offen, ich werde mich an Euch wenden, sobald der Prophet mir mitgeteilt hat, was es zu tun gilt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9603, "deDE", "Guten Tag, $N. Interessiert Ihr Euch fr einen Hippogryphenflug?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9603, "deDE", "Ihr msst also zu der Exodar?$B$BDas ist ein ganz schn weiter Weg, aber keine Sorge, ich wei schon, wie Ihr auf dem schnellsten Weg an Euer Ziel kommt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9604, "deDE", "Seid gegrt, Freund. Ihr seht aus, als httet Ihr einen langen Weg hinter Euch. Wie kann ich Euch helfen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9604, "deDE", "Ah, Ihr habt also Chellans Liste mitgebracht. Ausgezeichnet! Ich hatte gehofft, dass ich sie heute erhalten wrde. Lasst mal sehen, was sie braucht.$B$B<Nurguni berfliegt die Liste und beginnt verschiedene Gegenstnde in eine Kiste zu packen.>$B$BDas sind fast alle Dinge, die sie aufgelistet hat. Die Teile fr die Betten sind einfach zu gro, um auf einem Hippogryphen transportiert werden zu knnen. Ich werde dafr sorgen, dass sie mit einem Wagen nachgeliefert werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9605, "deDE", "Wohin wollt Ihr, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9605, "deDE", "Ihr wollt also mit dieser Kiste zurck zur Azurwacht? Kein Problem, $C. Sagt einfach Bescheid, wenn's losgehen soll.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9606, "deDE", "Willkommen zurck, $N. Konnte Nurguni Euch alle Vorrte mitgeben, die auf der Liste standen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9606, "deDE", "<Ihr berreicht die Kiste und erklrt, dass Nurguni dafr sorgt, dass die greren Gegenstnde mit einem Wagen nachgeliefert werden.>$B$BGroartig! Ich werde jetzt endlich ein paar Lagersttten und eine Versorgungsstation einrichten knnen. Hier wird man sich in Zukunft um Verwundete und mgliche weitere berlebende kmmern knnen.$B$BIch wei gar nicht, wie ich Euch fr Eure Hilfe danken kann, $N. Die Anerkennung von mir und den Verteidigern der Azurwacht ist Euch sicher.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9612, "deDE", "Gut gemacht, $N. Sehr gut gemacht! Ihr seid $Gein besonderer:eine besondere; $R - Weiter als andere in Eurem Alter. Nehmt dies als Belohnung!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9616, "deDE", "Ja? Ich bin wirklich sehr beschftigt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9616, "deDE", "Beim bartlosen, quadratischen Schdel von O'ros! Gut, dass Ihr das zu mir gebracht habt, $N. Diese Blutelfen haben es... nun ja, auf Blut abgesehen. Velen wird davon sofort in Kenntnis gesetzt werden! Nehmt dies als Anerkennung.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9622, "deDE", "Visionen sagt Ihr? Interessant... und diese Prophezeiung hat sich also bewahrheitet?$B$BIhr sagt jedoch, dass man den Kraftkern der Exodar gesehen hat? O'ros erschien auch in der Vision? Was kann das nur bedeuten?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9623, "deDE", "<Torallius rckt seine Augenklappe zurecht und sieht Euch an.>$B$BHaben wir der Hand von Argus nichts Besseres anzubieten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9623, "deDE", "<Torallius seufzt.>$B$BNun gut, lasst mich einen Blick auf diese Befehle werfen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9625, "deDE", "Naja, zumindest habt Ihr's bis hierher geschafft! Jetzt schicken wir Euch lieber an die Arbeit, bevor Ihr noch zertrampelt werdet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9798, "deDE", "Was habt Ihr denn da?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9798, "deDE", "Hier, lasst mich einen Blick auf diese Plne werfen.$B$BDie Blutelfen sind uns hierher gefolgt? Sie sind durch und durch bsartig und sollten ganz und gar ausgelscht werden!$B$B<Der Verteidiger braucht einen Moment, um sich wieder zu fassen.>$B$BEntschuldigung, ich htte mich beherrschen sollen. Wir stehen fr die Entdeckung dieser Informationen in Eurer Schuld, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9799, "deDE", "Hallo, $C. Habt Ihr die Blumen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9799, "deDE", "Das sieht mir nach ein paar guten Exemplaren aus. Danke fr Eure kleine Hilfsarbeit. Sobald ich Zeit zum Untersuchen der Blumen habe, sollte ich bestimmen knnen, was es zur Wiederherstellung der Felder bedarf.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10302, "deDE", "Ihr seid fertig? Das sind gute Nachrichten. Hoffentlich finden wir einen Weg, bei dem wir diese mutierten Kreaturen nicht tten mssen.$B$BIch glaube, ich habe einen Plan entwickelt, der uns eine alternative Lsungsmglichkeit fr unser Problem bietet, aber ich werde Eure Hilfe brauchen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10303, "deDE", "Diese Blutelfen wollen uns alle tten. Wie knnen wir sie davon abhalten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10304, "deDE", "$N, richtig? Ich habe viel Gutes ber Euch und die Arbeit, die Ihr in der kurzen Zeit, in der Ihr hier wart, geleistet habt, gehrt.$B$BWir sind auf Eure Hilfe angewiesen, um die Dinge wieder zu normalisieren. Ich bin mir sicher, dass auch Zhanaa da drben Eure Hilfe brauchen knnte.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10324, "deDE", "Die Blge der Bcke sind grob und stark und halten das Fleisch zh. Es ist kaum essbar, aber wir knnen es fr andere Zwecke einsetzen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10324, "deDE", "Wunderbar! Ihr entwickelt Euch zu einem beachtlichen Jger, $N.$B$BWrdet Ihr mir gerne hnlich sehen? Nun, hier ist Eure Chance! Whrend Ihr auf der Jagd wart, habe ich diese Gegenstnde aus den Blgen, die ich hier noch herumliegen hatte, gefertigt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10428, "deDE", "Meine Familie... Was habe ich getan... Was htte ich tun sollen?$B$BWarum werde ich so bestraft?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9501, "deDE", "Ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9501, "deDE", "Frchtet Euch nicht, $N. Wir werden hier mit Hilfe unserer Gedanken miteinander kommunizieren.$B$BIch bin Weissager Nobundo dankbar, dass er Euch zu mir gesandt hat. Etwas verunreinigt das Wasser auf und um die Blutmythosinsel herum. Nicht einmal einem Blinden wrde der Effekt entgehen, den diese Strung auf die Kreaturen hat.$B$BIhr und ich werden zusammen gegen diese Verderbnis vorgehen, bevor sie sich noch weiter ausbreitet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9504, "deDE", "Habt Ihr die Probe Reinsten Wassers aus den Ruinen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9504, "deDE", "Wasser schwindet schnell, vereinigt man aber genug davon, bildet es eine unaufhaltsame Kraft. Es splt Erde und Feuer hinfort und bahnt sich auch durch die Luft seinen Weg.$B$BWir sind diese unaufhaltsame Kraft, $N, nicht diejenigen, die die Blutmythosinsel vergiftet haben!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9508, "deDE", "Wart Ihr Erfolgreich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9508, "deDE", "Wir haben es geschafft! Der Katastrophe wurde vorgebeugt und Ihr habt die Erholung der Gewsser von der Blutmythosinsel in Gang gesetzt. Mit der Zeit wird sich das Wasser, in Verbindung mit den anderen Elementen, wieder von selbst heilen.$B$BEs ist wirklich traurig, die Blutelfen sehen einfach nicht, wie sie sich durch die Zerstrung ihrer Umgebung langsam selbst vernichten!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (5921, "deDE", "Und so beginnt es, Vater Cenarius. Und so beginnt es.$B$BDendrite macht eine schnelle, unsichtbare Geste in die Luft ber ihm. Eine leichte Aura der Macht fllt ber ihn.$B$BDie erste Perspektive der Natur, die Ihr Euch aneignen msst, ist die des Bren. Ich werde Euch auf den Weg bringen, um diesen Aspekt des Druidenlebens zu verstehen, aber Ihr msst ihn Euch zu Eigen machen und pflegen - von nun an und fr immer!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (5925, "deDE", "Ich bin froh, dass Ihr heute Euren Weg hierher gefunden habt. Es ist an der Zeit fr Euch, einen groen Schritt in eine noch viel grere Welt zu tun.$B$BIn jedem von Cenarius' Kindern steckt die Berufung, der Natur zu dienen. Tiere und Pflanzen sind unsere Freunde wie auch unsere Schtzlinge. Wir haben uns entschlossen, sowohl zu ihrem wie auch unserem Wohl unsere Leben dem Erhalt des Gleichgewichts zu widmen. Euer erster Schritt in diese Welt wird es sein, etwas ber den Weg des Bren und die Strke von Krper und Herz zu erlernen.$B$BMacht Euch bereit!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (5929, "deDE", "Kehrt erst zurck, wenn der groe Brengeist Euch wieder zu mir zurckschickt. Eure Ausbildung kann ohne seinen Segen nicht weitergehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (5929, "deDE", "Dendritte macht eine weitere unsichtbare Geste in der Luft ber ihm, als Ihr Euch ihm nhert. Eine erneute Aura der Macht legt sich auf ihn und er lchelt ein wenig.$B$BIch spre Weisheit in Euch, Weisheit, die nicht zugegen war, als wir uns das erste Mal trafen. Ihr blickt mit Strke und verstndnisvollem Eifer zu mir auf, auch wenn dieser Eifer noch nicht sehr zielgerichtet ist. Ihr werdet nun lernen, Eure Strke zu fokussieren und das Wesen des Bren in Eure Arbeit anzuwenden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (5931, "deDE", "Willkommen, $N. Ich spre, dass Eure erste Begegnung mit dem groen Brengeist genauso verlief wie bei allen Druiden, wenn sie die ersten Schritte auf dem Pfad der Klaue tun... etwas verwirrend, aber sehr eindrucksvoll. Ich wei, dass es fr mich damals auch so war.$B$BDer groe Brengeist ist schon so lange ein Teil von Azeroth wie Azeroth in den Himmeln existiert. Wir verlassen uns heute auf seine Weisheit und seine Macht, um unseren Absichten Antrieb zu verleihen. Nun ist es an der Zeit fr Eure erste Prfung dieser Absichten. Hrt mir gut zu...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (6001, "deDE", "Wenn Ihr der Herausforderung getrotzt habt, die vor Euch liegt, wird Euch das ganze Wissen ber die Kraft des Krpers und des Herzens zuteil werden. Bis dahin kann ich Euch nicht weiter behilflich sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (6001, "deDE", "Endlich habt Ihr den Schritt in eine viel grere Welt getan, $N. Ich spre die Lehre des groen Brengeistes in Euch, und auch, dass Ihr die Kraft erhalten habt, die Mondklaue besa.$B$BKein weiteres Hindernis versperrt Euren Weg... lasst mich Euch jetzt lehren, was es bedeutet, ein $C der Klaue zu sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9548, "deDE", "Konntet Ihr meine Ausrstung finden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9548, "deDE", "Das ist fantastisch! Der Diebstahl meiner Ausrstung hat mir zu noch mehr Einsicht in das Verhalten der Murlocs verholfen, als ich womglich mittels wochenlanger Beobachtungen htte gewinnen knnen!$B$BIch wei schon, was ich in meinen Bericht schreiben werde.$B$B'Beim Aufeinandertreffen mit Erzeugnissen einer technologisch hher gestellten Kultur, zeigten die Murlocs der Schwarzschlammkste nicht die sonst typische Neugier wie humanoide Entwicklungskulturen.$B$B'Das ist groartig! Die Herren in Eisenschmiede werden es lieben! Ich kann Euch nicht genug danken, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9549, "deDE", "Habt Ihr die Gegenstnde, die ich nach Eisenschmiede bringen mchte?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9549, "deDE", "Verblffend, einfach verblffend! Diese Stcke sind perfekt, $N. Ich kann sie mir bereits unter Glas im Museum von Eisenschmiede vorstellen. Der Text fr die Vitrine schreibt sich in meinem Kopf gewissermaen von selbst!$B$BEure Hilfe war fr den Fortgang meiner Studien von unschtzbarem Wert. Wie schreibt sich Euer Name doch gleich? Ich werde dafr sorgen, dass Ihr in meinem Bericht als Mitautor auftaucht.$B$BEiner der Burschen auf dem Schiff gab mir diese 'Schatzkarte', um seine Spielschulden bei mir zu begleichen, allerdings ist Mythologie keines meiner Fachgebiete. Aberglubiges Pack, diese Seefahrer.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9550, "deDE", "Vielleicht bezieht sich die Karte ja auf dieses alte Tagebuch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9550, "deDE", "Nachdem Ihr Euch das Buch sorgfltig angesehen habt, kommt Ihr zu dem Schluss, dass es sich um ein Tagebuch handelt. Die Eintragungen auf den Seiten weisen eine elegante Handschrift auf, die sich von der auf der Karte unterscheidet. Zahlreiche Texte sind durch hinzugefgte Beschriftungen und Zeichnungen im Stil der Karte unleserlich geworden.$B$BDurch das Geschreibsel des Kartenverfassers, in dem er von Trinkgelagen whrend des Landgangs faselt, sind die eigentlichen Eintragungen fast nicht mehr auszumachen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9557, "deDE", "Mge das Licht bei all Euren Bemhungen mit Euch sein, $C. Was habt Ihr da?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9557, "deDE", "Hmm... das ist uerst faszinierend. Ja, ich sollte in der Lage sein, die eigentlichen Eintrge des Schreibers in diesem Buch entziffern zu knnen.$B$BIch verfge ber die bentigten Mittel hier. Es sollte nur einen Augenblick dauern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9561, "deDE", "Gras und Gestrpp bedecken eine unnatrliche Wlbung am Boden.$B$BNach nur kurzem Graben entdeckt Ihr einen alten Kasten. Es muss sich dabei um den, der in Nolkais Tagebuch beschriebenen ist, handeln.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9567, "deDE", "Habt Ihr schon etwas Wissenswertes von den Satyn der Nazzivus erlangt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9567, "deDE", "Gut gemacht, $N. Mal sehen, was ich von dieser Glyphe in Erfahrung bringen kann. Dann knnen wir unseren nchsten Schritt planen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9569, "deDE", "<Verteidigerin Aalesia scheint eher durch Euch hindurch, als Euch anzusehen, sie ist voll und ganz mit den Satyrn beschftigt.>$B$BSeid Ihr schon gegen den Anfhrer der Satyrn vorgegangen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9569, "deDE", "Ohne ihren Anfhrer und die entwendeten Kristalle knnen wir die Satyrn bedenkenlos in Schach halten. Nur das Licht wei, wie viele dieser erbrmlichen Kreaturen da drauen noch lauern.$B$BHerold Mikolaas und ich sind Euch fr Eure Hilfe sehr dankbar. Ihr habt uns geholfen, eine noch grere Bedrohung durch die Satyrn abzuwenden. Euer Einsatz wird alle daran erinnern, dass Wachsamkeit der Schlssel zum Erfolg ist, wenn es darum geht, gegen die Legion und ihre Diener vorzugehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9574, "deDE", "Konntet Ihr die Proben bekommen, die ich bentige?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9574, "deDE", "Ich habe jetzt mehr als genug fr meine Untersuchungen. Ich danke Euch, $N. Solltet Ihr noch etwas anderes auftreiben knnen, das beweist, dass die Kristalle die einheimischen Kreaturen der Blutmythosinsel beeintrchtigen, so lasst es mich sofort wissen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9578, "deDE", "Ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9578, "deDE", "Der Leichnam trgt die fr einen Techniker der Exodar bliche Kleidung, aber der von Morae beschriebene Anhnger fehlt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9579, "deDE", "<Morae sieht Euch voller Hoffnung an.>$B$BSeid Ihr bei Eurer Suche auf Spuren von Galaen gestoen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9579, "deDE", "<Ihr berichtet Morae von Eurem Fund in den Trmmern des Kryokerns.>$B$BAlso... er ist...$B$BIch denke, dass ich es bereits wusste, wollte es aber einfach nicht glauben. Es sind zwar nicht die Neuigkeiten, die ich mir erhofft hatte, aber ich bin Euch fr das Auffinden von Galaen zutiefst dankbar.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9580, "deDE", "Wie verluft Eure Jagd, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9580, "deDE", "Mit diesen Vorrten werden wir frs Erste versorgt sein und die Zubereitung fr den spteren Verzehr wird mich sicherlich ein Weilchen beschftigen. Ich danke Euch fr Eure Hilfe, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9581, "deDE", "Konntet Ihr die erste Kristallprobe bekommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9581, "deDE", "Dieser Kristallsplitter wird unseren Forschern als Vergleichsgrundlage fr Proben von anderen Teilen der Insel dienen, da er aus einem weniger verseuchten Gebiet der Blutmythosinsel stammt.$B$BIch werde die Probe erst losschicken knnen, wenn ich ein vollstndiges Probenset habe, aber der Anfang ist getan. Wenn Ihr mir helfen mchtet, die anderen Proben einzusammeln, werde ich Euch fr Eure Mhe natrlich belohnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9584, "deDE", "Habt Ihr die zweite Kristallprobe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9584, "deDE", "<Herold Mikolaas untersucht den Kristallsplitter.>$B$BDieser Splitter unterscheidet sich stark von der ersten Probe.$B$B<Herold Mikolaas hlt den Kristall ins Licht.>$B$BSeht Ihr, wie das Licht darin pulsiert? Das ist bedenklich. Ich werde in meinem Bericht vermerken, dass die Blutelfen diesen Kristall verndert zu haben scheinen. Ich danke Euch fr Eure erneute Hilfe, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9585, "deDE", "Habt Ihr die letzte Probe fr mein Set?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9585, "deDE", "<Herold Mikolaas nimmt den letzten Kristall von Euch entgegen.>$B$BDamit ist das Set vollstndig. Ich muss nur noch alles verpacken und zur Exodar senden.$B$B<Der Herold scheint erleichtert, dass die Sache erledigt ist.>$B$BVersteht mich nicht falsch, $N, ich bin mir sicher, dass die Forschungsarbeit wertvoll ist, aber wir haben es hier drauen noch mit zahlreichen anderen Problemen zu tun, die dringend sind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9620, "deDE", "Auf dem Boden liegen berall die Krper der $R, die dem Vermessungsteam angehrten - von Waffen der Naga durchbohrt. Ihr seht die verstreuten berreste ihrer Vorrte und Vermessungsausrstung, aber ihre Datenkristalle befinden sich nicht darunter.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9624, "deDE", "Man findet die Sandbirnen immer seltener, was natrlich weniger Kuchen und somit weniger Leckerli fr die Elekk hier bedeutet. Konntet Ihr die bentigten Frchte auftreiben?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9624, "deDE", "War das Auffinden der Frchte schwierig? Ich brauche mit jedem Mal lnger.$B$BIch werde allmhlich ganz damit aufhren mssen, da ich keine Zeit mehr dafr habe. Danke fr das Sammeln der Birnen, $N. Jetzt kann ich zumindest noch ein oder zwei Kuchen backen, bevor die Saison vorber ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9628, "deDE", "Konntet Ihr herausfinden, was das Vermessungsteam von seiner Rckkehr abgehalten hat?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9628, "deDE", "<Ihr erzhlt Herold Mikolaas vom Schicksal des Vermessungsteams. Er nimmt den Kristall von Euch entgegen, aber schweigt fr einen langen Augenblick.>$B$BEs waren gute Mnner, Saphirus, sie wussten, dass ihre Mission gefhrlich sein wrde. Ich bin Euch dankbar fr das Auffinden des Teams und die Wiederbeschaffung des Kristalls.$B$B<Der Herold ballt seine Faust.>$B$BWie haben bereits zu viele unserer Mnner verloren, dabei hat unsere Mission gerade erst begonnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9629, "deDE", "Hat der Apparat funktioniert? Habt Ihr die Murlocs fr meine Untersuchungen makiert?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9629, "deDE", "Gut gemacht, $N. Ich hatte befrchtet, dass der Sender beim Markieren beschdigt werden knnte, aber scheinbar ist ja alles wie geplant verlaufen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9632, "deDE", "Seid gegrt, $C. Was habt Ihr da?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9632, "deDE", "Ich kann Euch helfen, nach Auberdine zu kommen. Glcklicherweise ist es nur eine kurze Fahrt mit dem Schiff von hier!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9633, "deDE", "Willkommen in Auberdine. Was bringt Euch zur Dunkelkste?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9633, "deDE", "<Thundris nimmt das Schreiben von Euch entgegen, liest es und nickt.>$B$BDie Inseln der $R, die Azurmythosinsel und die Blutmythosinsel, wurden vor langer Zeit von meinen Leuten bewohnt. Einige der Bedrohungen, denen sich Anachoret Paetheus und seine Brder und Schwestern jetzt stellen mssen, sind alte Feinde der Nachtelfen.$B$BIch bin mir sicher, dass man unseren neuen Verbndeten hier in Auberdine alles zur Verfgung stellen mchte, das bentigt wird. Sobald ich mit den Bewohnern der Stadt und den Schildwachen gesprochen habe, werde ich Paetheus benachrichtigen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9634, "deDE", "Wir mssen handeln, bevor die Felshetzer die einheimischen Raubtiere der Blutmythosinsel verdrngen. Konntet Ihr den Felshetzerbestand bereits etwas dezimieren?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9634, "deDE", "Gut gemacht, $N. Der Felshetzerbestand wird zwar nur fr einige Zeit begrenzt bleiben, aber es ist ein guter Anfang.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9641, "deDE", "Konntet Ihr Kristallsplitter von den Kreaturen der Blutmythosinsel einsammeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9641, "deDE", "Gut gemacht. Diese Splitter werden als Rohmaterial fr weitere Strkungskristalle dienen.$B$BIhr knnt Euch einen der drei Typen aussuchen, die ich herstellen kann. Ich finde sie alle recht ntzlich, je nachdem auf welche Beute man es abgesehen hat.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9642, "deDE", "Solltet Ihr noch ber mehr bestrahlte Kristallsplitter verfgen, tausche ich sie gerne gegen einen weiteren meiner Kristalle ein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9642, "deDE", "Sehr schn, hier sind meine Kristalle, $N. Verwendet sie weise.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9643, "deDE", "Habt Ihr die Wrgeranken bekommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9643, "deDE", "Das scheint mir stabil genug fr meine Konstruktion. Danke fr die Hilfe, $N. Euer Untersttzung bei der Verteidigung der Blutwacht wird nicht vergessen werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9646, "deDE", "Wie kann ich Euch helfen, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9646, "deDE", "Gut gemacht. Von dem, was ich gehrt habe, war es wohl kein leichter Kampf. Ihr habt Euch Eure Belohnung mehr als verdient, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9647, "deDE", "Es gilt jetzt, eine lngerfristige Lsung zu finden, um die Arten wieder in ihren Urzustand zurckzufhren. Momentan ist das zeitweilige Ausmerzen ihres Bestandes jedoch die beste Methode. Danke fr Eure Hilfe, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9648, "deDE", "Vergesst nicht, die von mir bentigten Pilze sind einzigartig in unterschiedlichen Gebieten der Blutmythosinsel.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9648, "deDE", "Nun, ich hatte Euch gewarnt - oder etwa nicht? Ich hoffe diese kleine Bezahlung wird Euch fr Eure Unannehmlichkeiten etwas entschdigen knnen. Kehrt zu mir zurck, sobald Ihr Euch ein wenig erholt habt, dann habe ich vielleicht noch eine Aufgabe fr Euch - mit Pilzen natrlich!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9649, "deDE", "Schon wieder zurck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9649, "deDE", "Ich hoffe diese Aufgabe hat nicht zu viel von Euch abverlangt, $N. Ihr habt erneut bewiesen, dass Ihr $Gein fhiger und aufmerksamer:eine fhige und aufmerksame; $R seid. Habt meinen Dank und natrlich eine kleine Abfindung.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9663, "deDE", "Ihr habt jetzt keine Zeit zum Ausruhen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9663, "deDE", "Gut gemacht, $N. Dies war ein Test Eurer Fhigkeiten, aber Ihr habt mit wehenden Fahnen bestanden. Um ein Soldat der Hand von Argus zu werden, gilt es viele dieser Tests zu bestehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9666, "deDE", "Es tut mir leid, dass Ihr das tun musstet, $N. Rohe Gewalt entspricht nicht der Natur der $R, aber manchmal ist sie der einzige Weg.$B$BUnglcklicherweise gehrt dies zum Leben und man versteht es oft erst dann, wenn es zu spt ist. Genau wie mit den Orcs, die unsere Brder und Schwestern abschlachteten.$B$BEin Mitglied der Hand von Argus zu werden, ist ein Prozess der Wiedergeburt. Mit der Geburt kommt bekanntlich auch der Schmerz...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9667, "deDE", "Ihr habt erneut bewiesen, dass Ihr all dem entsprecht, was in der Prophezeiung vorhergesagt wurde. Ihr habt den ewigen Dank des Oberhuptlings, $N - und Ihr habt die Wahl zwischen diesen Relikten der Tannenruhfeste...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9668, "deDE", "Ich hatte es bereits im Gefhl, dass Ihr es bis hierher schaffen wrdet, $C. Wenn Ihr noch weiter kommen mchtet, bin ich gerne bereit Euch zu unterrichten...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9669, "deDE", "Es war einfach grauenhaft...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9669, "deDE", "Unglaublich! Konntet Ihr andere berlebende finden?$B$B<Achelus senkt seine Stimme zu einem Flstern.>$B$BIch mchte ehrlich mit Euch sein, $N, niemand sonst wollte diesen Auftrag annehmen. Ihr seid sehr mutig, $C...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9670, "deDE", "Und, hattet Ihr Glck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9670, "deDE", "Ihr habt mehr als die Hlfte des Teams befreit! Der Exarch und die anderen bei der Blutwacht werden mit Sicherheit von Eurer heldenhaften Tat erfahren. Ich danke Euch, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9671, "deDE", "Seid Ihr $N? Es wurde auch Zeit! Ich war gerade dabei, die Suche nach Euch aufzugeben!$B$BBeim Briefkasten wartet eine dringende Nachricht auf Euch. Die Person, die sie mir bergeben hatte, war ein Admiral der Menschen von der Azurmythosinsel. Admiral Odesy-irgendwas... der Name ist mir entfallen. Er sagte, Ihr wrdet wissen, wer er sei.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9672, "deDE", "Habt Ihr von dem Blutfluch gehrt, Landratte? Nein, natrlich nicht. Der Blutfluch ist das, was all diese Schiffe hat sinken lassen. Der verderbte, entweihte Teil des Wassers hier wird Riff des Blutfluchs genannt - ein Teil der Welt, den Ihr niemals sehen mchtet. Ich bin hier selbst seit 20 Jahren.$B$BSkorbut, so ein Quatsch! Ich starb an diesen Wilden Ufern als Drachenfutter... Ah, das ist wieder eine andere Geschichte, fr ein anderes Mal. Jetzt mssen wir den armen Seelen helfen, die hier im Riff gefangen sind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9674, "deDE", "Ein guter Anfang, aber es gibt noch viel mehr zu tun!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9675, "deDE", "Oh, da wre noch etwas! Ihr solltet mit Ganaar bei der Exodar sprechen. Er wird Euch das letzte bisschen Wissen bezglich der Pflege und Ausbildung Eures Begleiters lehren.$B$BIhr findet ihn in der sdwestlichen Ecke der Hndlertreppe.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9675, "deDE", "Zu wissen, wie man seinen Begleiter fttert und wiederbelebt, ist nicht nur ein zustzlicher Leckerbissen, wie Euer letzter Lehrer so gelinde sagte - es ist viel mehr. Mit der richtigen Ausbildung kann sich Euer Begleiter auf eine Weise verbessern, wie er es nie von alleine getan htte.$B$BIch habe bereits viel von den Furbolgs gelernt, also gebt Acht und ich werde dieses Wissen an Euch weitergeben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9682, "deDE", "Trauert ihrem Niedergang nicht nach, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9682, "deDE", "Ihr habt den Hoffnungslosen einen groen Dienst erwiesen, $N. Es gilt jedoch noch eine letzte Aufgabe zu erledigen: Ttet den Anfhrer des Blutfluchs.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9683, "deDE", "Ihr habt es geschafft! Hunderte von Seelen sind Euch fr alles, was Ihr getan habt, dankbar.$B$BGestattet mir, Euch etwas zu berreichen...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9687, "deDE", "Die Kriege der Drachen sind seit langem vorber. Was noch bleibt, sind die Knochen und die aufgewhlten berbleibsel von Yseras Brut.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9687, "deDE", "Ein Ritual? Die Eulkin haben seit tausenden von Jahren mit dem Land in Einklang gelebt. Derartiges ist nicht normal!$B$BWie bedauerlich...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9688, "deDE", "Das Zusehen allein schmerzt mich. Bitte, lasst uns nicht noch weiter darber sprechen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9688, "deDE", "Wie verwerflich Eure Aufgabe auch war, es musste einfach getan werden. Jetzt msst Ihr in das Angesicht des Bsen sehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9689, "deDE", "Ich rate Euch, Verbndete mit Euch zu bringen, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9689, "deDE", "Wie lange war es noch? Tausend Jahre? Zehntausend Jahre vielleicht? Ihr habt mir und der Erinnerung meiner Leute einen groen Dienst erwiesen, $N. Bitte, nehmt diesen mchtigen Gegenstand als Zeichen der Anerkennung von Loreth'Aran an - fr Eure ehrenvollen Taten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9693, "deDE", "Ah, $N... ich habe bereits von Euch gehrt. Ihr seid also derjenige, der den Korsallauf in unter 15 Minuten gemacht hat. Ts, wahrscheinlich nicht.$B$BSeid Ihr bereit, etwas Blut im Namen des Lichts zu vergieen? Fr Velen? Fr Argus?$B$B<Boros lacht.>$B$BGut, gut, ich halte mich jetzt besser zurck.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9694, "deDE", "Es gibt so viele von ihnen... ich komme einfach nicht von dem Gefhl los, dass sie sich irgendwie vermehren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9694, "deDE", "Ihr seid zurck - in einem Stck!$B$BIch habe gute Neuigkeiten, $N. Wir haben neue Informationen zum wahrscheinlichen Aufenthaltsort weiterer berlebender erhalten. Eine neue Absturzstelle wurde entdeckt! Sobald ich mich eingehender damit befasst habe, werde ich mich an Euch wenden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9696, "deDE", "Ich habe hier etwas viel zu tun, $GAgent:Agentin; $N. Ich hoffe Euer Anliegen ist wichtig.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9696, "deDE", "<Elysia liest das Dokument.>$B$BWenn wirklich stimmt, was hier steht, dann haben die Blutelfen ein Portal zur Scherbenwelt geffnet! Das wrde auch ihre schier endlose Zahl erklren...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9698, "deDE", "<Velen lchelt.>$B$BSeid gegrt, $N. Ich habe Euch erwartet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9698, "deDE", "<Velen beginnt das Dokument zu lesen.>$B$BInteressant... dies erklrt in der Tat einiges.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9699, "deDE", "Ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9699, "deDE", "Es ist ein guter Plan, aber auch ein gefhrlicher. Ich bin mir jedoch sicher, dass alles gut verlaufen wird.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9700, "deDE", "Wie viele sagtet Ihr doch gleich, habt Ihr bei der Warpgondel gesichtet? Wie oft haben sie nur dieses Sonnenportal geffnet? Das sind sehr schlechte Neuigkeiten...$B$BTja, Ihr habt Euch nun alles von mir angeeignet, was ich Euch lehren kann, $N. Kuros wird nach Euch rufen lassen, wenn es an der Zeit ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9703, "deDE", "Waren die Informationen korrekt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9703, "deDE", "Nur Blutelfen? Keine berlebenden? Das ist beunruhigend. Ich brauche etwas Zeit zum Nachdenken, $N. Vielleicht haben Aesom oder Boros einen Auftrag fr Euch.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9706, "deDE", "Was habt Ihr da, $N? Ist das ein Tagebuch?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9706, "deDE", "<Kuros erbleicht beim Lesen des Tagebuchs und Trnen strmen seine Wangen herab.>$B$BSaruan war mein Meister... mein Lehrer... mein Mentor... Ich nahm seinen Platz im Dreigestirn erst vor Kurzem ein - nachdem wir die Suche nach ihm aufgegeben hatten.$B$B<Kuros' Gesichtsausdruck schlgt in Zorn ber.>$B$BMatis...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9711, "deDE", "Ein Soldat von Matis' Stand wird nicht alleine unterwegs sein. Gebt da drauen auf Euch Acht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9711, "deDE", "Das Dreigestirn hat beschlossen, Matis sofort zu verurteilen. Ihr habt heute einen Erzverbrecher der Gerechtigkeit berfhrt, junger $R.$B$BIhr habt fr zwei von drei auerordentliche Arbeit geleistet, lediglich Aesom fehlt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9740, "deDE", "Fabelhaft! Wir haben ihre Versorgungszufuhr durchtrennt. Jetzt mssen wir sie nur noch kaltmachen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9741, "deDE", "Dort herrscht ein ganz schnes Durcheinander, was? Gute Arbeit, $N. Ich werde ein paar Anachoreten entsenden, die Loryi und Jorli helfen werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9746, "deDE", "Dies wird ihre Verteidigung ganz sicher geschwcht haben. Ab sofort werden sie es sich zweimal berlegen, bevor sie uns kopflos berfallen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9748, "deDE", "Trinkt nicht das Wasser, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9748, "deDE", "Wie ich vermutet habe, ist das Wasser von der Verseuchung durch die austretende Kernflssigkeit rot geworden.$B$BIch hoffe, dass Ihr nicht in dem verseuchten Wasser geschwommen seid.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9751, "deDE", "Habt Ihr von dem Blutfluch gehrt, Landratte? Nein, natrlich nicht. Der Blutfluch ist das, was all diese Schiffe hat sinken lassen. Der verderbte, entweihte Teil des Wassers hier wird Riff des Blutfluchs genannt - ein Teil der Welt, den Ihr niemals sehen mchtet. Ich bin hier selbst seit 20 Jahren.$B$BSkorbut, so ein Quatsch! Ich starb an diesen Wilden Ufern als Drachenfutter... Ah, das ist wieder eine andere Geschichte, fr ein anderes Mal. Jetzt mssen wir den armen Seelen helfen, die hier im Riff gefangen sind.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9753, "deDE", "Ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9753, "deDE", "Ich brauche Eure Hilfe, $N. Es ist mein Plan, die gesamte Armee der Sonnenfalken zu strzen. Zuvor gilt es jedoch ein paar grundlegende Informationen zu sammeln.$B$BWir mssen die Schwche ihrer Verteidigung ausmachen. Unglcklicherweise ist es uns bis jetzt noch nicht gelungen, nahe genug an die Vektorspule heranzukommen und ihre Verteidigung auszukundschaften. Wenn wir den Gefangenen zum Sprechen bringen knnten, wrden wir womglich die Informationen bekommen, die wir brauchen, um durch ihre Verteidigung zu brechen und die Vektorspule zu zerstren.$B$BIhr habt richtig gehrt, ich will das verdammte Ding in die Luft jagen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9756, "deDE", "Kehrt erst wieder zurck, wenn Ihr erfolgreich gewesen seid!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9756, "deDE", "A-ha! Gut gemacht, $N. Wirklich sehr gut gemacht. Seid Ihr bereit der Blutelfenbedrohung unserer Insel nun endlich ein Ende zu bereiten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9757, "deDE", "Lasst uns sofort mit der Ausbildung beginnen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9579, "deDE", "Ihr habt die ganze Blutmythosinsel und Azurmythosinsel gerettet, das Leben der $R in Azeroth gesichert, $N! Dafr wird man Euch niemals genug danken knnen, keine Belohnung, nichts ist gro genug, um Euch unsere Anerkennung zu zeigen.$B$BLeider blieben noch so viele auf Draenor zurck, die dieses historische Ereignis nicht miterleben knnen. Ihr msst Euren Weg zurckfinden. Findet Euren Weg zur Scherbenwelt, Held von Argus. Bringt Eure Leute nach Hause. Nach Hause in das Land, das Ihr gerettet habt. Nach Azeroth...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9760, "deDE", "Ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9760, "deDE", "Vielleicht verhelft Ihr unserem Volk ja zu einem neuen Zeitalter des Wohlstands und des Friedens.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9761, "deDE", "Seht Ihr hier irgendwo einen ungewhnlich mutigen und starken Draenei?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9761, "deDE", "Hoffentlich meldet sich bald ein Held, bevor sie Verstrkung anfordern knnen, um die gerade von Euch abgesicherte Gegend zu bewachen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9779, "deDE", "Schon zurck? Konntet Ihr das Schreiben finden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9779, "deDE", "<Verteidiger Boros betrachtet das Dokument.>$B$BDas ist Thalassisch. Glcklicherweise kenne ich eine Person, die es bersetzen kann...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10063, "deDE", "Dachte ich's mir doch! Dieser mrrische alte Zwerg sollte mir eigentlich beim Transport meiner Ausrstung und dem Sammeln von Daten helfen, aber stattdessen sitzt er nur in dem komischen Draeneidorf herum und betrinkt sich sinnlos.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10064, "deDE", "Ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10064, "deDE", "Ich habe gehofft, dass die Blutwacht uns Verstrkung schicken wrde, sobald man dort wei, mit wem wir es hier zu tun haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10065, "deDE", "Es sieht da drauen schon etwas besser aus, ich bin mir jedoch noch immer nicht sicher, ob es besser wre, hier auf medizinische Versorgung zu warten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10066, "deDE", "Konntet Ihr ein paar mutierte Greifer ausmerzen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10066, "deDE", "Gut gemacht, $N. Ohne die Tcken der Greifer, wird es jetzt wesentlich einfacher sein, Verstrkung von der Blutwacht zu erhalten oder Angriffe gegen die Sonnenfalken zu starten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10067, "deDE", "Gut gemacht, $N. Es ist besonders wichtig, dass wir keine Mglichkeit auer Acht lassen, die den Sonnenfalken weiterhin ihre Existenz sichern knnte.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10350, "deDE", "Ah, Ihr seid also endlich gekommen. Ich bin Behomat und werde Euch in den Knsten eines Kriegers unterweisen. Ruada hat mir von Eurer Prfung gegen den Felshetzer erzhlt und ich bin sehr beeindruckt. Ich habe ein Geschenk fr Euch.$B$BWir konnten nur wenig aus den Trmmern der Exodar retten, und das verteilen wir nur an die Wrdigsten. Bitte nehmt Euch eine Waffe, die Eurem Kampfstil entspricht, und fhrt sie mit dem Wissen, dass Ihr sie verdient habt.$B$BWo wir gerade von Kampfstilen sprechen - es ist Zeit, dass Ihr etwas Neues lernt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10366, "deDE", "$N, ich habe Euch erwartet.$B$BTullas hat Lobeshymnen auf Euch gesungen, und ich bin sehr begierig darauf, Euch im Weg des Lichts zu unterweisen. Sucht mich auf, wenn Ihr bereit seid, Eure Fhigkeiten und Eure Macht verstehen zu lernen, und ich werde Euch geleiten.", 26972);
