INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9340, "deDE", "Gute Arbeit, $N. Das sollte die Felsklauen lehren, sich von uns fernzuhalten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9345, "deDE", "Habt Ihr die Pflanzen gefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9345, "deDE", "Die sollten ihren Zweck erfllen. Ich werde die Salbe fertigstellen und mein Bein schienen, bevor ich nach Thrallmar aufbreche. Wieso glaube ich genau zu wissen, was mir Scharfseherin Regulkut erzhlen wird, wenn sie mich so sieht?$B$B<Grelag seufzt halbherzig.>$B$BVielen Dank fr Eure Hilfe, $N. Ich habe noch einige von den Hllenwirbelkrutern brig. Nehmt sie mit, wenn Ihr sie braucht. Wer wei, wofr sie noch gut sind?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9349, "deDE", "Habt Ihr die Eier schon?$B$B<Legassis Magen knurrt vor Vorfreude.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9349, "deDE", "Fr ausgezeichnete Kche braucht man ausgezeichnete Zutaten. Und diese Eier sehen groartig aus!$B$BIch habe gerade meine Pfanne geputzt, Ihr seid also herzlich eingeladen, mir bei einem Mahl Gesellschaft zu leisten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9351, "deDE", "Konntet Ihr die Essenzen besorgen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9351, "deDE", "Perfekt! Ich glaube sogar, dass das so gut funktionieren wird, dass ich den Motor umbauen muss, damit ich alle Vorteile dieses Kraftstoffs nutzen kann! Falls - ich meine natrlich - wenn wir hier wegkommen, muss ich allen in Area 52 meine Entwrfe zeigen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9355, "deDE", "Gut gemacht, $N. Wir sollten uns aber nicht zu frh freuen. Ich bin mir sicher, dass wir diese Wrmer nicht zum letzten Mal gesehen haben. Wenn sie ihre hsslichen Kpfe wieder in diese Gegend strecken sollten, werden die Shne sie schon erwarten!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9356, "deDE", "Ich kann es kaum erwarten, seinen Gesichtsausdruck zu sehen, wenn er reinhaut! Seit dem Absturz beschwert er sich, dass ich nur ans Essen denke. Nun, jemand muss uns schlielich ernhren, whrend er am Zeppelin herumschraubt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9356, "deDE", "Felshetzereier... Bmm!$B$BHlleneberspeck... Bmm!$B$BBussardflgel... Bmm! Bmm!$B$BWas fr ein groartiges Essen. Ihr solltet wirklich mal probieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9361, "deDE", "Habt Ihr das Hlleneberfleisch erfolgreich gelutert?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9361, "deDE", "Hmmm...$B$BEs sieht essbar aus, wenn man es richtig wrzt. Erzhlt Quack aber nicht, woraus sein Frhstck besteht, ja?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9366, "deDE", "Das Teufelsblut... habt Ihr es bekommen, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9366, "deDE", "Die in den Proben gespeicherten Energien sind unfassbar stark, $N. Nun mssen wir nur noch sicherstellen, dass wir auch in Zukunft freien Zugriff auf das Teufelsblut haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9370, "deDE", "Ihr seid zurckgekehrt, $N. Habt Ihr dem Aberwitz der Draenei ein Ende gesetzt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9370, "deDE", "Das wird diese heiligen Fanatiker lehren, sich um ihren eigenen Kram zu kmmern.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9372, "deDE", "Habt Ihr die Blutproben bekommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9372, "deDE", "Gute Arbeit, $N. Diese Blutproben werden mir bei meinen Forschungen, und der Expedition bei ihren Studien der Scherbenwelt und ihrer Lebewesen, sehr dienlich sein.$B$BWenn ich den Grund fr die fortschreitende Vernderung der Tiere herausfinden knnte, knnte ich vielleicht ein Gegengift herstellen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9373, "deDE", "Guten Tag, $C. Was bringt Euch in mein Lager?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9373, "deDE", "Danke, dass Ihr mir dies hier gebracht habt, $C. Ich habe mich schon gewundert, warum ich so lange nichts mehr von der Expedition gehrt habe. Ich frage mich, wie lange diese Nachricht schon unterwegs ist und was wohl aus ihrem berbringer geworden ist.$B$BHier habt Ihr etwas Geld fr Eure Mhen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9374, "deDE", "Hattet Ihr Glck, $N? Die Teiche von Aggonar liegen nrdlich der Hllenfeuerzitadelle.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9374, "deDE", "Das Tagebuch meines Ehemanns... hat nicht das Geringste mit seinen Forschungen zu tun!$B$BWie konnte ich nur so blind sein? `Die magischen Eigenschaften dmonisch beeinflusster Schleimformen`, beinahe htte ich es geglaubt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9375, "deDE", "<Taleris ruft einige der Blutelfen aus dem Auenposten herbei.>$B$BSchafft sie herein und versorgt ihre Wunden!$B$B<Taleris wendet sich wieder Euch zu.>$B$BSie kann sich glcklich schtzen, dass Ihr sie noch rechtzeitig gefunden habt, $C. Viel lnger htte sie dort drauen alleine nicht berlebt.$B$BKonntet Ihr verstehen, was sie gesagt hat?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9376, "deDE", "Die Sache sieht nicht gut aus. So schafft sie es nie wieder auf die Strae. Habt Ihr den Beutel gefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9376, "deDE", "Vielen Dank, dass Ihr dies zurckgebracht habt, $N. Ich wei nicht, was der verletzten Pilgerin an diesem Beutel so wichtig ist, aber sie lsst keinen an sich heran, bis sie ihn wieder hat.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9381, "deDE", "Habt Ihr alle Federn beisammen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9381, "deDE", "Die sollten ihren Zweck erfllen. Ich hatte schon befrchtet, dass den Waldlufern bald die Pfeile ausgehen, doch nun muss ich mir darber erstmal keine Sorgen mehr machen. Vielen Dank fr Eure Hilfe, $C.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9383, "deDE", "Habt Ihr die Magie des Kristalls gegen einen unkontrollierten Leerwandler eingesetzt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9383, "deDE", "Ihr habt Euch bewhrt, $N. Es scheint, als wrde der Verwahrungskristall frs Erste standhalten, allerdings wei niemand wie lange. Wir werden ihn im Auge behalten und in der Zwischenzeit noch mehr Kristalle vorbereiten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9385, "deDE", "Gut gemacht, $N. Das wird den Biestern zeigen, wer hier der Boss ist!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9387, "deDE", "Habt Ihr die Proben bekommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9387, "deDE", "Ihr wart mir eine groe Hilfe, $N. Nun wollen wir sehen, was wir von unserem blauen Freund lernen knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9390, "deDE", "Ihr habt den Leichnam eines jungen Paladins der Draenei gefunden. Es scheint, als htte ihn jemand hinterrcks erschlagen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9391, "deDE", "Habt ihr getan, worum ich Euch gebeten habe, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9391, "deDE", "Obwohl die Groe Kluft noch immer ein gefhrlicher Ort ist, gibt sie einen brauchbaren Pfad fr unsere Pilger ab.$B$BEin derartiger Akt der Verzweiflung ist unvermeidlich, solange die Allianz weiterhin smtliche ein- und ausgehenden Straen zur Falkenwacht beobachtet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9396, "deDE", "Habt Ihr die Schriftrollen, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9396, "deDE", "Gut gemacht, $N. Die Geheimnisse der Scherbenwelt werden sich nicht lange vor uns verstecken knnen.$B$BEs ist unser Schicksal, die Meister dieser Welt zu werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9397, "deDE", "Ihr seid zurck. Habt Ihr mir ein Kaliriweibchen mitgebracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9397, "deDE", "Ihr habt es geschafft! Ihr habt ein junges Weibchen gefangen!$B$BSie ist wundervoll. Vielen, vielen Dank, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9398, "deDE", "Ihr habt gute Arbeit geleistet, $N. Es ist immer schlimm, wenn Tten die einzige Mglichkeit ist, die bleibt, sogar wenn es sich um so gefhrliche Biester wie die Steinsichelklauen handelt.$B$BIhr habt meinen Dank, dass Ihr diese grausame Aufgabe erledigt habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9399, "deDE", "Ihr habt es geschafft! Wir stehen tief in Eurer Schuld, $N. Seht Euch nur all die Ankmmlinge des Lumpenpacks an.$B$BUnter meiner geistigen Fhrung werden sie alles ber die alten Traditionen lernen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9400, "deDE", "Der Leichnam stimmt mit der Beschreibung von Krun Rckenbrecher, dem Assassinen der Hllenfeuerzitadelle, berein. Als Ihr den noch warmen Krper umdreht, erblickt Ihr eine Steinaxt, die in seinem Rcken steckt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9401, "deDE", "Sagt mir, ist der Assassine tot?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9401, "deDE", "Er wurde mit dieser Waffe gettet? Das ist unmglich!$B$BHaben wir... haben wir sie tatschlich gefunden?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9405, "deDE", "Sagt mir, wonach Ihr sucht, damit ich Euch bei der Suche untersttzen kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9405, "deDE", "Ich verstehe Nazgrels Anliegen, $C. Sagt nichts weiter.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9406, "deDE", "Was habt Ihr fr uns, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9406, "deDE", "Seid Ihr sicher? Das sind groe Neuigkeiten, $N. Diese Orcs knnen sicherlich unserer Sache hier und auch zuhause dienlich sein!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9407, "deDE", "Unser Expeditionskorps kam ohne Probleme durch das Portal und hat ein neues Lager namens Thrallmar errichtet. Aber wie Ihr sehen knnt ist die Brennende Legion eingefallen und hat uns von unseren Brdern abgeschnitten. Die Dmonen sind eindeutig darauf aus, das Dunkle Portal einzunehmen und uns daran zu hindern, Verstrkung aus Azeroth zu holen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9410, "deDE", "Ihr knnt froh sein, dass Ihr noch lebt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9410, "deDE", "Wer seid Ihr, dass Ihr hier so einfach unangekndigt umherwandert? Ich habe meine Mnner nur zurckgehalten, weil Ryga die Anwesenheit der Geister in Eurer Nhe versprte.$B$BIhr knnt frei sprechen, doch whlt Eure Worte mit Bedacht. Wir vertrauen normalerweise keinen Fremden, die hier so nah bei der Zitadelle umherstreifen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9417, "deDE", "Fantastische Arbeit, $N! Ich ernenne Euch hiermit zum Langbart ehrenhalber.$B$BBleibt noch ein Weilchen und trinkt etwas mit uns!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9418, "deDE", "Endlich habe ich meine Freiheit wieder!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9418, "deDE", "Mit dieser Kugel erlange ich meine Freiheit wieder. Nehmt Eure Belohnung, Sterblicher, auf dass meine Schuld bezahlt sei.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9420, "deDE", "Habt Ihr die Kalirifedern, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9420, "deDE", "Ihr habt Euren Teil der Abmachung erfllt. Ich schtze, Geschft ist Geschft!$B$BHier, mein Freund. Passt gut auf ihn auf.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9423, "deDE", "Sedai, mein geliebter Bruder... $B$BDiese feigen Orcs! Er war unbewaffnet, wie konnten sie nur?$B$BLasst mich alleine, $N. Ich danke Euch fr Eure Hilfe, doch ich brauche jetzt etwas Zeit fr mich selbst.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9424, "deDE", "Habt Ihr Sedai in Makurus Namen gercht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9424, "deDE", "Ja... Rache! Ihr wart gut.$B$BNehmt dies, es war ein Geschenk von Sedai. Wenn Makuru es behlt, stimmt es ihn nur traurig.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9426, "deDE", "Ja, ich fhle, wie die teuflische Prsenz in den Teichen abnimmt.$B$BIhr habt gute Arbeit geleistet, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9427, "deDE", "Ihr seid zurck, $N. Ist das Wasser gereinigt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9427, "deDE", "Die Essenz von Aggonar lebte in seinem verderbten Nachkommen Aggonis weiter. Dank Eures Erfolgs gegen den Dmon knnen wir nun beginnen, die Teiche zu ihrer ursprnglichen Reinheit zurckzufhren.$B$BIhr habt unseren Dank, $N. Eure Arbeit hat einen tiefen Eindruck in dieser Welt hinterlassen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9430, "deDE", "Habt Ihr die Relikte aus Sha'naar bei Euch, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9430, "deDE", "Das Glck hat Euch gesegnet. Genau wie ich es erhofft hatte, habt Ihr ein ganz besonderes Relikt zwischen all den anderen gefunden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9438, "deDE", "Ihr bringt Kunde aus der Scherbenwelt, $C? Sodann, sprecht!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9438, "deDE", "<Thralls Augen werden immer grer, als er Nazgrels Brief liest.>$B$BMein Volk... wir mssen sie erreichen. Wir mssen Nagrand so schnell wie mglich erreichen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9441, "deDE", "Es bedeutet uns sehr viel, dass Ihr uns in diesen schwierigen Zeiten Eure helfende Hand reicht. Niemals werden wir Euch, noch denjenigen vergessen, der Euch hierher entsandt hat.$B$BEuch gebhrt unser Dank.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9442, "deDE", "Habt Ihr den Pilz schon besorgt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9442, "deDE", "Gorkan hat viel Gutes ber Euch gesagt. Wie sich herausstellt, war alles wahr.$B$BVielen Dank fr Eure Hilfe.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9447, "deDE", "Ihr seid zurck, $N. Habt Ihr Eure Aufgabe erledigt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9447, "deDE", "Mgen die Geister Euch segnen, $N. die Mag'har stehen in Eurer Schuld.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9466, "deDE", "Gibt es Neuigkeiten ber Schwarzkralle?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9466, "deDE", "Ihr seid ein tapferer $C, $N. Diese Bestie hat viele meiner besten Jger das Leben gekostet.$B$BNehmt die Belohnung, Ihr habt sie Euch redlich verdient.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9472, "deDE", "Hervorragend! Diese hinterhltige Gre wird sich mit niemandes Ehemann mehr davonschleichen.$B$BHier habt Ihr eine Kleinigkeit fr Eure Mhen. Ich vertraue darauf, dass diese Angelegenheit unter uns bleibt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9483, "deDE", "Vielleicht werden wir ja doch noch Freunde. Folgt mir! Wir wollen schlielich nicht mit den anderen teilen mssen, oder?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9490, "deDE", "Habt Ihr schon einen Beweis fr Schwarzkralles Tod, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9490, "deDE", "Ja, an diese scharfen Krallen erinnere ich mich genau!$B$BNehmt diese Belohnung, $N. Ihr habt eine gefhrliche Aufgabe erledigt. Ihr habt Euch jedes einzelne Stck hiervon verdient.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9492, "deDE", "Seid Ihr mit den berresten von Kriegshuptling Messerfaust zurck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9492, "deDE", "Gut gemacht, $N. Mit Eurem Sieg in der Hllenfeuerzitadelle haben wir den Orcs groen Schaden zugefgt.$B$BIhr msst mit Feldkommandant Romus ber das weitere Vorgehen und das Ziel, die Orcs weiter in der Defensive zu halten, sprechen. Jetzt, da wir Verstrkung haben, knnen wir die Ehrenfeste viel leichter verteidigen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9493, "deDE", "Ich bin nicht so blauugig zu denken, dass ein Sieg ber die Legionre der Zerschmetterten Hand unseren Sieg ber die gesamte Hllenhorde bedeutet, aber es ist ein guter Anfang.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9493, "deDE", "Das sind groartige Nachrichten, $N. Mit diesen Fortschritten bei der Hllenfeuerzitadelle knnen wir es vielleicht sogar wagen, die Hllenhorde auch in anderen Orten der Halbinsel und der ganzen Scherbenwelt zu bekmpfen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9494, "deDE", "Habt Ihr die, ehm, Glut?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9494, "deDE", "Gut gemacht! Ich mae mir nicht an, Nethekurses Meisterwerke nachahmen zu knnen, aber wir wren dumm, wenn wir die Macht der Teufelsglut auer Acht lassen wrden.$B$BEs gibt jedoch einige, die meine Art der Forschung verbieten wollen, deshalb muss ich meine Entdeckungen geheimhalten. Bitte erzhlt niemandem davon, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9495, "deDE", "Habt Ihr einen Beweis fr den Sieg ber Kargath Messerfaust?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9495, "deDE", "Das habt Ihr gut gemacht, $N. In seiner Jugend war Kargath Messerfaust ein vortrefflicher Krieger, ein Held und ein gutes Beispiel fr sein Volk.$B$BSein Tod ist fr uns alle schmerzhaft, jedoch besonders fr Kriegshuptling Thrall. Was auch immer in seinen letzten Tagen mit ihm geschehen sein mag, wir drfen nie den Orc vergessen, der Kargath einst war, genauso wenig wie die Lektion, die uns sein Fall sein sollte.$B$BFr Eure Dienste fr die Horde werdet Ihr mit einer dieser Waffen belohnt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9496, "deDE", "Ausgezeichnete Arbeit, $N. Jetzt, da die Legionre gefallen sind, wird es einfacher, der Hllenhorde auf dem Schlachtfeld zu begegnen, auch wenn sie immer noch ein ernstzunehmender Gegner ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9498, "deDE", "Willkommen in der Falkenwacht, $R. Alle Verbndeten der Horde sind hier willkommen, insbesondere all jene, mit einer Empfehlung von Martik.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9499, "deDE", "Willkommen in der Falkenwacht, Bruder. Viele andere Blutelfen haben es leider nicht geschafft, heil hier anzukommen.$B$BEs ist eine Schande. Unsere Arbeit in der Scherbenwelt hat doch gerade erst begonnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9543, "deDE", "Wie kann ich Euch behilflich sein, $N? Ihr klingt, als httet Ihr Sorgen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9545, "deDE", "Habt Ihr eine Vision gehabt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9545, "deDE", "Tief in meinem Innersten hoffe ich, dass Eure Vision Euch neue Einblicke auf die Ereignisse, die zu Sedais Tod gefhrt haben, gewhrt hat. Das Relikt zeigte Euch eine mgliche Reihe von Ereignissen, die zur Gegenwart fhrten. Die Wahrheit mag irgendwo dazwischen liegen, doch wir werden es nie mit Sicherheit erfahren.$B$BHttet Ihr mit Eurem jetzigen Wissen anders gehandelt?$B$BWer kann schon sagen, ob unser Konflikt mit den Orcs ewig whren wird? Doch am wichtigsten ist es, immer fr alle Mglichkeiten offen zu sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9558, "deDE", "Wie habt Ihr uns gefunden, $C? Egal, Ihr seid sicher hier, um uns zu helfen. Dann mal an die Arbeit!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9563, "deDE", "Probleme, das Bitter zu finden? Der gute alte Bernie in der Burg Nethergarde sollte Fsser davon auf Lager haben.$B$BEs sei denn, seine Vorrte sind ausgegangen... das wre fatal!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9563, "deDE", "Oh ja! Ihr scheint ganz in Ordnung zu sein, $R. Ich denke, ich kann Euch vertrauen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9572, "deDE", "$N, richtig? Stok'ton hat mir gesagt, dass ich auf Euren Bericht warten soll.$B$BBringt Ihr gute Nachrichten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9572, "deDE", "Das ist ein guter Anfang. Ohne diese drei sollten die Truppen verwirrt sein. Und solange ihre Artillerie auer Gefecht ist, haben wir bessere Chancen, nher vorzurcken, um die Zitadelle einzunehmen.$B$BAber es gehrt mehr dazu, als man glaubt, ich fhle es. Deswegen habe ich einen weiteren Auftrag fr Euch.$B$BFrs Erste jedoch habt Ihr Eure Arbeit gut gemacht. Sucht euch etwas aus Thrallmars Waffenkammer aus.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9575, "deDE", "$N, richtig? Chadwick hat mir gesagt, dass ich auf Euren Bericht warten soll.$B$BWAS macht Ihr hier?! Habt Ihr Eure Aufgabe erledigt? Seid Ihr denn ein kompletter Vollidiot? Ich knnte mich bergeben!$B$BJetzt schwingt Euren nutzlosen Hintern zu diesem Bollwerk und tut, was Euch aufgetragen wurde! Habt Ihr mich gehrt?!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9575, "deDE", "Wisst Ihr, was ich noch mehr hasse, als mich zu irren? Gold bei einer Wette zu verlieren, dass Ihr es nicht schafft, $GKumpel:Schtzchen;.$B$BAusgezeichnet! Ohne diese drei sollten die Truppen verwirrt sein. Und solange ihre Artillerie auer Gefecht ist, haben wir bessere Chancen, nher vorzurcken, um die Zitadelle einzunehmen.$B$BIch habe jedoch das Gefhl, dass da noch mehr dahinter steckt. Deswegen habe ich einen weiteren Auftrag fr Euch. Hier, sucht Euch etwas aus der Waffenkammer aus!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9607, "deDE", "Gefreiter, berichtet! Habt Ihr den Blutkessel schon erkundet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9607, "deDE", "SIE HABEN EINEN GRUBENLORD?!$B$B<Der Truppenkommandant bemerkt die starrenden Gesichter der anderen und senkt seine Stimme.>$B$BDas ist es. Sie mssen das Blut dieses Grubenlords verwenden, um neue Hllenorcs, die aus irgendeinem Grund nicht zur Brennenden Legion gehren, zu erschaffen. Und wenn sie das mit den braunen Mag'har knnen, schaffen sie das auch mit grnen! Bei all den Orcs, die durch das Dunkle Portal kommen, sind das alles andere als gute Nachrichten!$B$BWir mssen sie ins Herz ihrer Militrmacht treffen, und zwar bald!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9608, "deDE", "Habt Ihr den Blutkessel schon erkundet? Was habt Ihr mir zu Berichten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9608, "deDE", "SIE HABEN EINEN GRUBENLORD?!$B$B<Nazgrel bemerkt die starrenden Gesichter der anderen und senkt seine Stimme.>$B$BDas ist es. Sie mssen das Blut dieses Grubenlords verwenden, um neue Hllenorcs, die aus irgendeinem Grund nicht zur Brennenden Legion gehren, zu erschaffen. Und wenn sie das mit den braunen Mag'har knnen, schaffen sie das auch mit uns!$B$BWir mssen sie ins Herz ihrer Militrmacht treffen, und zwar bald!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9912, "deDE", "Willkommen in der Zuflucht des Cenarius, $N. Ich hoffe, Ihr knnt uns bei unserer Aufgabe behilflich sein. Wir haben viel Arbeit vor uns.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10046, "deDE", "Willkommen in der Scherbenwelt, $N - willkommen im Alptraum! Auch wenn das erste Expeditionskorps es unbeschadet hierher geschafft hat, versucht die Legion nun, das Portal zurckzuerobern und unsere Verstrkung daran zu hindern, es zu durchschreiten.$B$BIch wrde Euch gerne hier einsetzen, aber unsere Truppen und die Soldaten der Horde knnen die Kolonnen der Legion, die diese Plattform erobern wollen, selbst zurckhalten.$B$BIhr msst dorthin gehen, wo Ihr am meisten gebraucht werdet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10047, "deDE", "Die Schreie scheinen irgendwie... schwcher geworden zu sein. Mge das Licht dafr sorgen, dass es nicht nur Wunschdenken ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10047, "deDE", "Ihr habt meinen aufrichtigen Dank fr Eure Mhen, $N. Wenn auch noch viel zu tun ist, macht es mich froh, dass einige nun ihren wohlverdienten Frieden gefunden haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10050, "deDE", "Die Shne sind Euch fr Eure Mhen dankbar, auch wenn nur wenige ber das Rstlager sprechen mchten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10055, "deDE", "Habt Ihr ein wenig wiederverwertbares Material unter all dem Staub, der Asche und der Kohle finden knnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10055, "deDE", "Das hier ist in einem besseren Zustand, als ich befrchtet habe. Sicher, es wird eine Weile dauern bis der ganze Rost und Zunder entfernt ist, aber ich kann mich nicht beklagen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10057, "deDE", "Wir knnen nur hoffen, dass die Geister der Fusoldaten mit dem Verschwinden der Geister ihrer Offiziere zur Ruhe kommen. Ich danke Euch ein weiteres Mal fr Eure Hilfe, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10058, "deDE", "Ich dachte, ich htte Euch gesagt, dass Ihr mich nicht stren sollt. Was gibt's?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10058, "deDE", "Ist es das? Kaum zu glauben, dass es die Schlacht berlebt hat. Die Gegebenheiten in den Ruinen waren nicht gerade gut fr das Buch, aber ich bin dennoch froh, dass ich es wiederhabe.$B$BEs ist das einzige Zeichen, das ich von Turalyon habe.$B$B<Vater Devidicus scheint mit den Trnen zu kmpfen.>$B$BWenn man so lange Zeit Seite an Seite gekmpft hat, steht man sich noch nher als die eigene Familie.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10078, "deDE", "Ihr scheint guter Laune zu sein. Haben die brennenden Belagerungsmaschinen der Horde Euch erheitert?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10078, "deDE", "Das nenne ich Einsatz!$B$BIch bin froh, dass wir endlich Untersttzung erhalten haben, aber ich bin nicht so sicher, ob sie die richtige Einstellung gegenber der Horde haben. Dort, wo ich herkomme, ist jeder, der eine Waffe auf Euch richtet, ein Feind und muss vernichtet werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10079, "deDE", "Bei Bronzebarts bronzenem... Bart, Ihr habt es geschafft! Ihr seid ein Held, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10086, "deDE", "Wie geht es mit der Sammelaktion voran? Habt Ihr etwas Wiederverwertbares finden knnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10086, "deDE", "Die Materialien sind ausgezeichnet! Vielleicht ein wenig verbogen oder angesengt, aber ich habe mich noch nie von minderwertigem Material aufhalten lassen, wenn es darum ging, Przisionssprengstoff herzustellen!$B$BDanke, $N. Ihr habt wirklich ein Hndchen exquisiten Mll!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10087, "deDE", "Ich habe nach Zeichen von brennenden Kanonen Ausschau gehalten - Rauchschwaden oder den Schreien brennender Hllenorcs...$B$BHattet Ihr Erfolg mit Eurem Auftrag?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10087, "deDE", "Dachte ich's mir doch! Ich konnte die Zerstrung von hier aus zwar nicht richtig sehen, aber ich kann sie mir redlich vorstellen! Ausgezeichnet!$B$BGut gemacht, $N. Das oberste Kommando von Thrallmar wird sich freuen, von Eurem Erfolg zu hren!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10093, "deDE", "Ah, Ihr habt also Seher Kryv getroffen. Er ist ein weiser und begabter Mann, und seine Freunde sind auch meine Freunde.$B$BMacht es Euch gemtlich und stellt Euch den anderen vor, die diesen Tempel ihr Zuhause nennen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10099, "deDE", "Ich bin froh, dass Ihr Euch um, um... wie sagtet Ihr hie er noch gleich? Z'kral?$B$BEurer Beschreibung nach war es wohl mehr ein es als ein er. Nun ja, was zhlt ist, dass es tot ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10103, "deDE", "Um ehrlich zu sein hatte ich mehr Leute erwartet, aber Ihr msst reichen. Willkommen im Sumpfrattenposten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10106, "deDE", "Gut gemacht, $N. Eure Taten auf dem Schlachtfeld sind fr unseren Kampf auf der Hllenfeuerhalbinsel sehr hilfreich.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10110, "deDE", "Gute Arbeit, $N. Ich konnte fast die Kampfschreie an den Befestigungsanlagen hren. Ich wnschte, ich wre dabei gewesen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10119, "deDE", "Willkommen in der Scherbenwelt, $N - willkommen im Alptraum! Auch wenn das erste Expeditionskorps es unbeschadet hierher geschafft hat, versucht die Legion nun, das Portal zurckzuerobern und unsere Verstrkung daran zu hindern, es zu durchschreiten.$B$BIch wrde Euch gerne hier einsetzen, aber unsere Truppen und die Soldaten der Horde knnen die Kolonnen der Legion, die diese Plattform erobern wollen, selbst zurckhalten.$B$BIhr msst dorthin gehen, wo Ihr am meisten gebraucht werdet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10120, "deDE", "Ich habe gesehen, wie Ihr mit Orion gesprochen habt - es muss eine wichtige Angelegenheit sein...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10120, "deDE", "Ihr seid also Orions neuer Bote? Ich hoffe, es ergeht Euch besser als dem letzten, den er geschickt hat, $C. Mit der vorrckenden Legion schafft Ihr es niemals zu Fu nach Thrallmar. Sagt Bescheid und mein treuer Wyvern wird Euch sicher dorthin bringen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10121, "deDE", "Ihr habt sicher auf Eurem Flug vom Dunklen Portal die Krieger der Legion berall in der Landschaft gesehen. Auch wenn wir sie in der Schlacht am Berg Hyjal vor etwa 5 Jahren besiegt haben, haben diese verfluchten Dmonen immer noch nicht genug. Wenn sie auf eine Herausforderung aus sind, wird die Horde ihnen entsprechend antworten!$B$BWir Orcs haben mit der Legion mehr als nur eine Rechnung zu begleichen! Was ist mit Euch, $N? Soll ich Euch von unserem Feind erzhlen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10123, "deDE", "Ausgezeichnete Arbeit, Soldat. Die Hufigkeit der Angriffe wird nun sicher nachlassen - zumindest bis sie neue Schreckenslords schicken.$B$BNun zu den schlechten Nachrichten: ich habe Eure neuen Befehle...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10124, "deDE", "Ihr habt also die Teufelsfunkenklamm aufgerumt, was? Gut zu hren - wir brauchen Soldaten wie Euch, die sich kopfber in die Schlacht strzen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10129, "deDE", "Wenn Ihr beim ersten Versuch verfehlt, startet einen neuen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10129, "deDE", "Das wird sie um Wochen zurckwerfen! Fantastische Arbeit, $N. Die Legion htte sicher nie mit so starkem Widerstand gerechnet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10132, "deDE", "Gute Arbeit.$B$BWenn Ihr mich fragt, mssen wir einfach genug von diesen Kolossen tten, dann brauchen wir uns nicht mehr um diese merkwrdigen Kristall in der Mitte kmmern.$B$BHier, nehmt eines hiervon als Belohnung der Expedition des Cenarius.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10134, "deDE", "Das ist ein ganz schn groer Kristall, den Ihr da habt. Habt Ihr ihn von den Kolossen im Grat?$B$BLasst mich ihn anschauen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10134, "deDE", "Hm, fr mich sieht es einfach wie ein groer Klumpen ungeschliffenen Kristalls aus.$B$BZugegeben, ich fhle eine kranke, bse Aura, die von ihm ausgeht, aber das ist nicht gerade mein Spezialgebiet. Ich bin nur hier, um die Bedrohung durch die Bergriesen aufzuhalten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10136, "deDE", "Alle kommenden Generation werden diesen Tag bejubeln, $N. Sie werden von Eurem Heldenmut singen. Ich wnschte, Thrall wre hier, um diesen Sieg mit uns zu feiern. Die Legion liegt am Boden, und die Hllenfeuerhalbinsel gehrt wieder der Horde! Vielleicht besteht doch noch Hoffnung fr diese verwstete Welt und unser Volk auf ihr.$B$BLok'tar ogar!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10140, "deDE", "Dem Licht sei Dank, dass Ihr heil hier angekommen seid. Die Ehrenfeste bentigt alle erfahrenen Krieger, die sie bekommen kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10140, "deDE", "Unsere Expedition zu dieser verlassenen, zerschlagenen Welt wre fast zum Stillstand gekommen, $N. Wir haben mehr Widerstand erfahren, als wir dachten.$B$BAls ob die Brennende Legion nicht schon schlimm genug wre, gibt es in diesen Lndern Schrecken, die wir noch nie zuvor gesehen haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10141, "deDE", "Truppenkommandant Danath hat meine Anfrage nach Verstrkung wohl etwas falsch verstanden. Ihr seht zwar fhig aus, aber... ich habe um ein komplettes Bataillon gebeten!$B$BWisst Ihr, wir haben da ein kleines Dmonenproblem.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10142, "deDE", "Ausgezeichnete Arbeit, Soldat. Die Hufigkeit der Angriffe wird nun sicher nachlassen - zumindest bis sie neue Schreckenslords schicken.$B$BNun zu den schlechten Nachrichten: ich habe Eure neuen Befehle...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10143, "deDE", "Schn, Euch kennenzulernen, Soldat.$B$BGenug gequatscht... nun hrt zu - Ich habe eine wichtige Mission fr Euch und ich mchte, dass Ihr sie erfllt... und berlebt!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10144, "deDE", "Ich wnschte, ich htte die Feuer sehen knnen. Dieser Sprengstoff hat mchtig Dampf!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10144, "deDE", "Gut gemacht, $N. Die Zerstrung der Portale wird den Nachschub an Verstrkung fr die Legion fr eine Weile bremsen!$B$BAber noch knnen wir uns nicht ausruhen...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10146, "deDE", "Dies ist ein wichtiger Auftrag, $N. Ich wrde ihn nicht an irgendjemanden vergeben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10146, "deDE", "Das wird sie um Wochen zurckwerfen, kein Zweifel! Ihr habt mir gerade ungewhnlich gute Laune verschafft, $N!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10159, "deDE", "Wie luft es mit der Bereinigung des Dornnebelhgels?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10159, "deDE", "Jetzt, da die Verheerer fort sind, knnen die Reisenden der Zuflucht des Cenarius in den Zangarmarschen wieder sicher vorankommen.$B$BDie Expedition des Cenarius ist Euch etwas schuldig, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10160, "deDE", "<Leutnant Amadi nickt.>$B$BGut, wir knnen jede Hilfe gebrauchen. Die zunehmende Orcbevlkerung am Sdlichen Bollwerk bereitet mir Kopfschmerzen. Ich habe einen Spher losgeschickt, um sie zu erkunden, aber er ist noch nicht zurck.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10161, "deDE", "Habt Ihr gengend Teile gefunden? Wenn nicht muss ich die Bratpfanne verwenden, mit der Legassi die ganze Zeit herumwedelt. Dann muss ich den Zeppelin in die `Fliegende Bratpfanne` umbenennen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10161, "deDE", "Einige davon sind in einem ziemlich schlechten Zustand, aber ich sollte damit zurechtkommen. Es braucht nur ein wenig kreative Ingenieurskunst und es wird sicher klappen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10162, "deDE", "Zeigt ihnen, dass nicht einmal die Himmel sicher sind!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10162, "deDE", "Ihr fliegt, als httet Ihr Euer Leben lang nichts anderes getan, $N. Hervorragende Vorstellung! Und nicht einmal ein kleiner Kratzer an den Wyvernzerstrern!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10163, "deDE", "Zeigt ihnen, dass nicht einmal die Himmel sicher sind!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10163, "deDE", "Ihr fliegt, als httet Ihr Euer Leben lang nichts anderes getan, $N. Hervorragende Vorstellung! Und nicht einmal ein kleiner Kratzer an den Greifenzerstrern!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10208, "deDE", "Die Legion ist bestimmt nicht auf unsere berraschung gefasst, was, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10208, "deDE", "Gut gemacht! Ich konnte die Explosionen fast bis hierher spren!$B$BJetzt haben wir noch eine weitere Bombenmission fr Euch...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10213, "deDE", "Wir wurden von der Brennenden Legion abgeschossen, obwohl wir eine Friedensflagge geschwenkt haben. Wer htte ahnen knnen, dass die Brennende Legion so gemein ist?$B$BWir knnen leider nicht mit Euch zum Rckenbrecherposten kommen. Wir mssen den Zeppelin reparieren und dann weiterziehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10220, "deDE", "Die Unerschtterlichen haben die Macht der Horde gesprt. Vielleicht haben sie jetzt ja Lust, sich meinen Vorschlag anzuhren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10229, "deDE", "Was ist das? Ein Buch von einem unerschtterlichen Geist? Wie merkwrdig. Lasst es mich anschauen, vielleicht kann ich mehr ber die Geschichte der Unerschtterlichen erfahren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10229, "deDE", "Ich habe mein ganzes Unleben lang alte Geschichte studiert, doch nie zuvor etwas wie dieses Buch gesehen. Er erzhlt die gesamte Geschichte des Rstlagers, sogar die Ereignisse nach seiner Zerstrung.$B$BDie Unerschtterlichen haben ihre Geschichte aufgeschrieben, sogar nach ihrem Tod in der Schlacht. Mit diesem Buch knnen wir die Unerschtterlichen verstehen und sie sicher davon berzeugen, sich uns anzuschlieen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10230, "deDE", "Wir mssen den Unerschtterlichen bald helfen. Wenn wir sie davon berzeugen knnen, sich uns anzuschlieen, hat die Horde einen starken Verbndeten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10230, "deDE", "Gut gemacht, $N. Dieses alte Horn wird uns helfen, die Unerschtterlich zu rufen.$B$BNatrlich nur, wenn sie es mchten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10236, "deDE", "Habt Ihr schon ein paar Ersatzteile fr meinen Schredder gefunden? Ich mchte ihn wirklich gerne reparieren, damit ich mich weiter dem Bergbau in der Mine widmen kann. Irgendwie muss ich ja ein wenig Geld verdienen. Und wenn ich davon nur eine Fahrkarte nach Beutebucht kaufe. Ohne Rckfahrt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10236, "deDE", "Hurra, Ihr habt ein paar Teile gefunden! Jetzt kann ich meinen Schredder hoffentlich wieder zum Laufen bringen. Wenn ich genug Geld verdiene, kann ich vielleicht wieder zurck nach Beutebucht.$B$BIch hoffe, dass mein Vetter Quack Lufthans mit seinem blden Zeppelin abstrzt. Ich kann es nicht fassen, dass er mich so hinterhltig in die Scherbenwelt gelockt hat.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10238, "deDE", "Vielen Dank, dass Ihr meine Peons gerettet habt. Vielleicht kann ich sie jetzt dazu bringen, meinen Schredder mit den Teilen, die Ihr gefunden habt, zu reparieren. Dann kann ich wieder etwas Geld verdienen und aus der Scherbenwelt abhauen. Ich kann es kaum abwarten, wieder in Beutebucht zu sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10242, "deDE", "Willkommen im Rckenbrechergrat. Wie kann ich Euch dienen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10242, "deDE", "Ein Bluttest also? Es wird mir ein Vergngen sein. Ich arbeite immer gerne mit Blut...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10250, "deDE", "Habt Ihr schon in das Horn gestoen? Wir mssen es bald tun. Wer wei, wann Thrallmar die Untersttzung der Unerschtterlichen braucht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10250, "deDE", "Ich bin froh, dass die Unerschtterlichen Euch im Kampf untersttzt haben. Ich dachte mir schon, dass sie dem Ruf ihres Schlachthorns folgen wrden, aber ich war mir nicht sicher. Vielleicht haben wir ihnen jetzt die Rache ermglicht, die sie so sehr begehrten.$B$BIhr habt Urtrak gettet. Diesen Sieg mssen wir Kommandant Hogarth im Rstlager der Allianz melden. Er wird verstehen, welche Gefahren Ihr fr ihn auf Euch genommen habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10254, "deDE", "Ich bin froh, dass Ihr hier seid, Bruder. Ich bin Danath Trollbann - Anfhrer der Shne des Lothar und Truppenkommandant dieser Feste.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10255, "deDE", "Habt Ihr die Ergebnisse des Experiments, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10255, "deDE", "<Thiah Rotmhne macht groe Augen, als Ihr die Ergebnisse des Experiments beschreibt.>$B$BWie konnte das nur geschehen? Ich bin mir sicher, dass ich das Gegengift richtig hergestellt habe!$B$BIch habe gesehen, wie es mit Spinnen- und Schlangengiften funktioniert hat, warum kehrt sich die Wirkung hier nicht um? Vielleicht ist das eine der vielen Besonderheiten der Scherbenwelt, $N. Ich leite diese Information am besten an meine Kameraden weiter.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10258, "deDE", "Wir haben die Schlacht vor so langer Zeit gegen die Hllenorcs verloren. Ihr habt uns unsere Ehre zurckgegeben, indem Ihr Urtrak gettet habt.$B$BNehmt Euch eine unserer Waffen. Wenn Ihr gegen unseren verhassten Feind in den Kampf zieht, benutzt diese Waffe, und die unter uns, die frei vom Fluch sind, werden Euch zu Hilfe kommen.$B$BBevor Ihr geht mchten einige, die vor Jahren beim Belagerungsturm gefallen sind, Euch fr die Zerstrung unseres Feindes Ehre erweisen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10278, "deDE", "Habt Ihr meinen Apparat schon ausprobiert? Ich glaube, dass mein instabiler Sphrenrissgenerator so nahe am Abgrund funktionieren sollte.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10278, "deDE", "Unglaublich. Ihr habt die Leere selbst berhrt! $N, Ihr habt mein Lebenswerk gerettet. Aber es gibt noch mehr, was getan werden muss, und Ihr knnt mir helfen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10286, "deDE", "Ich werde reden, $C. Nehmt die Waffe runter, ich will nicht noch mehr rger haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10287, "deDE", "Viera Sonnenwisper? Diese widerliche kleine Gre.$B$BIch werde ihr zeigen, dass man mit mir keine Spielchen treibt!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10288, "deDE", "Ich habe gehrt, wie Ihr mit Duron gesprochen habt. Ihr msst einen wichtigen Auftrag haben!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10288, "deDE", "Berichte fr die Ehrenfeste? Kein Problem, einer meiner Greifen wird Euch schnell und sicher dorthin bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10289, "deDE", "Ein Bericht von Orion? Gebt ihn mir, schnell!$B$B<Stirnrunzelnd berfliegt General Krakork den zerknitterten Brief.>$B$BVerdammt, es ist genau so, wie wir es befrchtet haben. Orion und seine Krieger knnen jeden Moment berrannt werden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10289, "deDE", "Ich werde alle Truppen, die ich entbehren kann, zu Orion beim Dunklen Portal schicken. Die Legion wird den Schlachtruf der Horde nicht so bald vergessen! Ihr, $N, habt Thrallmar heute einen groen Dienst erwiesen. Ich glaube, Ihr knnt uns hier eine groe Hilfe sein. Natrlich nur, wenn Ihr klug genug seid, um zu berleben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10291, "deDE", "Wir haben schon viele tapfere Krieger an dieses verfluchte Land verloren. Ich habe kein Interesse daran, noch mehr ungeprfte Rekruten in das Massaker zu schicken.$B$B<Nazgrel schaut Euch durch seine zottige Wolfsmaske streng an.>$B$BAber wenn General Krakork Euch zu mir geschickt hat, muss an Euch ja etwas dran sein. Was sagt Ihr also, $N? Werdet Ihr dem Kriegshuptling mit Ehre dienen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10294, "deDE", "Habt Ihr es schon getan? Habt Ihr die Splitter?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10294, "deDE", "Ausgezeichnet. Der verbesserte Sphrenrissgenerator ist fast fertig. Es gibt nur noch einen letzten Bestandteil, den wir brauchen. Bald knnen wir einen stabilen Sphrenriss ffnen, den wir in den kommenden Jahren untersuchen knnen.$B$BWer wei, welche fremdartigen Kreaturen durch ihn hindurchkommen werden? Stellt Euch nur vor, $N, die Macht, die ein solches Wissen mit sich brchte, wre unermesslich!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10295, "deDE", "Habt Ihr den Baron der Leere Galaxis schon vernichtet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10295, "deDE", "Ihr habt mein Lebenswerk gerettet, $N.$B$BSie haben mich wahnsinnig genannt, mich ausgelacht und gedacht, meine Besessenheit auf den Leerengrat und die Sphrenfelder htte mich krank gemacht. Mit diesem Seelensplitter kann ich das Geheimnis der Leerwandler lften. Ich werde die Macht ber den Abgrund beherrschen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10340, "deDE", "Ha ha! Ein frischer $R vom Festland! Nun, keine Sorge - der Trmmerposten kann Euch vielleicht etwas seekrank machen, aber er treibt nicht weit weg!$B$BUnd bisher hat er sich nur einmal auf den Kopf gedreht... oder zweimal.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10344, "deDE", "Ihr habt mit Runetog gesprochen? Er ist ein guter Zwerg, hlt seine Greifen gut in Schuss und immer kampfbereit.$B$BIch bin Greifgar, Kommandant des Trmmerpostens. Ich wei, es ist nicht gerade hbsch hier, aber dieser Felsklotz ist ein wichtiger Sttzpunkt fr Operationen auf der Hllenfeuerhalbinsel. Man kann hier rund um die Uhr Greifen ein- und ausfliegen sehen.$B$BEinen Rat? Hier ist einer: haltet Eure Ausrstung bereit, Euren Magen leer und Eure Augen am Horizont... wenn der Trmmerposten anfngt auseinanderzufallen, haltet Euch an etwas fest, was nicht wegfliegen kann!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10346, "deDE", "In der Abyssischen Untiefe sind immer noch gengend Ziele, $N. Schnappt Euch einen Greifen, wenn Ihr fr eine weitere Runde bereit seid.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10346, "deDE", "Wie ist es gelaufen? Ihr seid ja in guter Verfassung... Ist sie gut genug fr einen weiteren Flug?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10347, "deDE", "Die Legion darf sich auf der Abyssischen Untiefe nicht in Sicherheit wiegen! Wir mssen immer und immer wieder zuschlagen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10347, "deDE", "Die anderen Windreiterfhrer halten sehr viel von Euch, $N. Ihr habt in der Abyssischen Untiefe ganz schn aufgerumt. Macht weiter so!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10349, "deDE", "Ist das ein Stck des riesigen Kristallsplitters oben auf dem Himmelssturzgrat?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10349, "deDE", "Oh, ausgezeichnet!$B$BIch habe die ganze Zeit gehofft, an ein Stck davon zu kommen, aber Tola'thion kommt nicht in die Gnge. Er sollte mir eigentlich helfen herauszufinden, was im Himmelssturzgrat vor sich geht. Stattdessen schickt er nur arme Tlpel wie Euch - nichts fr ungut - dort hoch auf die Schlachtbank.$B$BWenn Ihr mir den Kristall gebt, kann ich vielleicht herausfinden, in welcher Verbindung er zu den Kolossen steht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10351, "deDE", "Hat es funktioniert? Was habt Ihr beobachtet? Habt Ihr herausgefunden, was im Himmelssturzgrat passiert ist?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10351, "deDE", "Goliathon? Pathaleon der Kalkulator?$B$BKAEL'THAS!?$B$BPrinz Kael'thas hat seine Hand im Spiel?!$B$B<Die Erdbinderin schumt vor Wut.>$B$BWas er getan hat ist Wahnsinn! Riesige Kristalle vom Himmel zu werfen und das Land zu dezimieren kann nur die Arbeit eines Verrckten sein!$B$BIch verspreche Euch, dass der Zirkel des Cenarius nun, da wir wissen was zu tun ist, jegliche weitere Versuche von Illidan und seinen Streitmchten, so etwas noch einmal zu tun, von uns vereitelt werden.$B$BWir stehen tief in Eurer Schuld, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10367, "deDE", "Habt Ihr den Schlssel schon, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10367, "deDE", "Ich danke Euch, $N. Dank Eurer Hilfe wird mein Volk bald frei sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10368, "deDE", "Sagt mir, $N. Sind die ltesten des Lumpenpacks schon frei?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10368, "deDE", "Ich kann es fhlen, $N! Die Geister kehren zu uns zurck.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10369, "deDE", "Ist es vollbracht, $N? Ist Arzeth tot?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10369, "deDE", "Es ist wahr, $N. Ich war einst Naladu, der Bewahrer der Erde.$B$BIch war es, der die anderen Mitglieder des Lumpenpacks fr leere Versprechungen von Macht verkauft hat. Ihr knnt mich nun verachten, wenn Ihr wollt. Wichtig ist, dass mein Stamm nun wieder frei ist und die Geister zu uns zurckgekehrt sind.$B$BIch werde mich dem Rest des Stammes nicht anschlieen, aber nun kann ich in Frieden sterben. Mgen die Geister der Erde unter meinen Brdern einen neuen Diener finden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10382, "deDE", "Das trifft sich gut, $C. Ich bin froh, dass dieser brummige alte Zwerg es endlich auf die Reihe bekommen hat, uns ein wenig Hilfe zu schicken!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10388, "deDE", "Ah, $N, Ihr kommt genau zur rechten Zeit. Unsere Spher haben herausgefunden, dass es sich bei den Ansammlungen der Legion auf dem Grat hinter uns tatschlich um 'Konstruktionslager' handelt. Dort stellen die Dmonen ihre infernalen Teufelshscher her. Wenn wir schnell gegen sie vorgehen, knnen wir vielleicht ihre Produktion aufhalten!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10389, "deDE", "Bravo, $N. Ich wusste, dass Ihr Erfolg haben werdet! Das Ende der Legion rckt immer nher!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10390, "deDE", "Beeilt Euch, $C. Wir mssen das Konstruktionslager: Mageddon vernichten, bevor die Dmonen mit dem Bau fertig sind!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10390, "deDE", "Gut gemacht, $C. Ein Konstruktionslager weniger, um das wir uns sorgen mssen. Die Legion wird eine Weile brauchen, um die Einrichtung wieder zum Laufen zu bringen. Ihr habt uns das beschafft, was uns im Augenblick am meisten fehlt: Zeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10391, "deDE", "Ich wusste, dass Ihr Biss habt, $N, aber dieses Mal habt Ihr Euch selbst bertroffen! Wir haben die Explosionen dieser verfluchten Kanonen bis hierher gehrt! Das msste der Legion beweisen, dass sie die Shne Durotars nicht unterschtzen sollten!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10392, "deDE", "Beeilt Euch, $N. Mit jeder Sekunde, in der das Warpportal offen steht, betritt ein weiterer Dmon diese Welt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10392, "deDE", "Gelobt seien die Ahnen, Ihr habt es geschafft, $N! Die Legion hat eine schreckliche Niederlage einstecken mssen. Thrallmar ist wieder sicher. Ach, wenn ich doch nur hundert Krieger mit Eurem Herz und Eurem Verstand htte, dann wre dieses zerschlagene Land schon lngst gezhmt. Ich salutiere vor Euch.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10393, "deDE", "Ich habe nicht viel Zeit, $C. Womit wollt Ihr mich belstigen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10393, "deDE", "Es war ganz richtig von Euch, mir dies zu bringen. Es knnte sich um einen Befehl zur Herstellung von zustzlichen Teufelshschern handeln - oder um neue Angriffsplne! Es wird etwas dauern, bis ich es entziffert habe. Lasst mich alleine. Ich lasse Euch rufen, wenn ich Genaueres wei.$B$B<Magister Blutfalke nickt Euch zu, als Ihr Euch abwendet.>$B$BIhr seid sehr gescheit, $N. Ich werde Eure Fortschritte mit Interesse verfolgen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10394, "deDE", "Nun, ich muss zugeben, ich bin beeindruckt, $N. Ich htte nicht gedacht, dass Ihr den Schneid fr diese Mission habt. Wirklich gut gemacht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10395, "deDE", "Was ist los, $C? Ich spre, dass Ihr etwas gefunden habt, das von Verdammnis kndet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10395, "deDE", "Welch glcklicher Zufall, dass Ihr diesen Schriftwechsel abgefangen habt, $N. Ich werde zwar etwas Zeit brauchen, bis ich ihn entziffert habe, aber ich kann den Hass und die Eile, die in dieser Schriftrolle stecken, deutlich spren. Ich habe das Gefhl, dass die wahren Plne der Legion in dieser Welt sich bald offenbaren werden...$B$BGebt mir einen Moment Zeit, um die Schrift zu entziffern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10396, "deDE", "Eines muss ich Euch lassen, $N, Ihr seid ein geborener Kmpfer! Diese Tlpel der Horde werden vielleicht nie erfahren, wie knapp sie ihrem Ende entronnen sind. Es erfordert sehr viel Rckgrat, um dem Unheil im Namen seiner eigenen Feinde entgegenzutreten. Genau diese Ehre und dieses Pflichtgefhl machen die Allianz aus!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10397, "deDE", "Habt Ihr das Warpportal schon geschlossen, $N? Whrend Ihr hier herumtrdelt kommen immer mehr Dmonen in die Scherbenwelt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10397, "deDE", "Gute Arbeit! Das sollte die Zahl der Dmonen, die uns auf die Pelle rcken, dramatisch verringern. Ich kann Euch nicht genug fr Eure Hilfe danken, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10399, "deDE", "Ich habe diesen groartigen Sieg vorausgesehen, $N. Die gesegneten Naaru schenken Euch an diesem Tag ihr Lcheln.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10400, "deDE", "Dem Licht sei Dank, ich wusste, dass Ihr es schafft! Arazzius und seine Diener sind Geschichte! Bald wird die Legion nach Hause flchten und diese geschundene Welt wird wieder sicher sein. Und dann, wenn endlich Gerechtigkeit herrscht, knnen auch wir nach Hause gehen. $B$BVielen Dank, $N. Die Allianz und die Shne Lothars feiern Euch heute!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10403, "deDE", "Ihr seid gekommen, um meinem Stamm zu helfen, $R? Wie ungewhnlich. Es gibt nicht viele, die sich um das Schicksal der Zerschlagenen in diesem Land scheren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10442, "deDE", "Oh, gut, Ihr msst die Hilfe sein, die ich vor Wochen von der Falkenwacht angefordert habe.$B$BNun, jetzt seid Ihr hier und es gibt viel zu tun. Ich habe genau das Richtige fr Euch fr den Anfang.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10443, "deDE", "Oh, gut, Ihr msst die Hilfe sein, die ich vor Wochen vom Tempel von Telhamat angefordert habe.$B$BIch bin froh, dass Ihr hier seid; es gibt viel zu tun. Ich habe genau das Richtige fr Euch fr den Anfang.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10449, "deDE", "Es ist mir eine Ehre, der Horde dienen zu knnen. Wie kann ich Euch helfen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10449, "deDE", "Ah ja. Das Blut des Knochenmalmerklans. Ich interessiere mich sehr fr diese Orcs... sie sind zwar immer noch die verkommenen Feinde unserer Vergangenheit, aber ihre Strke und Macht ist grer, als ich gedacht habe...$B$BNun, wollen wir beginnen, oder?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10450, "deDE", "Habt Ihr Euch den Knochenmalmern gestellt, $N? Ich hoffe, dass Ihr ihr Blut in rauen Mengen vergossen habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10450, "deDE", "Hah! Ihr habt also die Knochenmalmer, unsere verkommenen Vettern getroffen. Gut gemacht, $N. Ich bedauere nur, dass nicht mehr von ihnen ihr Leben lassen und ihr Blut vergieen konnten, um meinen Zorn zu besnftigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10482, "deDE", "Sehr gut. Ich bin mir sicher, dass ihr Verlust in der Hllenfeuerzitadelle bemerkt wird. Auch wenn diese dunkle Bastion uns berschattet und unberwindlich scheint, knnen wir mit stolzen Taten wie Eurer sicher einen Weg finden, sie zu besiegen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10483, "deDE", "Bringt Ihr Neues von der Ehrenfeste, $N? Das ist gut. Unseren Vorgehen gegen die Dmonen und Hllenorcs gehen so langsam die fhigen Kmpfer aus!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10483, "deDE", "Ah, Ihr habt ein Insigne von den Orcs des Blutenden Auges ergattert? Gut! Es ist eine Schande, dass sie unsere Symbole gegen uns verwenden und sich ber unsere Kameraden, die im Kampf gegen diese rothutigen Bestien gefallen sind, lustig machen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10484, "deDE", "Habt Ihr die Talismane, $N? Es schmerzt mich zu wissen, dass diese verdammten Orcs die Insignien unserer Soldaten besitzen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10484, "deDE", "Danke, $N. Mein Herz ist voller Trauer, wenn ich diese Talismane betrachte. Sie unterstreichen die zahlreichen Verluste, die wir in der Ehrenfeste gegen die Hllenorcs hinnehmen mussten. Aber es freut mich zu sehen, dass Ihr Erfolg gegen sie hattet. Wenn wir gengend von ihnen tten knnen, treten wir vielleicht als Sieger aus diesem Krieg hervor.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10485, "deDE", "Habt Ihr Morkhs zerschmetterte Rstung? Habt Ihr Euch wieder todesmutig nach Zeth'Gor begeben?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10485, "deDE", "Ihr habt es geschafft! Morkh ist tot! Jetzt haben wir eine Chance im Kampf gegen die Bestien der Hllenorcs!$B$BVielen Dank, $N. Eure Hilfe in diesem Krieg ist unermesslich.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10538, "deDE", "Habt Ihr das gekochte Blut, $N? Ich wrde es furchtbar gerne ohne Unterbrechungen untersuchen knnen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10538, "deDE", "Sehr gut. Jetzt kann ich meine Tests viel leichter durchfhren...$B$BVielen Dank, $N. Eure Begabungen sind beachtlich.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10629, "deDE", "Konntet Ihr meine Schlssel finden? Ich bin mir sicher, dass einer der Hunde ihn gefressen hat. Wahrscheinlich ist es der, den ich Euch mitgegeben habe. Wahrscheinlich.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10629, "deDE", "Ihr riecht ja frchterlich! Aber Ihr habt meine Schlssel gefunden. Vielen herzlichen Dank! Jetzt kann ich meinen Schredder in Betrieb nehmen und ein wenig Gold verdienen. Bald werde ich das Geld zusammen haben, um die Scherbenwelt zu verlassen. Ich wei nicht, warum Ihr hier bleiben wollt. Ich hasse diesen Ort. Wrt Ihr nicht viel lieber wieder in Beutebucht?$B$BVielleicht um ein wenig zu Angeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10630, "deDE", "Diese Gan'arg werden sich jetzt sicher verziehen. Und in einer Woche bin ich wieder in Beutebucht. Ich kann es kaum abwarten, endlich wieder angeln gehen zu knnen. Es war ein Fehler in die Scherbenwelt zu kommen. Wenn Ihr meinen Rat hren wollt, macht Euch schleunigst auf den Weg zurck nach wo Ihr herkommt. Verbringt ein wenig Zeit im Schlingendorntal! Oder wenn Ihr mehr auf Abenteuer aus seid im Krater von Un'Goro. Hauptsache nicht hier.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10754, "deDE", "Habt Ihr mir etwas zu zeigen, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10754, "deDE", "Kann das wahr sein? Lasst mich die Form einmal genauer betrachten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10762, "deDE", "Was ist das?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10762, "deDE", "Ein Schlssel? Danath sollte uns eigentlich an Belagerungsmaschinen arbeiten lassen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10763, "deDE", "Habt Ihr die Materialien, um die ich Euch gebeten habe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10763, "deDE", "Dann wollen wir es mal versuchen. Mal sehen, was hinter der Schmiedekunst der Orcs steckt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10764, "deDE", "Habt Ihr getan worum ich Euch gebeten habe, $C?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10764, "deDE", "Ihr habt es geschafft, $GJunge:Mdel;! Jetzt msst Ihr sie nur noch zur Hllenfeuerzitadelle bringen und ein paar Orcs erlegen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10792, "deDE", "Hah! Ich konnte die Flammen ber Zeth'Gor bis hierher sehen! Mgen unsere Feinde des Blutenden Auges zu Asche zerfallen und keinem wahren Orc mehr unter die Augen kommen!$B$BVielen Dank, $N. Ihr habt geholfen, eine Beleidigung fr den Geist eines jeden Orcs hinfort zu brennen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10809, "deDE", "Berichtet, $N. Ist das Blut des Blutenden Auges da auf Eurem rmel? Ich hoffe doch!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10809, "deDE", "Ihr habt also den Tunichtgut Kruush erschlagen! Gut gemacht, $N! Sehr gut! Sein Tod wird seinen verfluchten Worgreitern die Giftzhne ziehen! Ich hoffe, Ihr habt auch ein paar von ihnen erledigt, als Ihr Euren kleinen Ausflug nach Zeth'Gor gemacht habt!$B$BHier ist Eure Belohnung. Ihr habt sie Euch verdient!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10813, "deDE", "Die Orcs des Blutenden Auges waren einst starke Verbndete... und nun sind sie noch strkere Feinde.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10813, "deDE", "Ich stehe tief in Eurer Schuld, $N. Ihr habt ein weiteres Mal ber Zeth'Gor triumphiert und seine Dunkelheit nahe an Euer Herz gelassen. Ich werde das Auge untersuchen und damit hoffentlich die Geheimnisse seines Meisters Grillok lften.$B$BHoffentlich kann es seine Magie nicht lnger einsetzen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10834, "deDE", "Die Bsartigkeit der Orcs des Blutenden Auges kennt keine Grenzen und wird nur noch von der ihrer Vettern der Zerschmetterten Hand bertroffen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10834, "deDE", "Ah ja. Ich frage mich, ob Grillok in seinen dunklen, dunklen Trumen immer noch mit seinem verlorenen Auge verbunden ist, wie jemand, der eine Hand oder ein Bein in der Schlacht verloren hat...$B$BAuch ich werde davon trumen. Und wenn das Schicksal auf unserer Seite steht, werden wir dadurch erfahren, was Grillok und die Orcs des Blutenden Auges dazu gebracht hat, sich den Dmonen zu unterwerfen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10835, "deDE", "Guten Tag, $N. Wie gefllt Euch Euer Aufenthalt auf der Hllenfeuerhalbinsel? Ich bin mir sicher, dass er sehr ergiebig ist...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10835, "deDE", "Ah, ein Bericht von Albreck? Sehr gut. Die knigliche Apothekervereinigung versucht verzweifelt herauszufinden, wie die Orcs zu Hllenorcs geworden sind. Ihr Verrat ist unverzeihlich... und wir mssen sicherstellen, dass unsere Orcfreunde fr alle Zeit vor dieser Verderbnis sicher sind.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10838, "deDE", "Habt Ihr die Messung? War Euer Auftrag mit dem dmonischen Seher erfolgreich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10838, "deDE", "Ihr habt die Messung? Ausgezeichnet. Ich bin mir sicher, dass diese Daten sich fr unsere Forschungen als unersetzlich erweisen werden. Ich werde sie gleich an Apothekerin Zelana und Apotheker Albreck weiterleiten.$B$BIhr solltet stolz auf Euch sein, $N. Eure Strke und Euer Mut steht auer Frage... genauso wie Eure Leistungen gegenber der Horde.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10864, "deDE", "Gut gemacht, $N. Eure Seele ist mit vielen Toden belastet... und der dmonische Seher wird nun seine Pflicht fr Euch erfllen.$B$BWollen wir hoffen, dass Ihr fr das Folgende gewappnet sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10875, "deDE", "Es ist schn, von Euren Schachzgen gegen die Hllenorcs zu erfahren, $N. Die Apotheker haben viel Zeit und viel Mhe aufgewendet, um hinter den Grund der Verderbnis zu kommen, und auch wenn diese Informationen sicher unersetzlich sind... schreit mein Orcblut danach, diesen Verrtern einen anstndigen Schlag zu verpassen!$B$BHrt genau zu, wenn ich Euch Eure Aufgabe erklre...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10876, "deDE", "Habt Ihr Euch der Hand von Kargath gestellt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10876, "deDE", "Ihr habt eine groe Tat geleistet, $N. Die Hllenorcs sind eine Schande fr alle wahren Orcs. Wenn wir eine Angst haben, so ist es die, erneut einer dmonischen Verderbnis zu verfallen. Unser Krieg gegen die Hllenorcs ist also mehr als nur eine Schlacht gegen schlimme Feinde, es ist eine Schlacht in uns selbst.$B$BUnd mit einer Strke, wie Ihr sie heute bewiesen habt, werden wir diese Schlacht gewinnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10895, "deDE", "Ich konnte von hier aus den Rauch und die Flammen sehen! Gute Arbeit, $N! Ihr verdient mehr als nur ein paar Lobesworte von unseren Greifenreitern. Was Ihr da getan habt, erfordert eine gehrige Portion Mut.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10903, "deDE", "Ja, es ist wahr. Oberst Jules' Zustand hat sich in den letzten Tagen stark verschlechtert. Anachoret Barada betet schon die ganze Zeit fr ihn... Wir hoffen, dass er geheilt werden kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10909, "deDE", "Die Hllengeister der Orcs der Zerschmetterten Hand sind fr Jules' arme Seele eine Schmach. Sagt mir, $N... habt Ihr ihn gercht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10909, "deDE", "Die Orcs der Zerschmetterten Hand sind verderbt und nicht mehr zu retten. Und dennoch erfllt es mein Herz mit Traurigkeit, dass die Rettung von Oberst Jules ein solches Blutvergieen erfordert.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10916, "deDE", "Habt ihr die Gebetsperlen schon gefunden, $N? Ihre Macht ist fr viele heilige Rituale unerlsslich... darunter auch fr einen Exorzismus...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10916, "deDE", "Ihr habt die Gebetsperlen gefunden! Gut gemacht, $N. Ihr seid in der Tat ein Wunder. Im einen Moment erschlagt Ihr die Schrecken der Hllenfeuerhalbinsel, im anderen findet Ihr verlorene Schtze. Euer Volk ist wirklich sehr vielseitig!$B$BEs ist gut, dass Ihr diese Perlen gefunden habt... Sie sind fr das folgende Exorzismusritual von uerster Wichtigkeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10919, "deDE", "<Fei-Fei bellt glcklich und schnappt sich das Hundeleckerli. Er schaut nach links und rechts, um zu entscheiden, wo er seinen neuen Schatz vergraben soll...>", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10935, "deDE", "Geht zu Anachoret Barada, $N. Er muss mit seinem Ritual Erfolg haben, wenn der Oberst gerettet werden soll.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10935, "deDE", "Ihr habt es geschafft, $N. Ihr habt Oberst Jules' Seele gerettet und die Dunkelheit in ihm vertrieben. Das ist eine groartige und selbstlose Tat! Die Tat eines Helden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10936, "deDE", "Das wird auch langsam Zeit! Ich habe davon erfahren, was Ihr und dieser Draeneipriester angestellt habt! Ihr habt meinen Boten vllig schockiert!$B$BJetzt, da Ihr damit fertig seid, habe ich einen kleinen Auftrag in der Hllenfeuerzitadelle fr Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10937, "deDE", "Ich wollte es nicht glauben, bis ich es nicht von Euch persnlich gehrt habe. Unsere Spher berichten uns jetzt schon, dass die Zerschmetterte Hand ohne ihren geschtzten Drillmeister, der ihnen sagt, was zu tun ist, vllig berfordert ist.$B$BIhr habt einen langen Weg zurckgelegt, $N. Ich vertraue darauf, dass Ihr diese Rstung zusammen mit meinem Dank annehmt. Tragt sie mit Stolz, denn Ihr habt sie verdient!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11515, "deDE", "Diese Monster sind eine Schande fr unser Volk. Wir mssen sie vernichten, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11515, "deDE", "Narren. Sie haben gelernt, wie man Teufelsenergie entzieht, aber nicht, wie man sie behlt.$B$BWir haben Glck, $N. Ein erfahrener Streiter des Teufelsbluts htte gewiss einen beeindruckenden Gegner abgegeben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11516, "deDE", "Ist es vollbracht, $N? Kael'thas darf keine weitere Verstrkung durch die Legion erhalten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11516, "deDE", "Ausgezeichnete Arbeit, $N. Die Dmonen werden den Durchgang womglich rechtzeitig reparieren. Na dann werden wir ihn wohl noch einmal in die Luft jagen mssen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (13408, "deDE", "Gut gemacht, $N. Eure Taten auf dem Schlachtfeld sind fr unseren Kampf auf der Hllenfeuerhalbinsel sehr hilfreich.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (13409, "deDE", "Gute Arbeit, $N. Ich konnte fast die Kampfschreie an den Befestigungsanlagen hren. Ich wnschte, ich wre dabei gewesen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (13410, "deDE", "Gut gemacht, $N. Eure Taten auf dem Schlachtfeld sind fr unseren Kampf auf der Hllenfeuerhalbinsel sehr hilfreich.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (13411, "deDE", "Gute Arbeit, $N. Ich konnte fast die Kampfschreie an den Befestigungsanlagen hren. Ich wnschte, ich wre dabei gewesen!", 26972);
