INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (8322, "deDE", "Nachdem Ihr Euch erfolgreich bis ins Gasthaus durchgeschlagen habt, werft Ihr jetzt die Eier in den groen Kessel. Ihr riecht bereits, wie der faulige Gestank die Luft verpestet...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (8353, "deDE", "Was fr ein Spa! Nicht schlecht, $N! Hier ist Eure Sigkeit.$B$BGruselige Schlotternchte!", 26972);
