INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (8355, "deDE", "Huu-huu! Klasse, $N! Hier ist Eure Sigkeit.$B$BGruselige Schlotternchte!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (8356, "deDE", "Ihr seid wirklich stark, haha! Gut gemacht, danke fr die tolle Schau. Hier ist Eure Sigkeit.$B$BGruselige Schlotternchte, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (8357, "deDE", "Ihr seid ganz schn tanzwtig, $N!$B$BHier ist die Sigkeit fr Eure tolle Leistung. Gruselige Schlotternchte und sagt dem kranken Kindchen alles Gute von mir! Hoffentlich wird es bald wieder gesund.", 26972);
