INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9789, "deDE", "Ein akzeptabler Start. Doch das war erst der Anfang.$B$BDie harmloseren Grollhufe beweisen noch gar nichts. Versuchen wir es doch mal mit einer greren Herausforderung fr Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9792, "deDE", "Ikuti ist wie ein Bruder fr mich. Seine Freunde sind auch meine Freunde. Ihr seid hier willkommen, $N, und Ihr habt unseren Dank fr die Hilfe, die Ihr meinem Volk geleistet habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9797, "deDE", "<Auch wenn er sich Mhe gibt, es zu verheimlichen, macht sich Erleichterung auf Kroghans Gesicht breit.>$B$BEs wurde auch langsam Zeit, dass Ihr kommt! Sicher, Ihr seid keine Armee, $R, aber hier ist ein jeder willkommen, der Kraft genug besitzt, eine Waffe zu fhren oder einen Zauber zu wirken. Die Einwohner Garadars sind ein Teil der Horde und stolz, ihr Blut fr deren Verteidigung vergieen zu drfen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9800, "deDE", "<Elementarist Lo'ap ballt seine Hnde zu Fusten und schttelt den Kopf.>$B$BOgerfuste...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9800, "deDE", "Bitte wascht Euch nicht in den heiligen Wassern des Throns.$B$B<Elementarist Lo'ap reicht Euch eine fertige Caracolitablette.>$B$BLegt sie unter Eure Zunge und lasst sie sich auflsen. Einmal aufgelst werdet Ihr Wasser wie Luft atmen knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9804, "deDE", "<Elementarist Lo'ap kniet im Wasser und betet.>$B$BDer Irdene Ring ist nicht stolz auf das, was geschehen ist. Wir tun das, was wir mssen, um das Land zu heilen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9805, "deDE", "Es beschmt mich, dass mein eigenes Volk die Gewsser in Nagrand beeinflussen mchte, um ein Marschland zu erschaffen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9805, "deDE", "Eure Bemhungen haben die Ausbreitung der Verschmutzung eingedmmt, aber nun sind neue Probleme aufgetaucht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9810, "deDE", "Die Elemente haben Euren Sieg besungen, als Ihr die verschmutzte Essenz gettet habt. Der Irdene Ring dankt Euch und mchte, dass Ihr Euch eine Belohnung aussucht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9815, "deDE", "Lasst keinen verseuchten Schlamm zurck!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9815, "deDE", "Der Irdene Ring dankt Euch, $N. Auch die Elemente sind Euch dankbar. Eure wiederholten Bemhungen, unsere Welt zu reinigen, werden sehr geschtzt und sicher nicht vergessen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9818, "deDE", "<Gordawg scheint Euch zuzunicken.>$B$BSetzt Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9819, "deDE", "<Gordawg nickt zufrieden.>$B$BGut. Geister schlafen jetzt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9821, "deDE", "Gordawg isst den Stein. Gordawg findet den Thronruber. Bringt Stein!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9821, "deDE", "<Gordawg spuckt den gekauten Stein aus, woraufhin ein Steinschauer auf Euch herabrieselt.>$B$BGift. Diese Felsen sind nicht Nagrand. Diese Felsen sind vergiftet. Ihr zerstrt Giftfels. Ihr findet Thronruber.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9849, "deDE", "Gordawg will den Stein zurck.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9849, "deDE", "GUROK! Gurok hat die Elemente verraten!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9850, "deDE", "Beeindruckend, sehr beeindruckend.$B$BIhr habt Potenzial, Jungspund. Ich werde Euch die Mglichkeit geben, richtig viel Spa zu haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9851, "deDE", "Ein warmes Feuer und eine Frau an Eurer Seite knnt Ihr daheim finden. Hier jagen wir ernsthaft oder sterben bei dem Versuch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9851, "deDE", "Ihr habt es tatschlich geschafft! Ihr habt Banthar erlegt! Ich habe niemals an Euch gezweifelt, $N.$B$BHier, ich denke, das habt Ihr Euch redlich verdient.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9852, "deDE", "Es ist keine Schande zuzugeben, dass Ihr nicht den Mumm habt, eines der mchtigsten Tiere die dieses Land zu bieten hat, zu erlegen, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9852, "deDE", "$N! Ihr bringt das Herz dieses alten Zwergs zum Springen.$B$BIhr habt Euch nun bewiesen und seid in die Eliternge aufgestiegen! Andere sind voller Angst geflchtet, sind gestorben oder wurden unter den Fen des Tieres zerquetscht. Ihr habt Euren Verstand, Eure List und Eure Entschlossenheit genutzt, um zu beweisen, dass Ihr die Jagd beherrscht.$B$BNehmt dies und seid gewiss, dass Ihr jederzeit zur Jagd an meiner Seite willkommen seid.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9853, "deDE", "Gordawg isst Gurokpulver.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9853, "deDE", "<Gordawg beit fest in Guroks irdenen Kopf und bringt die Steinformation fast zum Brckeln.>$B$BGuroks Herrschaft ist zu Ende. Die Erdelementare sind frei. Schlafen alle. Gewinner bekommt Preis!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9854, "deDE", "Jeder Tag ist ein Geschenk. Macht das Beste daraus. Denn schon morgen werden wir uns schneller fortbewegen als heute. Wir werden unsere Arme weiter ausbreiten und uns anstrengen, um das zu erzielen, von dem wir heute Nacht noch trumen, auch wenn wir es vielleicht nie erreichen knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9855, "deDE", "Ihr habt Eure Fhigkeiten erneut unter Beweis gestellt, $C. Jetzt msst Ihr Euch mit der wildesten Sorte von Windrocs messen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9856, "deDE", "Habt Ihr Euch Aaskralle schon gestellt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9856, "deDE", "Ihr Auge sieht aus, als wre es noch am Leben. Denkt Ihr, sie kann immer noch in Eure Seele schauen?$B$B$N, Ihr knnt Euch zu den Elitesafarijgern zhlen. Und Ihr seid auf dem besten Weg, die entscheidende Beute dieses Landes zu jagen, Zahna!$B$BIhr habt Euch das hier verdient. Mge es Euch gute Dienste leisten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9857, "deDE", "Das ist schon eine tolle Geschichte...<hust>...wie Ihr all die Hirsche erlegt habt, $C. Aber das knnt Ihr sicher noch besser. Ich habe da etwas anderes fr Euch im Auge.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9858, "deDE", "<hust>, <hust>... $N! Ihr habt es geschafft. Jetzt gibt es... ...nur noch eine Sache......zu tun.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9859, "deDE", "<Harold schaut geschwcht zu Euch hoch und ringt sich ein Lcheln ab.>$B$BHabt Ihr...<hust>... den Huf?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9859, "deDE", "Wow! Ihr habt es geschafft! Das ist tatschlich Bach'lors Huf!$B$Bhm, wie es aussieht fhle ich mich schon viel besser. Hier, lasst mich Euch etwas zur Feier Eures Jgerknnens schenken!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9861, "deDE", "Gebt es her, schnell!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9861, "deDE", "Es ist ein Notruf, $R. Eine Macht versucht die Elemente zu untergraben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9862, "deDE", "Ihr habt gesehen, wie sie die Elemente kontrollieren? Der Wind beugt sich ihrem Willen?$B$B<Morgh versinkt fr einen Moment in Gedanken.>$B$BDas ist sehr beunruhigend, $N. Ich muss mich mit den anderen beraten.$B$BVielen Dank fr Eure Mhen, $N. Vielleicht solltet Ihr in Euer Dorf zurckkehren und Euch umhren, ob es weitere Informationen ber die Finsterblut und ihre schndlichen Ziele gibt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9863, "deDE", "Tut es fr uns, weil wir es nicht fr uns selbst tun knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9863, "deDE", "Ihr habt den Shnen des Blitzschlags neue Hoffnung gegeben, $N. Wenn unser Anfhrer uns schon nicht beschtzt, vielleicht knnen es andere tun - so wie Ihr.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9864, "deDE", "Ich habe Geschichten ber unser Volk gehrt. Sie sagen, dass wir einst Krieger waren...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9865, "deDE", "<Saurfang dreht sich zu Euch um.>$B$BRieche ich da etwa Blut? Ich bedauere es sehr, dass ich Euch nicht helfen konnte. Wie stolz Ihr auf Euch sein msst. Meine Brust hebt sich allein von dem Wissen, was Ihr getan habt.$B$BGibt es noch mehr wie Euch? Gibt es noch mehr Helden dort, wo Ihr herkommt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9866, "deDE", "<Scharfseher Corhuk starrt gedankenverloren auf den Boden. Er nickt zustimmend.>$B$B<Scharfseher Corhuk wischt sich eine Trne aus dem Gesicht.>$B$BDer Sohn ist der Vater... Mgen die Geister gndig zu denen sein, die ihm im Weg stehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9867, "deDE", "Bringt mir den Kopf ihres Anfhrers, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9867, "deDE", "Das ist also das Gesicht unseres Feindes? Ich werde den Kopf aufspieen lassen und auerhalb von Garadar aufstellen. Sollten sie je wagen, uns nochmals anzugreifen, werden sie den Folgen ihres Handelns in die Augen sehen mssen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9868, "deDE", "Was macht das schon? Die Gromutter liegt im Sterben. Das Leben ist nicht lebenswert...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9869, "deDE", "Ah, danke fr diese Nachricht. Httet ihr vielleicht etwas dagegen, dem Irdenen Ring zu helfen? Die Elemente sind sehr aufgewhlt, wir knnen alle Hilfe, die wir bekommen knnen, brauchen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9870, "deDE", "Ah, danke fr diese Nachricht. Httet ihr vielleicht etwas dagegen, dem Irdenen Ring zu helfen? Die Elemente sind sehr aufgewhlt, wir knnen alle Hilfe, die wir bekommen knnen, brauchen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9871, "deDE", "Was gibt's?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9871, "deDE", "<Arechron schlgt mit der einen Hand in die andere.>$B$BOrtor... Dieser verrterische Dreckskerl!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9872, "deDE", "Was gibt es?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9872, "deDE", "<Garrosh zerknllt die Karte und wirft sie ins Feuer.>$B$BWas soll ich damit? Sie greifen uns schon seit Wochen an. Das ist nichts neues.$B$BVersteht Ihr nicht? Wir sind geliefert... Gromutter Geyah liegt im Sterben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9873, "deDE", "Tausend tote Orcs sagt Ihr? Was knnte wohl der Auslser fr solch ein unverfrorenes Vordringen in Orcgebiet sein? Es ist gut, dass er tot ist, aber da steckt mehr dahinter, als ein Tentakel fhlen kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9874, "deDE", "Die Orcs werden nicht verstehen, warum wir das tun, aber es muss getan werden. Fr das Wohl der Orcs sowie der Zerschlagenen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9874, "deDE", "<Otonbu seufzt.>$B$BDas macht uns jegliche Hoffnung auf Frieden mit den Orcs fr weitere zehn Jahre zunichte. Zumindest werden wir diese zehn Jahre noch erleben, was wir vielleicht nicht htten, wenn die Leichen im See verrottet wren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9878, "deDE", "Ich glaube kaum, dass die Orcs verstehen oder zu schtzen wissen, was Ihr da getan habt, aber Eure Tat war gut fr Nagrand. Vielen Dank, Problemlser...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9879, "deDE", "Ja, ich wei von dem Totem... aber es gehrt noch viel mehr zu dieser Geschichte. Ich darf Euch jedoch nichts darber erzhlen. Nur Auserwhlte haben Zugang zu solchem Wissen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9882, "deDE", "Habt Ihr die Kristallfragmente bekommen, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9882, "deDE", "Ich bin beeindruckt. Die meisten sind in ausgezeichnetem Zustand.$B$BIch werde bei unserem Volk ein gutes Wort fr Euch einlegen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9883, "deDE", "Ihr habt noch mehr Kristallfragmente? Nehmt aber bitte keine, die noch mit dem Berg verbunden sind. Wir wollen keinen rger mit den Naaru.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9883, "deDE", "Bringt mir ruhig weiter Kristalle, $N. Wir knnen nicht zulassen, dass diese Diebe unser Eigentum stehlen.$B$BAuch wenn es eigentlich noch gar nicht unseres ist. Theoretisch natrlich.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9884, "deDE", "Das Konsortium ist dafr bekannt, dass es sich gut um seine Freunde kmmert. Zu Beginn jeden Monats werde ich Euch ein paar Edelsteine als Bezahlung fr Eure Dienste geben.$B$BDie Qualitt der Steine hngt davon ab, wie treu ergeben Ihr uns wart. Lasst uns also hoffen, dass Ihr das Richtige tut und bei uns bleibt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9885, "deDE", "Schn Euch wiederzusehen, $N. Ich habe Eure Edelsteine fr diesen Monat fertig.$B$BIch habe Euch ein paar mehr dazugetan, damit Ihr wisst wie froh wir sind, Euch bei uns zu haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9886, "deDE", "Das Konsortium ist dafr bekannt, dass es sich gut um seine Freunde kmmert. Zu Beginn jeden Monats werde ich Euch ein paar Edelsteine als Bezahlung fr Eure Dienste geben.$B$BDie Qualitt der Steine hngt davon ab, wie treu ergeben Ihr uns wart. Lasst uns also hoffen, dass Ihr das Richtige tut und bei uns bleibt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9887, "deDE", "Das Konsortium ist dafr bekannt, dass es sich gut um seine Freunde kmmert. Zu Beginn jeden Monats werde ich Euch ein paar Edelsteine als Bezahlung fr Eure Dienste geben.$B$BDie Qualitt der Steine hngt davon ab, wie treu ergeben Ihr uns wart. Lasst uns also hoffen, dass Ihr das Richtige tut und bei uns bleibt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9888, "deDE", "<Kilrath flstert.>$B$BDiese Oger sind extrem sonderbar. Und auch extrem dumm. Schaut nur, wie der fette den, h, weniger fetten zum Tanzen zwingt. Es ist faszinierend.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9889, "deDE", "Lasst mich leben und ich sage Euch alles, was Ihr wissen wollt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9890, "deDE", "Ganz ruhig, $N. Ich bin nur ein kleiner $C. Ich habe keine Ahnung, was ich mit solch einer Information anfangen soll.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9891, "deDE", "Sie denken also, dass sie uns Orcs nach Lust und Laune vertreiben und abschlachten knnen, was? Ich bin mir sicher, dass das nichts damit zu tun hat, dass unser Anfhrer ein unfhiger Jammerlappen ist... Wenn er doch nur einen Bruchteil der Leidenschaft seines Vaters geerbt htte...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9892, "deDE", "Habt Ihr noch mehr Kriegsperlen? Ihr findet gengend Oger weit im Norden, an der Grenze zu den Zangarmarschen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9892, "deDE", "Wir werden gut an diesen Perlen verdienen, $N. Ich werde Khoraazi ganz sicher von Eurer Arbeit hier erzhlen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9893, "deDE", "Wie fhren die Oger sich auf, $N? Harte Nuss, was?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9893, "deDE", "Ausgezeichnet. Das wird uns ber Wasser halten bis die Sha'tar fertig sind mit was immer sie auch im Oshu'gun tun mgen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9897, "deDE", "Es ist sehr...<hust>...nett von Kristen, dass sie ihre Tierhute teilt, meint Ihr nicht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9897, "deDE", "Nun, ich konnte nicht wissen, ob Ihr Kristen helfen konntet oder nicht. Aber wenn sie Euch eines ihrer Bndel mit Tierhuten gegeben hat, msst Ihr wohl in Ordnung sein.$B$BHier ist Eure Belohnung.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9900, "deDE", "Dank Euch mssen wir uns nun nicht mehr vor Gava'xi frchten. Wollen wir uns nun um eure Belohnung kmmern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9906, "deDE", "Unser Auftrag ist noch nicht zu Ende. Es gibt noch andere, die einer Lektion bedrfen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9907, "deDE", "Wenn dies das Blut in ihren Adern nicht zum Gefrieren bringt, dann wird es nichts schaffen. Ihr habt den Mag'har zu Gerechtigkeit verholfen, $N. Und fr Gerechtigkeit zahlen die Mag'har gut.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9910, "deDE", "Ehrfurcht aus Angst! Das ist so gut wie jeder andere Grund.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9910, "deDE", "Vielleicht wollt Ihr noch eine andere Aufgabe? Eine etwas heiklere?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9913, "deDE", "Seid Ihr bereit fr ein wenig Arbeit, $R? Ich habe die perfekte Aufgabe fr Euch.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9914, "deDE", "Ich brauche Elfenbein, keine Entschuldigungen. Wir knnen natrlich Euren Anteil am Gewinn neu verhandeln. Ich glaube aber kaum, dass Ihr mit dem Ergebnis zufrieden sein werdet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9914, "deDE", "Ich habe keine Sekunde an Euch gezweifelt, mein Freund. Die meisten hiervon sehen gut aus. Ein bisschen dreckig und verkratzt vielleicht. Aber ich denke, sie sind den Preis, den wir vereinbart hatten, wert.$B$BIch hoffe, das wir auch in Zukunft ins Geschft kommen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9915, "deDE", "Ich habe immer noch Bedarf an Elfenbeinstozhnen von den wilden Elekk in Nagrand. Unglcklicherweise kann ich es mir nicht mehr leisten, Euch dafr zu bezahlen.$BWenn Ihr mir aber trotzdem weiter Stozhne bringt, werdet Ihr Euch beim Konsortium sehr beliebt machen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9915, "deDE", "Ausgezeichnet, noch mehr hochwertige Stozhne. Auch wenn ein paar davon etwas schbig aussehen.$B$BIhr habt den Dank des Konsortiums, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9916, "deDE", "Vielleicht knnen unserer Kinder eines Tages ohne Hunger leben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9916, "deDE", "Die meisten unserer Besitztmer wurden bei Angriffen zerstrt oder gestohlen. Ich kann euch nicht viel bieten, aber hier sind ein paar Dinge, die Ihr vielleicht als ntzlich empfinden knntet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9917, "deDE", "Habt Ihr Neuigkeiten erfahren?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9917, "deDE", "<Bintook liest die Plne.>$B$BBEIM LICHT! Diese Handschrift ist grauenvoll! So wie ich das sehe, planen sie \"die blauen Hute zu essen und ihr Dorf einzunehmen\" oder einen Blaubeerkuchen zu backen. Es knnte wirklich beides heien. Wir mssen der Sache auf den Grund gehen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9918, "deDE", "Oh je, das war nur eine kleine Jagdgesellschaft? Ihr msst Mo'mor erzhlen, was Ihr herausgefunden habt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9920, "deDE", "<Mo'mor schaut verdutzt drein.>$B$BEin neuer Ogerstamm drngt die Felsfuste immer weiter nach Sden? Das klingt beunruhigend. Wir mssen mit der Situation in der Nhe unseres Zuhauses fertigwerden...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9921, "deDE", "Ausgezeichnet. Konntet Ihr diesen Lantresor, der in dem Brief dieses Dummkopfs erwhnt wurde, ausfindig machen?$B$BEgal, wir mssen weitermachen. Ich habe Berichte ber weitere Ogeraktivitten erhalten, um die sich sofort jemand kmmern muss!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9922, "deDE", "Ausgezeichnete Arbeit! Ich stelle im Augenblick Nachforschungen ber den Totschlgerstamm an, den der Leutnant der Felsfuste erwhnt hat. Jetzt mssen die Aufgaben, die Ihr erledigt habt, getestet werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9923, "deDE", "Es tut mir wirklich sehr leid, $R. Er gert immer in Schwierigkeiten. Manchmal wnschte ich, ich knnte ihn anleinen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9924, "deDE", "Ich bin Euch so dankbar, $N. Seine Mutter wird sich in Zukunft um ihn kmmern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9925, "deDE", "Ihr zaubert ein Lcheln auf mein Gesicht, $N. Zumindest wrdet Ihr das, wenn ich noch ein Gesicht htte.$B$BWenn Ihr so weiter macht werden wir noch beste Freunde.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9927, "deDE", "<Lantresor schaut Euch entgeistert an.>$B$BSchon fertig?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9927, "deDE", "Knnt Ihr Euch vorstellen, wie verwirrt sie darber sein werden, $R? Das wird wundervoll.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9928, "deDE", "Wir werden einen nie dagewesenen Hass zwischen den beiden Klans sen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9928, "deDE", "Ausgezeichnet! Unser nchstes Ziel sind die Ruinen des Lachenden Schdels.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9931, "deDE", "Knnt Ihr vorhersehen, was als Nchstes geschieht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9931, "deDE", "Der Plan ist perfekt, $N. Ich sehe jetzt schon die Frchte unserer Arbeit.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9932, "deDE", "Wisst Ihr nun, warum Ihr nie einen Krieg gegen mich gewinnen wrdet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9932, "deDE", "Ich muss zugeben, dass ich mich seit Jahrzehnten nicht mehr so lebendig gefhlt habe. Ihr habt gute Arbeit geleistet, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9933, "deDE", "Frieden sagt Ihr?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9933, "deDE", "Bitte, $N, nehmt Euch von diesem Friedensangebot was immer Ihr mchtet. Schlielich wre dies ohne Eure Hilfe niemals mglich gewesen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9934, "deDE", "<Garrosh scheint von dem Angebot unbeeindruckt zu sein.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9934, "deDE", "<Garrosh tritt die Kiste um.>$B$BIhr verschwendet Eure Zeit, $R. Nehmt Euch aus der Kiste was immer Ihr wollt. Weder ich noch Garadar haben fr diesen unntzen Tand Verwendung.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9935, "deDE", "Die Alte ist tot! Das sind wundervolle Nachrichten, $N. Hier ist Eure Belohnung.", 26972);
