INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9937, "deDE", "Durns tdlicher Griff wurde gelst! Nagrand schaut Dank Euch auf eine bessere Zukunft, Held. Gut gemacht!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9938, "deDE", "Durns tdlicher Griff wurde gelst! Nagrand schaut Dank Euch auf eine bessere Zukunft, Held. Gut gemacht!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9939, "deDE", "Dies ist ein groer Sieg fr die Bewohner von Garadar! Gut gemacht, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9940, "deDE", "Dies ist ein groer Sieg fr die Bewohner von Telaar! Gut gemacht, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9944, "deDE", "Dreiig Orcs, $R! Von diesen dreiig sind nur noch Ungriz und ich brig. Wir haben den jungen Orc, Saurfang, halb tot in der Nhe von Sonnenwind gefunden. Offensichtlich ist er aus einem anderen Grund hier...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9945, "deDE", "Das ist ein guter Anfang, aber ihr Anfhrer muss gettet werden, damit Eure Taten auch langfristige Erfolge erzielen knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9946, "deDE", "Er ist sicher sehr schwer bewacht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9946, "deDE", "Diesen Kopf stellen wir an dem Toren von Garadar auf, damit alle ihn sehen knnen. Die Oger werden es sich zweimal berlegen, bevor sie heilige Rituale der Mag'har stren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9948, "deDE", "Ihr habt geschafft, was keiner fr mglich hielt und unsere vermissten Brder und Schwestern zurckgebracht. Fr die Mag'har seid Ihr ein Held, $N. Alle werden Euren Namen kennen und wissen, was Ihr fr uns getan habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9954, "deDE", "Diesmal haben sie mich gut eingesperrt, $N. Der Knig der Totschlger, Cho'war, hat den Schlssel.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9955, "deDE", "Danke, $N. Ich habe Corki zu den Nachtelfen im Schergrat geschickt. Hoffentlich gelingt es ihnen, ihn vom rger fern zu halten.$B$BWegen der Belohnung...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9956, "deDE", "Ich komme mir so dumm vor. Archerons Sohn, Corki, hat mich gefragt, was los ist, und ich habe es ihm gesagt. Jetzt ist er schon wieder verschwunden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9956, "deDE", "Erstaunlich! Wie habt Ihr es geschafft, all das alleine zurckzutragen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9962, "deDE", "Der Kampf ist vorber, wenn Ihr oder Euer Gegner tot zu Boden geht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9962, "deDE", "Hm, nicht schlecht. Gar nicht schlecht... Ihr habt Potenzial, Kleiner. Hier habt Ihr etwas Gold fr Eure Taschen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9967, "deDE", "Ich gebe zu, dass ich nicht geglaubt habe, dass Ihr eine Chance httet, $N! Das war beeindruckend! Wie wr's mit einem weiteren Kampf? Gurgthock hat den perfekten Gegner fr Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9970, "deDE", "Ihr habt die Oger zum Schwitzen gebracht, Kleiner. Diesmal bekommt Ihr noch etwas dazu. Benutzt nicht alles auf einmal!$B$BSeid Ihr bereit fr einen weiteren Kampf? Gurgthock setzt die ganze Bank auf Euch!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9972, "deDE", "KLEINER! KLEINER IHR HABT ES GESCHAFFT! Als nchstes kommt der groe Kampf! Die Meisterschaft!$B$BOk, locker bleiben. Wir wollen ja nicht, dass die Oger erfahren, dass hier was im Busch ist. Schlielich bekomme ich ja auch meinen Anteil daran.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9973, "deDE", "Oh je, Mogor hat sein Kampfrecht als Held der Totschlger eingefordert. Ihr msst gegen ihn kmpfen!$B$BNiemand hat je behauptet, dass die Totschlger gute Verlierer... h... Oger wren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9977, "deDE", "Gurthock hat doch gesagt, dass es sich bezahlt machen wird, Kleiner. Sucht Euch was aus!", 26972);
