INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9991, "deDE", "Ausgezeichnete Arbeit, $N! Jetzt haben wir einen Ausgangspunkt um unseren Gegenangriff zu planen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9999, "deDE", "Wie ich es mir dachte. Es gibt keinen normalen Weg, Teufelsstahl zu zerstren. Es ist aber noch nicht alles verloren...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10001, "deDE", "Bleibt auf jeden Fall von den Vorarbeitern fern. Sie drfen nichts von Eurer Anwesenheit mitbekommen. Die Folgen knnten verheerend sein!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10001, "deDE", "Mein Bekannter kennt hoffentlich einen Weg, die Lager zu zerstren. Er war schlielich mal ein Ingenieur der Legion...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10004, "deDE", "<Sal'salabim kratzt sich am Kopf.>$B$B[Dmonisch] Ik il romath sardon.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10004, "deDE", "Beruhigt Euch, Schtzchen, Sal'salabim wird helfen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10009, "deDE", "Ihr ja nicht kommen zurck ohne Sal'salabims Gold!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10009, "deDE", "Nun, Geschft sein Geschft, aber wenn Sal'salabim Euch sagen, Ihr nicht bse werden, ja?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10010, "deDE", "Natrlich! Wir werden sie mit ihren eigenen Waffen schlagen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10011, "deDE", "Wir haben gesiegt! Das Vorrcken der Legion wurde gebremst!$B$BIch biete Euch eine Auswahl an Belohnungen fr Eure heldenhaften Taten!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10044, "deDE", "Wir mssen zuerst Euren Geist erweitern, damit Ihr mit den Ahnen sprechen knnt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10045, "deDE", "Hallo, Kind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10045, "deDE", "Ihr seid sehr geschickt, $N. Unsere besten Krutersammler htten doppelt so lange gebraucht, um die Kruter zu sammeln.$B$B$B$BHabt keine Angst.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10074, "deDE", "Der Erdkern dieser Gegend ist vom Kristallpulver des Bergs durchdrungen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10074, "deDE", "Wundervoll, $N. Das wird uns in unserem Kampf gegen den Abschaum der Allianz helfen, der sich hier in der Scherbenwelt breitgemacht hat.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10075, "deDE", "Dieser Sttzpunkt war die erste Forschungseinrichtung fr die Untersuchung von Naarukristallen.$B$BWir haben hier eine wahre Goldgrube an Forschungsunterlagen ber die Kristalle des \"diamantenen\" Bergs, Oshu'gun, gefunden. Wie es scheint, ist berall in diesem Gebiet etwas von der brig gebliebenen Macht der Kristalle von Oshu'gun enthalten. Wenn Ihr whrend Euren Abenteuern in diesem Gebiet Kristallpulver finden solltet, bringt es zu mir, und ich werde Euch ein Zeichen meiner Wertschtzung geben, dass Ihr bei den Rstmeistern eintauschen knnt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10075, "deDE", "Ich danke Euch fr Euren Beitrag, <Name>. Wenn Ihr noch mehr finden solltet, wisst Ihr, wo Ihr sie hinbringen knnt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10076, "deDE", "Das Kristallpulver des Bergs ist vom Erdenkern dieser Gegend durchdrungen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10076, "deDE", "Wundervoll, $N. Das wird uns in unserem Kampf gegen den Abschaum der Horde helfen, der sich hier in der Scherbenwelt breitgemacht hat.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10077, "deDE", "Dieser Sttzpunkt war die erste Forschungseinrichtung fr die Untersuchung von Naarukristallen.$B$BWir haben hier eine wahre Goldgrube an Forschungsunterlagen ber die Kristalle des \"diamantenen\" Bergs, Oshu'gun, gefunden. Wie es scheint, ist berall in diesem Gebiet etwas von der brig gebliebenen Macht der Kristalle von Oshu'gun enthalten. Wenn Ihr whrend Euren Abenteuern in diesem Gebiet Kristallpulver finden solltet, bringt es zu mir, und ich werde Euch ein Zeichen meiner Wertschtzung geben, dass Ihr bei den Rstmeistern eintauschen knnt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10077, "deDE", "Ich danke Euch fr Euren Beitrag, <Name>. Wenn Ihr noch mehr finden solltet, wisst Ihr, wo Ihr sie hinbringen knnt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10081, "deDE", "Der Trank, den Ihr zu Euch genommen habt, hat keine echte Wirkung. Ihr knnt einen Geist nur sehen, wenn er es zulsst. Der Trank ist nur ein Symbol Eures Glaubens und Eurer Hingabe. Es war ein Test. Und Ihr habt ihn bestanden...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10082, "deDE", "Es ergibt keinen Sinn. Sie erheben sich weiter. Es muss etwas Heimtckischeres dahinter stecken.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10085, "deDE", "Das ist sehr beunruhigend. Ihr sagt, keiner der Ahnen war in seinem Dorf? Sie gehen alle nach Sden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10101, "deDE", "<Eine sanfte, melodische Stimme erklingt in Eurem Kopf.> Ich bin K'ure von den Naaru, $N. Ihr steht hier im Herzen meines alten Schiffs.$B$BOshu'gun, wie die Orcs es genannt haben, ist das Schiff, mit dem die Draenei zuerst auf diese Welt gekommen sind. Obwohl wir schon vor hunderten von Jahren hier vom Himmel gefallen und abgestrzt sind, sind meine Energien immer noch in diesen Trmmern gefangen. Leider bin ich der Grund fr die Schmerzen der Orcgeister.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10102, "deDE", "Es war weise von K'ure, Euch hierherzuschicken. Wir knnen das Leiden der Orcahnen nicht lindern, doch es gibt einen, der es kann.$B$BEinen, der bald im Licht wiedergeboren wird...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10107, "deDE", "Wie also lautet Eure Entscheidung? Soll es Krieg geben, oder werdet Ihr mich untersttzen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10108, "deDE", "Wie also lautet Eure Entscheidung? Soll es Krieg geben, oder werdet Ihr mich untersttzen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10109, "deDE", "Habt Ihr das Gas?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10109, "deDE", "Ausgezeichnet! Nur ein paar kleine Einstellungen und dieses Ding ist so gut wie neu. Oh richtig, Euer Schlssel! Bitte sehr.$B$BDer Spring-o-Mat hat brigens noch ein paar Macken, die ich noch reparieren muss. h, seid einfach vorsichtig damit.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10111, "deDE", "Habe ich meine Vorliebe fr Eier erwhnt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10111, "deDE", "Nun, es ist kein Ei... Aber Hhnchen mag ich auch! Ich beie mich einfach durch diese riesige Klaue durch! Danke!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10113, "deDE", "Ich schtze, wir mssen einen Ersatz fr den Wassertrger finden. Nun, Ihr seht aus wie ein krftiger $C. Interesse an einem Job? Oder kommt Ihr wegen der Jagd?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10114, "deDE", "Ich bin erleichtert zu hren, dass er in Sicherheit ist. Seid Ihr hier, um an der Jagd teilzunehmen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10168, "deDE", "<Mutter Kashur ist sichtlich aufgebracht.>$B$BDiese Naaru sind weiser als alle Lebewesen in dieser Welt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10168, "deDE", "Ihr habt eine lange und gefhrliche Reise hinter Euch und Leben und Gesundheit fr die Sicherheit der Mag'har und den Frieden unserer Ahnen aufs Spiel gesetzt. Ich bin Euch dafr dankbarer, als tausend Worte sagen knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10170, "deDE", "Ich mchte, dass Ihr diese Nachrichten persnlich zu Garrosh bringt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10171, "deDE", "Alle sind stolz. Stolz, dass unser Volk einen weiteren Winter berleben wird. Aber danach? Was ist danach?$B$BVielleicht solltet Ihr diesen Klan anfhren, $N. Vielleicht ist es mir dann vergnnt zu sterben, wenn die Gromutter dahinscheidet. Vergnnt, die Schande meines Familiennamens auszulschen. Ich sehne mich nach diesem Frieden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10172, "deDE", "<Trnen strmen ber Gromutter Geyahs Gesicht.>$B$BThrall ist auf dem Weg hierher. Ich wei, dass er bald hier sein wird.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10476, "deDE", "Nun, $N? Knnt Ihr nur Reden schwingen, oder habt Ihr mir etwas vorzuweisen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10476, "deDE", "Ihr beweist Euch allmhlich als wertvoller wertvoller Verbndeter und Kmpfer. Macht weiter so!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10477, "deDE", "Habt Ihr noch mehr Obsidiankriegsperlen dabei, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10477, "deDE", "Ihr beweist Euch weiterhin als wertvoll, $N. Mge Eure Strke nie versagen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10478, "deDE", "Habt Ihr noch mehr Obsidiankriegsperlen besorgt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10478, "deDE", "Ihr beweist uns auch weiterhin Eure Strke und Ehre, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10479, "deDE", "Habt Ihr etwas, das Ihr mir zeigen mchtet, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10479, "deDE", "Ihr seid wirklich ein starker Verbndeter, $N. Den Ogern der Scherbenwelt Auge in Auge gegenberzutreten ist nichts, was jeder einfach so tun kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10640, "deDE", "Ihr habt einen langen Weg hinter Euch, Fremder. Ich bin berrascht, dass derjenige, der Euch zu mir geschickt hat, von mir gehrt hat.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10641, "deDE", "Ihr habt beweisen, dass Ihr nicht von der Berhrung der Legion verderbt wurde. Der Feind meines Feindes ist mein Freund.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10646, "deDE", "Jetzt kennt Ihr die Geschichte. Bleibt nur noch eine Sache zu erledigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10668, "deDE", "Ihr gehrt also nicht zu Illidan. Ihr seid einen Schritt nher daran, mein volles Vertrauen zu erlangen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10669, "deDE", "Ihr seid zurck. Ist Xeleth tot?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10669, "deDE", "Gut gemacht, $N. Er konnte sich also immer noch an den Tag erinnern, an dem ich den Speer in seinen Krper rammte? Das ist schon so lange her...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10689, "deDE", "Ihr habt einen langen Weg hinter Euch, Fremder. Ich bin berrascht, dass derjenige, der Euch zu mir geschickt hat, von mir gehrt hat.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11042, "deDE", "<Der Raketenchef hrt Euren Ausfhrungen ber die Vision des Sehers aufmerksam zu.>$B$BNun, das ist alles vllig neu fr mich, aber Eure Hilfe knnen wir allemal gebrauchen. Seht Euch das Ungetm hinter mir an. Das ist die X-52...$B$BIst sie nicht das Schnste, was Ihr je gesehen habt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11044, "deDE", "Eine Vision sagt Ihr? Es interessiert mich nicht, ob er es in einer Vision gesehen, in einem Brief gelesen oder es aus dem Mund eines Grubenlords vernommen hat, aber er hat den Nagel auf den Kopf getroffen! Was fhrt Euch hierher, Freund? Oh, und wir sind fr jede Untersttzung dankbar!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (9794, "deDE", "<Kialon schaut Euch misstrauisch an.>$B$BIch kenne Euch nicht, $R. Was wollt Ihr von mir?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9794, "deDE", "Ihr bringt mir also Timothys Antwort?$B$B<Kialon hlt einen Moment inne, um den Brief zu lesen, und fngt an zu lachen.>$B$BEr hat alles unter Kontrolle, sagt er, braucht keine Hilfe. Ich htte es wissen mssen!$B$BManche Leute ndern sich nie. Schaut, er hat sich sogar die Zeit genommen, meine Rechtschreibfehler zu korrigieren.$B$B<Kialon seufzt halbherzig.>$B$BIch htte ihm den Brief nicht schicken sollen. Ich habe es vergessen. Der Spezialist hat die Situation immer unter Kontrolle. Immer.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (9795, "deDE", "Denjai braucht Hilfe? Ich wrde ihm gerne ein paar Leute schicken, aber wir knnen uns selbst kaum halten. Wir haben hier oben unsere eigenen Probleme mit den Ogern.$B$BSobald ich kann, werde ich ihm alle Informationen, die wir ber diesen Ogerstamm und seine Taktiken haben, zukommen lassen. Wenn er lang genug durchhlt, knnen wir die widerlichen Kreaturen vielleicht von zwei Seiten aus angreifen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10455, "deDE", "Das ging schnell. Seid Ihr sicher, dass Ihr so viele gettet habt, wie es ntig war?$B$BGut, dann knnt Ihr uns bei einer weiteren Angelegenheit helfen, wenn Ihr mchtet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10456, "deDE", "Ich kann nicht deutlich genug sagen, dass diese Terrorwlfe der Donnerfrsten erledigt werden mssen. Ihre bloe Anwesenheit strt das Gleichgewicht des Lebenden Hains und all unsere Arbeit hier.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10456, "deDE", "Gut. Ich werde schauen, ob ich aus diesen Schwnzen etwas Ntzliches machen kann.$B$BDie Horde wird ohne Zweifel einen Weg finden, Ihr Wolfsrudel wieder zu vergrern, aber nun knnen wir etwas freier durchatmen.$B$B$N, wenn Ihr dazu bereit seid, habe ich eine letzte Bitte an Euch, um Sylvanaar und den Lebenden Hain zu verteidigen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10457, "deDE", "Wie geht es mit der Verstrkung der Verteidigung des Lebenden Hains voran?$B$BSolange wir nicht wissen, was die Arakkoa im Lashhversteck vorhaben und die Horde sich auf der anderen Seite der Klamm befindet, habe ich Angst, dass wir unser neues Heim und unsere Arbeit nicht mehr verteidigen knnen, wenn wir nicht bald handeln.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10457, "deDE", "Nun, da Ihr die Verteidigung des Hains verstrkt habt, fhle ich mich viel sicherer.$B$BIhr habt Euch mehr als nur unseren Dank und unsere Wertschtzung verdient.$B$BMir fllt gerade ein, dass es da noch etwas gibt, bei dem ich Eure Hilfe brauchen knnte.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10486, "deDE", "Das ging schnell. Seid Ihr sicher, dass Ihr so viele gettet habt, wie ntig war?$B$BGut. Es gibt noch eine weitere Aufgabe, bei der wir Eure Hilfe bentigen. Eine weitaus wichtigere.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10487, "deDE", "Es sieht aus, als htte die Allianz vor, uns berallhin zu folgen. Wir wollen ihnen beweisen, dass ihre Anwesenheit uns keineswegs einschchtert.$B$BHabt Ihr besorgt, worum ich Euch gebeten habe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10487, "deDE", "Ihr habt nicht nur ihre Verteidigung geschwcht. Mit dem Staub werden wir dazu noch unsere eigene Verteidigung gegen die Allianz strken knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10488, "deDE", "Wie geht es mit der Verstrkung unserer Terrorwolfverteidigung voran?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10488, "deDE", "Nun, da Ihr die Wlfe und unsere Verteidigung gestrkt habt, knnen wir uns wieder einigen tdlicheren Angelegenheiten, wie zum Beispiel den Ogern, zuwenden.$B$BIhr habt uns hier gut geholfen. Wenn Ihr Euch entscheidet zu bleiben, knnten wir auch weiterhin die Dienste von jemandem, der so fhig ist wie Ihr, brauchen.$B$BWie auch immer Ihr Euch entscheiden mgt, Ihr habt meinen Respekt und meine Wertschtzung sicher.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10489, "deDE", "Was habt Ihr da? Ihr seid aber stark!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10489, "deDE", "Starkast Tiefwurz' Stumpf! Habt Ihr ihn selbst gefllt?$B$BSicher habt Ihr das. Wisst Ihr, ein starker $C wie Ihr es seid, ist genau die Hilfe, nach der Rexxar und ich gesucht haben. Besonders bei den stndigen Problemen, die wir mit den Ogern haben, knnten wir Euch brauchen. Setzt Euch mit mir in Verbindung, wenn Ihr Interesse habt.$B$BNun, dieser Stumpf ist ein stichhaltiger Beweis. Ihr seid hier, um das Kopfgeld einzustreichen, nicht um mit mir zu plaudern. Wohlan, sucht Euch etwas aus.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10502, "deDE", "Das ist ein guter Anfang, um Sylvanaar und den Hain zu schtzen.$B$BJetzt, da die Oger nicht mehr ganz so zahlreich sind, berlegen die Blutschlger es sich sicher zweimal, bevor sie uns hier angreifen.$B$BWenn das doch nur der einzige Ogerstamm wre, mit dem wir Probleme htten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10503, "deDE", "Das ist ein guter Anfang, um unsere Position hier gegen die Oger zu verteidigen.$B$BJetzt, da die Oger nicht mehr ganz so zahlreich sind, berlegen die Speerspieer es sich sicher zweimal, bevor sie versuchen, die Donnerfeste zurck zu erobern.$B$BWenn das doch nur der einzige Ogerstamm wre, mit dem wir Probleme htten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10504, "deDE", "Eure Taten gegen die Blutschlger und Speerspieer sind ein groer Schritt nach vorne fr die Sicherung der Zukunft Sylvanaars. Als wir hier ankamen, fanden wir diesen Flecken Land unbewohnt. Wir haben kein Interesse daran, uns noch weiter auszubreiten, aber wir werden auch nicht dulden, dass man uns vertreibt.$B$BDa Ihr uns gute Dienste geleistet habt, mchte ich Euch mit der Wertschtzung der Allianz belohnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10505, "deDE", "Eure Taten gegen die Blutschlger und Speerspieer sind ein groer Schritt nach vorne fr die Sicherung der Zukunft der Donnerfeste. Wir werden nicht dulden, dass sich jemand unserem Schicksal in den Weg stellt.$B$BDa Ihr uns gute Dienste geleistet habt, mchte ich Euch mit der Wertschtzung der Horde belohnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10506, "deDE", "Hattet Ihr Glck da drauen? Ich wei, dass es wegen der Oger der Blutschlger sehr gefhrlich ist, aber wenn wir es vermeiden knnen, die Tiere zu tten, bin ich sehr froh.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10506, "deDE", "Ich bin froh zu hren, dass es geklappt hat. Je weniger Wesen der Natur sterben mssen, desto besser.$B$BIch muss sagen, dass ich Euch nach all dem, was Ihr fr uns getan habt, ins Herz geschlossen habe.$B$BGebt auf Euch Acht, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10510, "deDE", "Ist das nicht aufregend? Nach dem ganzen Dreck und den Insekten in Silithus ist dieser Ort hier ein wahres Paradies!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10510, "deDE", "Ooh, so wie sie aussehen, handelt es sich hierbei um ganz tolle Exemplare! Ich kann es kaum abwarten, sie mit meinen Untersuchungsgerten genau unter die Lupe zu nehmen!$B$BVielen Dank!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10511, "deDE", "Ziemlich trocken hier. Wie soll ich denn meinen Urlaub ohne etwas zu Trinken genieen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10511, "deDE", "Was riecht hier so? Oh nein, bitte sagt mir nicht, dass dies das Ogergebru ist?$B$BIch habe fast schon Angst, es zu probieren. Ach zur Hlle, was soll's? Hoch die Tassen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10512, "deDE", "Wie sieht es aus? Sind die Speerspieer so anspruchslos wie die Blutschlger oder sind sie auch fast dabei umgekommen wie ich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10512, "deDE", "Ah, hahaha! Das habe ich mir gedacht. Oger trinken wahrscheinlich alles.$B$BWas fr ein dummer Haufen!$B$BAuch wenn ich mein Gebru nicht bekommen habe, habt Ihr mir doch einen guten Start in den Urlaub bereitet.$B$BIch sage Euch was... wir haben hier allen mglichen Kram, den wir nicht wirklich brauchen. Kann ich Euch etwas davon anbieten?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10516, "deDE", "$C, habt Ihr mein Schwert und meinen Schild?$B$BIch kann zwar jederzeit hier raus, aber es wre mir sehr peinlich, wenn ich ohne meine Ausrstung nach Sylvanaar zurckkehren msste.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10516, "deDE", "Ich stehe tief in Eurer Schuld, denn Ihr habt mir meine Ehre wiedergegeben. Aber unsere Arbeit hier ist noch nicht vorber.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10517, "deDE", "Sehr gut. Ihr seid ein sehr gewitzter $C, so viel ist sicher.$B$BIch frage mich, ob Ihr wohl noch eine Sache fr mich und Sylvanaar tun knntet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10518, "deDE", "Was fhrt Euch zu mir? Es gibt doch nicht etwa rger mit den Blutschlgern?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10518, "deDE", "Gurn Grubnoshs Helm und ein Banner der Speerspieer?$B$BEin ziemlich gewitzter Plan von der Verteidigerin und Euch.$B$BWenn die Blutschlger weiter mit den Speerspieern beschftigt sind, nimmt uns das viel von dem Druck, unter dem wir hier in letzter Zeit leiden.$B$BBitte, $N, nehmt dieses bescheidene Geschenk von Sylvanaar an.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10524, "deDE", "Kenne ich Euch?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10524, "deDE", "Ich kann meinen Augen nicht trauen! Wenn das ein Scherz sein soll, ziehe ich Euch die Haut vom Leibe, $C!$B$BSchnell, lasst mich diese unschtzbaren Artefakte untersuchen, bevor Ihr sie noch mit Euren zwei linken Hnden zerbrecht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10525, "deDE", "Was habt Ihr gesehen? Sagt es mir! Ich muss wissen, wo es ist!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10525, "deDE", "In der Draenethystmine? Die Blutschlger mssen es von dem Speerspieern gestohlen haben.$B$BPah! Das ist zu weit weg... zu tief im Blutschlgergebiet, als dass ich dorthin reisen knnte.$B$BAber Ihr! Ihr kennt den Weg. Ihr sollt an meiner statt dorthin gehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10526, "deDE", "Ihr knnt sicher nicht verstehen, wie wichtig diese Artefakte sind.$B$BWenn ich alle fnf beisammen habe, werde ich ein Ritual durchfhren, das unser Besitzrecht an der Donnerfeste durch die Besnftigung der toten Geister festigen wird.$B$BVersteht Ihr nun, was daran so wichtig ist? Versteht Ihr nun, warum ich keine Zeit fr Geplnkel habe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10526, "deDE", "Ich bin beeindruckt. Knnt Ihr Euch berhaupt vorstellen, wie viele zuvor bei dem Versuch, die Relikte zu bergen, gestorben sind?$B$BWir knnen uns glcklich schtzen, dass Ihr hier mit uns zusammenarbeitet, $N. Auch wenn Ihr etwas langsam seid.$B$BErlaubt mir, Eure Aufopferung fr unsere Sache zu belohnen. Whlt, aber whlt weise. Wenn Ihr fertig seid, werde ich die Geister der Donnerfeste besnftigen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10542, "deDE", "He, habt Ihr meine Wasserpfeife und das Gebru schon?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10542, "deDE", "Ja, genau davon hat T'chali geredet! Ist sich nicht ganz sicher, was die Oger da reingetan ham. Die Pfeife riecht nicht nach T'chalis Tabak.$B$BEgal, T'chali ist glcklich. Sogar so glcklich, dass er Euch um einen weiteren Gefallen bitten will.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10543, "deDE", "T'chali ist zufrieden, dass Korgaah und Grimnok Schlachttaufe jetzt auch die Radieschen von unten anschauen.$B$BAber T'chalis Rachedurst ist noch nicht gestillt. Ihr msst noch etwas anders machen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10544, "deDE", "T'chali will nicht mehr sprechen, bis $N den Fetisch benutzt hat, um die Huser der Speerspieer und der Blutschlger zu verfluchen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10544, "deDE", "Hahaha, Rache ist sss!$B$BAber T'chali sieht, dass Ihr nun, da meine Rache verbt wurde, gerne weiterziehen wollt. Ich versteh' schon, Kumpel... lassen wir das Leben den Lebenden.$B$BIch sag' Euch was, T'chali hat hier immer noch ein paar Sahnestckchen in seinem Grab. Sucht Euch aus, was Euch am besten gefllt!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10545, "deDE", "Sagt es T'chali... hat das Gebru funktioniert? Haben die Speerspieer es gemocht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10545, "deDE", "Das sind gute Nachrichten! Das Gebru wird bei der nchsten Aufgabe, die T'chali fr Euch hat, ntzlich sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10555, "deDE", "Die groben Piktogramme in dem uralten Folianten weisen darauf hin, dass eine groe Menge von Federn der Lashh'an bentigt wird, um die Sprche in dem Buch zu wirken.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10555, "deDE", "Ihr blttert die Seiten voll gekritzelter Notizen in der dnnen Schrift der Arakkoa der Lashh'an durch und findet ein Piktogramm, das die Kreise der Macht, die Ihr im Lashhversteck gesehen habt, darstellt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10556, "deDE", "Das ist so aufregend! Ich kann es kaum abwarten, die Wirkung des Zaubers zu untersuchen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10556, "deDE", "Ich wei nicht, was ich sagen soll. Dieser Zauber ist sehr merkwrdig. Es handelt sich nicht um eine einfache Beschwrung oder Verzauberung, sondern eher um eine Art Vereinigungs- oder Kommunikationszauber.$B$BIch wei nicht, was ich davon halten soll, $N. Ich muss noch ein wenig darber nachdenken.$B$BAber trotzdem vielen Dank fr Eure Hilfe! Ich bin sicher, dass ich frher oder spter dahinter komme.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10557, "deDE", "Wow, Ihr habt es geschafft! Und kein einziger blauer Fleck oder offener Knochenbruch! Seht Ihr, ich habe Euch doch gesagt, dass alles funktionieren wird!$B$BHier ist Eure Bezahlung, $N. Ich habe die Arztkosten gleich dazu gerechnet, aber da Ihr sie nicht gebraucht habt, seht es einfach als kleinen Bonus an!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10565, "deDE", "Die Arakkoa der Vekh'nir waren viele Jahre lang gtige Wesen., doch nun hat sie etwas aufgehetzt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10565, "deDE", "Ah, schaut nur, wie er leuchtet! Ich muss ihn sofort ausprobieren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10566, "deDE", "Diese Informationen werden uns sehr viel weiterhelfen, $N. Ich werde den Zauberstab noch verbessern und schauen, ob wir ihn auch fr weitere Zwecke einsetzen knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10567, "deDE", "Sobald der Herold bemerkt, dass Ihr seine Brut angreift, wird er sicher schnell reagieren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10567, "deDE", "Ihr habt ihn!$B$BMit diesem Anhnger werde ich dafr sorgen knnen, dass Ihr die Sprache der Arakkoa aus Grishnath versteht.$B$BHiermit werden wir die Arakkoa und das Wesen des Raben besser verstehen lernen. Ich brauche dazu Eure Hilfe.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10580, "deDE", "SYLVANAAR? KOMMANDANT HIMMELSSCHATTEN?$B$BKEIN DATENBANKEINTRAG!$B$BTRETET ZURCK, BEVOR ICH MICH GEZWUNGEN SEHE, EUCH MIT LAUTEN GERUSCHEN ZU QULEN!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10581, "deDE", "In Ordnung, wollt Ihr Euch ntzlich machen? Gut, ich habe genau die richtige Aufgabe, mit der Ihr Euch beweisen knnt.$B$BIch glaube, Ihr werdet sie mgen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10584, "deDE", "Diese Forschungsreihe mit den Elektromentaren ist faszinierend! Stellt Euch nur vor... Eine intelligente Kreatur, die wir nach Belieben erschaffen knnen und die all unseren Anweisungen folgt!$B$BWas kann da schon schiefgehen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10584, "deDE", "Ausgezeichnet! Mit der ganzen Energie, die Ihr gesammelt habt, werden wir die Maschinerien fr eine Weile am Laufen halten knnen.$B$BDas Zephyriumkapazitorium ist ein echter Schluckspecht, wenn es um den Energieverbrauch geht!$B$BIch hoffe, mit den Elektromentaren fertigzuwerden war nicht allzu schwierig. Ich habe noch eine andere Aufgabe fr Euch. Ich frchte, sie wird etwas schwerer werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10594, "deDE", "Nun, habt Ihr die Messungen der singenden Kristalle, um die ich Euch gebeten habe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10594, "deDE", "Oh, das ist auergewhnlich! Ich hatte ja keine Ahnung... Es ist fast so, als wren die Kristalle lebendig!$B$BIch frage mich, ob eine Art symbiotische Beziehung zwischen ihnen und den Netherdrachen besteht.$B$BVielen Dank, vielen Dank! Ihr msst etwas aus meiner Kuriosittensammlung nehmen, das ich nichtmal im Tode anziehen wrde!$B$BAn Euch knnte etwas davon jedoch gut aussehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10607, "deDE", "Das ist wirklich beunruhigend. Die Arakkoa waren lange ein friedliebendes Volk. Dieser Rabenkult ist nichts als eine Versammlung von Auenseitern und Verrckten.$B$BAber wir knnen nicht leugnen, dass ihre Macht wchst. Vielleicht steckt hinter diesem Raben mehr, als wir vermuten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10608, "deDE", "Ein Glck, dass ich sie los bin! Verdammte Biester!$B$BWenn Ihr sicher seid, dass Ihr diese Nervensgen ausgelscht habt, habe ich ein kleines Forschungsprojekt fr Euch. Ihr werdet es sicher mgen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10609, "deDE", "Habt Ihr sie? Habt Ihr die Drachenessenzen, die ich so dringend brauche, um meine Experimente weiter zu fhren?$B$BWusstet Ihr, dass diese Nethergrodracheneier einmal Schwarzdracheneier waren? Es ist wahr! Aber als Draenor in tausend Stcke gesprengt wurde, waren die Eier dem Wirbelnden Nether ausgesetzt und sind mutiert.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10609, "deDE", "Ihr habt ja keine Ahnung, wie wichtig diese Experimente sein werden. Vielen Dank!$B$BEs wird aufregend werden, sich mit diesen Essenzen zu befassen. Ich bin besonders gespannt, ob der Nether, der die schwarzen Dracheneier zu Netherdracheneiern verwandelt hat vielleicht ein besonderer Segen fr meine Studien ist.$B$BDa fllt mir gerade ein, dass Ihr die erste Person sein solltet, die die Vorzge meines kleinen Gebrus genieen sollte. Hier, nehmt diese Proben und benutzt sie, wenn Ihr sie braucht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10614, "deDE", "Was fhrt Euch in unser Dorf?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10615, "deDE", "Ah, ja, Dertrok. Wir haben kurz ber die Arakkoa gesprochen. Ich muss allerdings zugeben, dass unsere... Interessen was sie betrifft deutlich auseinander gehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10617, "deDE", "Fr die Ernte braucht man eine schnelle und ruhige Hand. Ich hoffe, Ihr habt Erfolg.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10617, "deDE", "Diese Kokons sind von guter Qualitt. Wie ich sehe, habt Ihr die Ernte im Griff.$B$BWir haben versucht, unsere eigenen Seidenflgellarven zu zchten, aber leider produzieren die kleineren nicht genug Seide, um unseren Bedarf zu decken.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10618, "deDE", "Die Flgel sind sehr zerbrechlich, knnen aber in vielen unserer Kunstgegenstnde als Dekoration dienen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10618, "deDE", "Vielen Dank, $N. Wir werden sie schon frher benutzen, als Ihr Euch vorstellen knnt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10620, "deDE", "Ziemlich gut fr einen Grnschnabel. Es scheint, als gbe es eine kleine Gruppe von Eierkpfen, die unbedingt die Astralen und ihre Technologie im Norden 'beobachten' mchten. Sie werden sich riesig freuen, dass Ihr ihnen den Weg freigerumt habt.$B$BHier, nehmt ein paar hiervon. Wir haben nicht viele, aber vielleicht knnen sie Euch in der Zukunft ntzlich sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10632, "deDE", "Wie geht es mit dem Sammeln der Zhne voran?$B$BIch bin davon berzeugt, dass Ihr Eure Arbeit fr uns gut machen werdet. Und whrend Ihr das tut, werden wir diesen Auenposten hier sichern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10632, "deDE", "Gute Arbeit, Junge. Der Schmerz ist gerechtfertigt, wenn auch nur einem meiner Soldaten durch Eure Bemhungen das Leben gerettet wird.$B$BIch bin der Meinung, dass auch Ihr von Eurem Blut und Euren Trnen profitieren solltet. Wir werden bald neue Waffen bekommen. Warum nehmt Ihr daher nicht einfach eine von meinen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10657, "deDE", "Der Trick dabei ist, die repolarisierte Magnetsphre rechtzeitig abzuschalten, bevor Ihr gegrillt werdet.$B$BNatrlich msst Ihr Euch dabei gut berlegen, wie vielen Schlangen Ihr gleichzeitig gegenbersteht.$B$BWo wir gerade davon sprechen, habt Ihr die Sphre schon aufgeladen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10657, "deDE", "Jippieh, es hat geklappt! Ich war mir da ehrlich gesagt nicht so sicher... Ich habe eine Wahrscheinlichkeit von 23,72 Prozent berechnet, dass Ihr beim dritten Treffer implodiert.$B$B<Toshley grinst unschuldig.>$B$BJetzt wissen wir also, dass die Sphre funktioniert. Es sollte also keine weiteren Probleme geben, den ntigen Saft fr unser Zephyriumkapazitorium zu beschaffen. Natrlich nur, wenn ich noch mehr Freiwillige finde, die fr mich dort hinaus gehen und sich brutzeln lassen.$B$BAber Ihr habt den Pionier fr unsere Bemhungen gespielt und verdient eine Belohnung!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10671, "deDE", "Habt Ihr mein Geschnetzeltes schon? Wie, das findet Ihr nicht lustig?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10671, "deDE", "Das sollte gengen. Es stinkt frchterlich, oder?$B$BIch hoffe, dass es Eure Mhen wert war... Ich bin nur froh, dass ich das Zeug nicht selber sammeln musste!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10674, "deDE", "Ich nehme an, dass Ihr die Lichtkugeln bei Razaans Landung schon besorgt habt, wenn Ihr Zeit habt, hier mit mir zu plaudern. Ich wrde sie wirklich gerne untersuchen und herausfinden, was die Astralen damit vorhaben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10674, "deDE", "He! Wartet mal!!$B$BDas sieht nicht gut aus...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10675, "deDE", "Habt Ihr diesen Dreckskerl Razaan schon erledigt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10675, "deDE", "Das geschieht ihm ganz Recht! Vielen Dank, dass Ihr dieser makaberen Mischung aus Magie und Technologie ein Ende bereitet habt.$B$BAls wahrer Held der Gnome berall auf dieser Welt, lasse ich Euch nun die hchste unserer Ehren zukommen... ein paar Stromwandler und Eure Wahl aus diesen Dingen hier!$B$BIch wei, dass Ihr sie gut einsetzen werdet!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10682, "deDE", "WAS?! Sie haben Euch fortgeschickt und unser Angebot ausgeschlagen?$B$BDas ist emprend! Wir sind in Frieden zu ihnen gekommen und sie schicken Euch weg, ohne auch nur zu versuchen, einen Kompromiss zu finden?$B$BNun, das wird Folgen haben!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10690, "deDE", "Unglaublich. Habt Ihr Rema ganz alleine gettet?$B$BSo wie Ihr ausseht, glaube ich das. Wisst Ihr, wir sind auf der Suche nach einem starken $C wie Ihr es seid, um uns hier ein wenig zu helfen. Besonders bei den stndigen Problemen, die wir mit den Ogern haben, knnten wir Euch brauchen. Setzt Euch mit mir in Verbindung, wenn Ihr Interesse habt.$B$BDas Kopfgeld, das auf dem Anschlag versprochen wurde, habt Ihr Euch jedenfalls verdient.$B$BBitte, whlt Euch etwas aus!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10709, "deDE", "Mein Vater ist also tatschlich am Leben. Ich habe nicht erwartet, dass er mir verzeiht. Das war noch nie seine Art. Aber ich hatte gehofft, dass seine Wut ber meine Abreise ber die Jahre schwcher geworden ist.$B$BVor dreiig Jahren habe ich die Mok'Nathal verlassen, um der Horde nach Azeroth zu folgen. Leoroxx war strikt dagegen, gab mir aber auch keinen Grund zu bleiben. Und doch htte ich mir ein herzlicheres Willkommen gewnscht.$B$BEr wollte einen Mok'Nathal als Sohn haben und hat immer noch einen, auch wenn er nichts davon wissen mchte.$B$BIch werde ihm die Augen ffnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10710, "deDE", "Wow! Ihr habt schon wieder berlebt! Ich bin ein besserer Ingenieur, als ich immer dachte!$B$BHier ist Eure Bezahlung! Und schaut doch spter nochmal bei mir vorbei... Ich habe noch mehr Experimente, fr die ich Versuchspersonen brauche!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10711, "deDE", "Oh, beeindruckend! So schnell schon zurck! Ich habe gesehen, wie Ihr abgehoben habt und wie eine Rakete in die Luft geschossen seid! Ich dachte, es wrde mindestens einen Monat Genesung brauchen, bis Eure gebrochenen Beine wieder geheilt sind! Ihr seid hart im Nehmen, mutig und khn! Aber vor allem... mutig!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10712, "deDE", "Hallo hallo! Mein Klo... h... mein Vetter Tally hat mir gesagt, dass Ihr vorbeikommen wrdet. Habt Ihr etwas fr mich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10712, "deDE", "Oh, Ihr habt Aufzeichnungen mit der Netherwetterfahne gemacht? Ausgezeichnet! Die Daten dieses Instruments werden zusammen mit meinen Messungen druidischer Magie sicher viele Frchte tragen!$B$BOh, die Einsatzmglichkeiten werden Euch um den Verstand bringen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10713, "deDE", "Danke, $N.$B$BWir werden uns darum kmmern, dass die Wildtiere sich selbst wieder schnell vermehren. Bei den Bumen ist dies jedoch ein sehr viel zeitaufwndigerer Prozess.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10714, "deDE", "Was habt Ihr erfahren, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10714, "deDE", "<Geisterschwinge lsst sich auf Rexxars Schulter nieder, whrend Rexxar aufmerksam zuhrt.>$B$BDie Shne des Gruul stecken also hinter den Handlungen der Oger. Sie befehlen den Ogern der Blutschlgern, die Mok'Nathal zu vernichten, um ihre Gunst zu erlangen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10715, "deDE", "Habt Ihr die Giftdrsen? Wenn nicht, dann hinfort mit Euch und wagt nicht, meine Zeit zu verschwenden, bevor Ihr sie habt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10715, "deDE", "Sehr gut, Ihr habt eine einfache, wenn nicht gar banale Aufgabe erfllt. Nun gebt mir die Drsen, damit ich den schwierigen Teil erledigen kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10717, "deDE", "Wie geht es mit der Beschaffung der Netze voran? Meiner Einschtzung nach sind diese Netze der Wilderer stark genug, um unserer Sache dienlich zu sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10717, "deDE", "Oh, die sehen hervorragend aus. Diese Kultisten wissen wirklich, was wahre Handwerkskunst ist.$B$BZu schade, dass sie sie missbrauchen.$B$BJetzt, da wir die Netze haben, bruchte ich Eure Hilfe bei ein oder zwei Angelegenheiten. Habt Ihr Lust?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10718, "deDE", "Wovon redet Ihr?$B$BGarm Wolfsbruder? Er war der letzte Huptling der Donnerfrsten. Wenn Ihr mit seinem Geist gesprochen habt, muss es wahr sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10719, "deDE", "Was habt Ihr da? Ist da ein Blutfleck auf der Einladung?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10719, "deDE", "Das ist interessant, aber auch beunruhigend. Vielleicht sollten wir Samia fragen, wer dieser Kolphis Dunkelschuppe ist?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10720, "deDE", "Ist es vollbracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10720, "deDE", "Viele Jahre lang habe ich versucht, die Kommunikation mit der Natur zu perfektionieren. Dies ist nur ein Bruchteil dessen, was die Tiere fr Euch tun knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10721, "deDE", "So schnell schon zurck? Seid Ihr hier, um Rexxars Schulden zurckzuzahlen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10721, "deDE", "<Baron Zobelmhne lugt in den Sack des Gronns und scheint zufrieden.>$B$BSehr gut, Ihr habt Euren Teil der Abmachung gehalten. Nun, da der Preis bezahlt ist, werde ich Rexxar geben, was er begehrt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10722, "deDE", "Uns angreifen? Uns auslschen? Das glaube ich aber nicht!$B$BDieser Pechschwingenkoven muss noch viel lernen, wenn sie denken, dass man die Expedition des Cenarius so leicht loswerden kann!$B$BWie sagt man so schn? Wie du mir, so ich dir!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10723, "deDE", "Die Oger der Blutschlger werden uns nie mehr belstigen, wenn Ihr Eure Aufgabe erfllt habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10723, "deDE", "Die Oger der Blutschlger und ihr Meister, Gorgrom, werden die Mok'Nathal nie wieder belstigen.$B$BIch mache mir jedoch Sorgen ber die Auswirkungen unserer Tat auf die anderen Gronn. Ich glaube, wir mssen diesem Kampf ein fr alle Mal ein Ende setzen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10724, "deDE", "Leokk hat uns den Kopf eines schwarzen Drachen von Drachenend gebracht, damit knnen wir Goc, den Gronn, der ihn gefangen genommen hat, erzrnen und ihn in die Schlacht locken.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10742, "deDE", "Nur wenn Goc und Gorgrom der Drachenfresser tot sind, werden die Mok'Nathal frei sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10742, "deDE", "Der Kampf war hart und Ihr habt Euch gut geschlagen, $N. Ich bin stolz, dass ich in der Schlacht gegen Goc an Eurer Seite kmpfen durfte.$B$BDie Mok'Nathal sollten von diesen groartigen Siegen erfahren, aber ich bin noch nicht bereit, ihnen gegenberzutreten. Ich kenne meinen Vater und ich kenne mein Herz. Es gibt immer noch vieles, was ich lernen und tun kann, um strker zu werden und ihm zu beweisen, dass ich sein wahrer Sohn bin.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10747, "deDE", "Hattet Ihr viel rger beim Versuch, die Welpen zu fangen? Ich hoffe, Ihr habt Euch nicht verbrannt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10747, "deDE", "Ach herrje, das sind ja mehr, als ich gehofft hatte! Fr Euren Heldenmut habt Ihr gewiss ein Lob verdient!$B$BDiese Welpenbrut sieht sehr gut aus. Sie sind wie gemacht fr meine Untersuchungen.$B$BBitte, nehmt eine kleine Belohnung von mir an. Ich bestehe darauf!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10748, "deDE", "Jetzt, da der Anfhrer unserer Feinde tot ist, knnen wir endlich wieder ein wenig aufatmen. Und Ihr habt auch noch das gesamte Gebirge vor ihrer Verwstung bewahrt.$B$BWas auch immer ihre endgltigen Plne gewesen sein mgen, Ihr habt sie vereitelt!$B$BIhr habt unseren Dank, $N. Bitte nehmt dieses Zeichen unserer Wertschtzung sowie unsere ewig whrende Gastfreundschaft.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10749, "deDE", "$C, so schnell schon zurck? Habt Ihr das Gift?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10749, "deDE", "Die Verwendung von Giften ist ein dreckiges Geschft. Aber so werden wir unsere Ziele erreichen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10753, "deDE", "Es erfllt mich nicht mit Stolz zu zerstren, was Elune erschaffen hat, aber die Verderbnis muss aufgehalten werden. Ihr habt Eure Arbeit gut gemacht, $C.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10770, "deDE", "Es gibt so viele, dass wir sie nie alle tten knnen, aber Ihr habt mir Hoffnung gegeben. Vielleicht sollte ich noch ein paar Jahrhunderte lang strker versuchen, meinen geliebten Wald wieder zum Leben zu erwecken.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10771, "deDE", "Ist es vollbracht? Habt Ihr etwas Leben in mein altes Zuhause zurckgebracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10771, "deDE", "Ihr habt viele dieser verderbten Kreaturen gettet. Mit dieser Gewissheit kann ich nun etwas ruhiger schlafen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10783, "deDE", "Hallo, $R. Ich bin Baron Zobelmhne. Ein Freund Rexxars ist auch mein Freund.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10784, "deDE", "<Tor'chunk nickt und scheint dabei sichtlich zufrieden mit Eurer mrderischen Khnheit.>$B$BIhr erstaunt mich immer wieder, $N. Jedes Mal, wenn einer dieser stinkenden Oger zu Boden geht, sind wir einen Schritt nher daran, einen unwrdigen Feind loszuwerden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10785, "deDE", "Habt Ihr bekommen, was auch immer in diesem Sack des Gronns war? Habt Ihr es Zobelmhne gebracht? Hat er Euch etwas fr mich mitgegeben?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10785, "deDE", "Hm, eine Falle, die aus der Essenz einer Drachenflamme gebaut wurde? Ich frage mich, wie Zobelmhne an eine so seltene Substanz gekommen ist!$B$BNun, ganz egal. Nicht egal ist allerdings, dass wir sie benutzen werden, um einen Sohn des Gruul zu tten!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10786, "deDE", "<Tor'chunk grinst, als Ihr ihm von Euren Abenteuern in Fels'mok erzhlt.>$B$BAch, ich wnschte, ich htte mit Euch kommen knnen. Ich will Euch etwas sagen, $N, lasst Euch nie auf Pflichten ein, die Euch vom Ruhm der Schlacht fernhalten!$B$BHa! Habt Ihr mir berhaupt noch ein paar Oger in den Bergen brig gelassen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10795, "deDE", "Jetzt, da Dorgok tot ist, sind die Oger der Blutschlger ohne Anfhrer und hoffentlich verwirrt, bis ein neuer Anfhrer aus ihren Rngen gewhlt wird.$B$BVielen Dank, $C. Ich glaube, wir knnen nun ein wenig aufatmen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10796, "deDE", "<Der Leutnant nickt und scheint mit Eurer mrderischen Khnheit sichtlich zufrieden.>$B$BIch bin beeindruckt, $N. Euer Ruf scheint wohlverdient zu sein.$B$BJedes Mal, wenn einer dieser Oger zu Boden geht, sind wir einen Schritt nher daran, einen Feind loszuwerden, der uns niemals in Frieden lassen wird.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10797, "deDE", "Hallo, Junge! Was gibt es Neues?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10797, "deDE", "Oh, das ist aber hbsch. Sieht ein bisschen aus wie diese Gronn, die die Oger der Speerspieer immer durch die Gegend scheuchen.$B$BKomisch, dass Ihr es in der Hand eines Anfhrers der Blutschlger gefunden habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10798, "deDE", "Ah, ein Gesandter der gerhmten Allianz. Sagt mir, Lakai, warum seid Ihr es, der hierhergekommen ist und mich um dieses Ding bittet, und nicht Eurer illustrer Kommandant?$B$BKann es etwa sein, dass er Angst vor mir hat? Irgendetwas an Euch sagt mir, dass Ihr keine habt, aber ich wei noch nicht, was das ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10799, "deDE", "Habt Ihr die Giftdrsen? Wenn nicht, dann hinfort mit Euch und wagt nicht, meine Zeit zu verschwenden, bevor Ihr sie habt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10799, "deDE", "Sehr gut, Ihr habt eine einfache, wenn nicht gar banale Aufgabe erfllt. Nun gebt mir die Drsen, damit ich den schwierigen Teil erledigen kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10800, "deDE", "Ihr steht mir wieder gegenber, $C. Das muss bedeuten, dass Ihr den Sack unter Grullocs Nase weggeschnappt habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10800, "deDE", "<Baron Zobelmhne lugt in den Sack des Gronns und scheint zufrieden.>$B$BSehr gut, Ihr habt Euren Teil der Abmachung gehalten. Nun werde ich Euren Kommandanten mit der Falle beschenken.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10801, "deDE", "Hat der Baron uns eine Falle gebaut? Eine, die stark genug ist, um einen Gronn zu tten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10801, "deDE", "Hm, eine Falle, die aus der Essenz einer Drachenflamme gebaut wurde? Ich frage mich, wie Zobelmhne an eine so seltene Substanz gekommen ist!$B$BNun, ganz egal. Nicht egal ist allerdings, dass wir sie benutzen werden, um einen Sohn des Gruul zu tten!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10802, "deDE", "Wurde die Nachricht schon berbracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10802, "deDE", "Gut gemacht, $N. Sehr gut gemacht!$B$BIch nehme an, dass wir nun nicht mehr von den Ogern und ihrem Herrn hren werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10803, "deDE", "<Leutnant Schnwetter grinst, als Ihr ihr von Euren Abenteuern in Fels'mok erzhlt.>$B$BAch, ich wnschte, ich htte mit Euch kommen knnen. Ich will Euch etwas sagen, $N, lasst Euch nie auf Pflichten ein, die Euch vom Ruhm der Schlacht fernhalten!$B$BHa! Habt Ihr mir berhaupt noch ein paar Oger in den Bergen brig gelassen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10805, "deDE", "Sehr gut. Ich habe eine weise Wahl getroffen, als ich Euch zur Speerspitze meines Kampfes gemacht habe. Ich schtze, es ist an der Zeit, die Dinge auf eine ganz neue Ebene zu tragen.$B$BErinnert Ihr Euch an den kleinen Leckerbissen, den Ihr Grolloc fr mich unter der Nase weggeschnappt habt? Ihr werdet die Gelegenheit bekommen, ihn sinnvoll einzusetzen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10806, "deDE", "Bald werden alle Shne des Gruul vernichtet sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10806, "deDE", "Fr jemanden von Eurem Schlag habt Ihr erstaunlich gut gekmpft. Es war mir eine Ehre, Euch an meiner Seite zu wissen. Meine Verwandlung muss Euch wohl ein wenig berrascht haben. Ich hoffe, ich habe Euch nicht allzu sehr erschreckt.$B$BWir sollten meine wahre Identitt jedoch fr uns behalten. Als Belohnung fr Eure treuen Dienste biete ich Euch eines dieser Schmuckstcke an.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10810, "deDE", "Geht mir nicht auf die Nerven! Knnt Ihr nicht sehen, dass ich dabei bin, mysterise Krfte zu entfesseln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10810, "deDE", "Ah, was habt Ihr denn da? Lasst mich das mal genauer ansehen, $C. Ja, ja... es ist genau das, was ich dachte. Lasst mich die Maske fr Euch reparieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10812, "deDE", "Ich traue meinen Augen nicht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10812, "deDE", "Diese Maske, die Ihr da in den Hnden haltet, ist eine Gasmaske der Teufelsbrut! Und wie es aussieht, ist sie nicht beschdigt! Der rger, den wir damit im Konstruktionslager: Groll anrichten knnen, ist grenzenlos! Habt Ihr Lust, uns dabei zu helfen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10818, "deDE", "Ah, wie ich sehe habt Ihr beschlossen, Euch ganz schn Zeit zu lassen, bis Ihr meinem Aufruf Folge geleistet habt.$B$BWie auch immer, ich hatte genug Zeit, um an meinen Racheplnen zu feilen, und Ihr sollt in der kommenden Schlacht mein Vollstrecker sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10819, "deDE", "Was soll das bedeuten? Niemand ruft mich! Sprecht, oder ich werde Euch fr immer zum Schweigen bringen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10820, "deDE", "Das wird ihnen sicher zeigen, dass sie besser tun sollten, was ich ihnen sage...$B$Bbrigens... wo ist Schicksalsrufer? Warum habt Ihr mir von dieser Revolte berichtet und nicht sie?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10821, "deDE", "Das sind wirklich groartige Nachrichten, $N. Ich htte es mir nicht besser wnschen knnen. Eure Dienste gegenber meinem Volk sind sehr geschtzt. Bitte whlt Euch einen dieser Gegenstnde aus.$B$BEuer Name wird fr immer in den Herzen unseres Volkes klingen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10825, "deDE", "$C, knnt Ihr das spren? Ich fhle, dass etwas Unnatrliches und Bses in der Nhe ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10825, "deDE", "Ja, es ist die Kugel, die Ihr da habt! Merkwrdig, sie scheint von den Arakkoa zu stammen. Wart Ihr krzlich in Grishnath?$B$BAh, das erklrt das Ganze. Aber ich frage mich, welchem teuflischen Zweck dieses Objekt dient. Obwohl das Bse eindeutig darin schlummert, scheint sie doch mit der Natur verbunden zu sein.$B$BBitte erlaubt mir zu versuchen, sie zu aktivieren. Das Bse darin ist so stark, es erinnert mich an etwas...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10829, "deDE", "Gre, kleiner $R. Die Vgel in meinen Zweigen haben mir zugeflstert, dass Ihr kommen wrdet.$B$BIhr seid hier, um die Bume des Waldes von ihrer Besessenheit zu heilen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10830, "deDE", "Das Bse verschwindet aus dem Rabenwald! Knnt Ihr es fhlen, $N?$B$BIhr habt uns und alle Wildtiere hier vor der unweigerlichen Zerstrung gerettet, kleines Wesen!$B$BFr solche Gelegenheiten trage ich vier magische Ringe in meinen Zweigen und verschenke sie, wenn ein Held sich erhebt, um eine groe Tat zu vollbringen.$B$BIhr seid solch ein Held! Im Namen des gesamten Rabenwaldes danke ich Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10843, "deDE", "<Leoroxx mustert Euch aufmerksam.>$B$BGnosh Brognats Tod ist ein Segen fr alle Mok'Nathal. Das ist gut fr Euch.$B$BMal schauen, ob Ihr immer noch so heldenhaft seid, wenn wir die Messlatte etwas anheben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10845, "deDE", "Nein! Ihr habt Unheilschwinge gettet? Vielleicht habe ich mich in Euch getuscht, $C.$B$BVielleicht.$B$BDennoch habt Ihr die Anfhrer der drei grten Bedrohungen fr unser Volk erschlagen. Darauf knnt Ihr stolz sein.$B$BIch werde Euch ein Zeichen meiner Wertschtzung geben, aber lasst es Euch nicht zu Kopf steigen. Ihr habt immer noch keine Ahnung davon, was es bedeutet, ein Mok'Nathal zu sein. Vielleicht werden wir immer noch auf Eure Fhigkeiten zurckkommen mssen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10846, "deDE", "Die Vekh'nir sind noch die harmlosesten unserer Feinde.$B$BIhr habt also einen bergroen Vogel gettet. Das macht Euch noch lange nicht zu einem wahren Krieger. Das lsst Euch auch nicht unser Wesen verstehen.$B$BAber es ist ein Anfang.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10851, "deDE", "Wart Ihr im Auenposten der Speerspieer und habt ihnen die Totems abgenommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10851, "deDE", "Das sieht nach einer guten Sammlung aus. Wir sollten einen Weg finden knnen, sie respektvoll einzusetzen.$B$BIn der Tat habe ich sogar schon eine Idee!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10853, "deDE", "Die Drachen des Singenden Bergrckens sind vielleicht eine Bedrohung fr uns, doch sie sind majesttische Wesen.$B$BOder sie waren es zumindest. Ich hoffe, dass Ihr ihre Geister retten knnt.$B$BUnd falls Ihr noch mehr Totems brauchen solltet, habe ich noch ein paar ber.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10853, "deDE", "Ihr habt heute eine groe Tat vollbracht, $C. Die Geister der Drachen werden nun wieder singen knnen, denn sie sind frei. Und die berreste ihrer Geister, die sie hinterlassen, dienen als mchtiges Heilmittel.$B$BHabt Ihr vielleicht Lust auf eine Reise in den Norden? Es gibt da eine Sache im Zusammenhang mit den Astralen, die mich brennend interessiert.$B$BIch fhle groen Schmerz aus dieser Richtung kommen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10859, "deDE", "Habt Ihr alle Lichtkugeln der Razaani, die ich brauche, gesammelt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10859, "deDE", "Etwas fhlt sich hier falsch an.$B$BBitte, $N, reicht mir das Totem. Ich muss diese Kugeln untersuchen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10860, "deDE", "Habt Ihr alle Zutaten schon? Darber zu reden hat mir das Wasser im Munde zusammenlaufen lassen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10860, "deDE", "Mmm, ich kann sie schon schmecken! Schnell, reicht mir die Zutaten... Ich werde sie rasch klein wrfeln und dann ab damit aufs Feuer!$B$BZu Eurem Glck hlt sich das Zeug lange, und ich meine damit sehr lange! So, nehmt dies. Und da Ihr so freundlich wart, meine Speisekammer aufzufllen, gebe ich Euch die Rezepte gleich noch dazu.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10865, "deDE", "Ist das, was Ihr da sagt, berhaupt mglich? Was sage ich da? Natrlich ist es das, wenn der Geistrufer davon berzeugt ist.$B$BErinnert Ihr Euch noch, als ich sagte, dass wir vielleicht wieder Eure Hilfe brauchen wrden? Wie es scheint, kann ich in die Zukunft sehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10867, "deDE", "Habt Ihr es getan? Sind die Seelen unserer Vorfahren sicher vom Bsen der Astralen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10867, "deDE", "Es ist gut, dass der Abschaum der Astralen und ihr Anfhrer tot sind. Ich wnsche ihren Brdern tausend Plagen auf den Hals!$B$BIhr sollt fr Eure Taten im Namen aller Mok'Nathal belohnt werden, $N! Ihr seid wirklich ein Held fr uns!$B$BLasst uns nun die Seelen unserer Vorfahren dem reinigenden Feuer bergeben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10893, "deDE", "Nun, da Draaca unschdlich gemacht wurde, sollten wir in der Lage sein, unseren Vorteil aus der Situation zu ziehen. Sie werden sich jetzt untereinander darum streiten, wer die Leitung bernehmen soll, und das wird uns viel Druck nehmen.$B$BEs sei denn der Aufseher aus dieser Einladung, die Ihr mir gegeben habt, nimmt ihren Platz ein.$B$BWir sollten sicherstellen, dass das niemals passieren wird!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10894, "deDE", "Wie kann ich Euch helfen? Bringt Ihr Nachrichten aus dem Ewigen Hain?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10894, "deDE", "Es ist genau so, wie ich es befrchtet habe, und das ist nicht gut!$B$BIch habe schon eine Idee, wie ich Euch helfen kann, aber vielleicht knnt zunchst Ihr uns helfen?$B$BEine Hand wscht die andere.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10904, "deDE", "Habt Ihr die Kanonenkugeln schon?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10904, "deDE", "Diese Dinger stinken ja schlimmer, als ich dachte!$B$BSchnell, gebt sie mir, damit ich die mit der Macht der Natur erfllen kann. Dann werden sie zu tdlichen Waffen gegen die teuflischen Meister und ihre unnatrlichen Schpfungen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10910, "deDE", "Ich bin sehr erleichtert, dass Wildfrst Antelarion Euch zu uns geschickt hat. Das Problem ist noch viel schlimmer, als wir befrchtet haben!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10911, "deDE", "Habt Ihr beide Warpportale schon zerstrt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10911, "deDE", "Euer Heldenmut ist beeindruckend!$B$BSehr gut. Nun, da die Warpportale auer Gefecht sind, bleibt nur noch eines zu tun.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10912, "deDE", "Ist es vorbei? Ist die Gefahr an der Schwelle des Todes abgewendet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10912, "deDE", "Ihr seid wirklich ein ganz erstaunlicher $R! Wenn ich schon zuvor dachte, dass wir in Eurer Schuld stehen, so gibt es nun einfach keinen Weg mehr, Euch je fr Eure Taten zu danken.$B$BAber wir wollen es dennoch versuchen. Vielen Dank, Held des Ewigen Hains!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10927, "deDE", "Gute Arbeit, $C. Dieser Tunnel ist unsere direkte Verbindung zum Sumpf.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10928, "deDE", "Gute Arbeit, $C. Dieser Tunnel ist unsere direkte Verbindung zum Sumpf.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10974, "deDE", "Ihr habt es geschafft! Hattet Ihr Glck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10974, "deDE", "<Das Bild von Kommandant Ameer kramt in den Beweisstcken.>$B$BEs ist schlimmer, als ich dachte... Diese Kammern enthalten wilde Bestien und Kreaturen von vielen verschiedenen Welten. Das Astraleum hofft, eines Tages Kontrolle ber diese Bestien zu gewinnen und sie ihrem Willen zu unterwerfen. Wir mssen sie alle vernichten!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10975, "deDE", "Es gibt immer noch mehr Arbeit, die erledigt werden muss, $N. Begebt Euch nun auf die Suche nach einer Stasiskammer und vernichtet, was auch immer darin lauern mag!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10976, "deDE", "Dies wird Eure furchteinflendste Aufgabe sein, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10976, "deDE", "Endlich!$B$B<Kommandant Ameer liest die Inschrift auf dem Abzeichen des Nexusknigs.>$B$BZwei Gefngnisse? Schon der Gedanke an die Schrecken, die darin hausen mgen, lsst mich erzittern...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10982, "deDE", "Ihr habt viel fr das Protektorat und das Konsortium getan, $N. Daher ist es nur gerecht, dass einer unserer neuen Schlssel an Euch geht. Mit diesem Schlssel knnt Ihr Shaffars persnliche Stasiskammer jederzeit ffnen, ohne den Schlssel dabei zu zerstren.$B$BEr ist von Dauer und wird auf ewig Euch gehren. ", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10983, "deDE", "Grok sagt, ich bin weise? Ich wei ja nicht so recht.$B$BVielleicht liegt das daran, dass ich der Einzige meines Klans bin, der aufrichtig versucht uns vor der Vernichtung durch den Speerspieerklan zu retten. Und natrlich vor Gruul und seinen Shnen, die sie untersttzen.$B$BIch hoffe, dass Ihr gekommen seid, um uns aus der unaufhaltsamen Verdammnis zu erretten, $C.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10989, "deDE", "Endlich hat Chort mir jemanden geschickt, der in der Lage sein knnte, mir zu helfen. Ich bin bemht, die Blutschlger vor der Auslschung durch die Speerspieer zu bewahren. Und natrlich durch Gruul und seine Shne, die sie untersttzen.$B$BIch vertraue fest darauf, dass Ihr unserer Geheimorganisation helfen und uns in die Freiheit fhren knnt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10995, "deDE", "Ist Grulloc schon Geschichte?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10995, "deDE", "Das muss ein harter Kampf gewesen sein, $C. Ich bin sehr erleichtert, dass er uns nun nicht mehr auf die Pelle rcken wird.$B$BIch werde diesen Schdel zu Chort schicken, damit er auf ihn aufpasst. Ich glaube, dass wir unser Ziel mit eurer Hilfe erreichen knnen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10996, "deDE", "Habt Ihr die Truhe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10996, "deDE", "Sie war leer? Was ein Pech! Aber nun habt Ihr eine Ahnung von der sinnlosen Gewalt, unter der wir leiden mssen.$B$BJetzt, da Maggoc tot ist, sind wir meinem Traum von einem Zuhause, in dem wir Oger unser Schicksal selbst schmieden knnen, einen entscheidenden Schritt nher gekommen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10997, "deDE", "Haltet Ihr Slaags Standarte schon in Euren Hnden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10997, "deDE", "Ich bin erleichtert, dass Slaag nicht mehr unter uns weilt. Nun mssen wir nicht lnger in Angst vor seiner drohenden Rckkehr leben!$B$BWenn Ihr nichts dagegen habt, wrde ich die Standarte gerne als Symbol der Hoffnung fr alle Oger dieser Berge entgegennehmen.$B$BVielen Dank, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10998, "deDE", "Habt Ihr Vim'gol den blen beschworen und seinen toten Klauen den Zauberfolianten entrissen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10998, "deDE", "Ich scheue mich davor, ein Ding von solcher Abscheulichkeit auch nur anzufassen. Und doch mssen wir den Folianten lesen, wenn wir sein Wissen erkunden und Skulloc aus seinem Versteck locken mchten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11000, "deDE", "Habt Ihr Skullocs Seele schon geraubt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11000, "deDE", "Es ist also vorber. Ihr habt uns von den Fesseln der Sklaverei durch die Shne von Gruul befreit. Ihr habt den Klan der Blutschlger vor der sicheren Auslschung bewahrt.$B$BEs gibt keine Worte, um zu beschreiben, wie tief ALLE Oger des Schergrats, Blutschlger wie Speerspieer, in Eurer Schuld stehen, $N.$B$BSchaut dort, wie all die Oger sich versammeln, um Euch Tribut zu zollen! Sollen Gruul und seine Marionette, der alte Knig Maulgar, sich ruhig in ihrem Unterschlupf verkriechen. Geht zu den Ogern als ihr neuer Knig!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11009, "deDE", "Mog'dorg hat eine Nachricht voraus geschickt, um mich ber Euer Kommen zu informieren. Ich bin froh, dass Ihr hier seid.$B$BNach dem Fall der Shne des Gruul, ist es nur noch eine Frage der Zeit - es drfte nicht sehr lange dauern - bevor die unterschiedlichen Bewohner dieser Bergterrasse und die des Nordens beginnen, Jagd auf unsere Ogerbrder dort unten zu machen.$B$BDies gilt es zu verhindern und ich vertraue auf Eure Untersttzung, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11010, "deDE", "Habt Ihr schon ihre Stapel mit Teufelskanonenkugeln vernichtet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11010, "deDE", "Gut, gut, anscheinend seid Ihr doch hrter im Nehmen, als ich dachte. Ausgezeichnete Arbeit. Mglicherweise hatte der alte Keller doch Unrecht, was Euch anbelangt.$B$B<Himmelsoffizierin Vanderlip grinst.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11022, "deDE", "Mein werter Herr, es freut mich, dass Ihr vor Eurer Abreise noch einmal mit mir zu sprechen gedenkt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11023, "deDE", "Wie steht es mit dem Bombenangriff?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11023, "deDE", "Hervorragend, $N! Ich wrde Euch ja sofort wieder in die Luft schicken und ein paar Bomben werfen lassen, aber unsere Richtlinien hier schreiben vor, dass unsere Piloten ausreichend Ruhe und Erholung erhalten mssen. Es tut mir leid, ich mache die Regeln nicht, aber lasst Euch versichern, dass ich sie durchsetzen werde!$B$BSehen wir uns morgen wieder, Sportskanone?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11025, "deDE", "Wenn Ihr mehr ber die Splitter und Kristalle lernt, werdet Ihr uns und unsere Ogerbrder dort unten besser beschtzen knnen.$B$BDie Splitter selbst wurden nach einer uralten und mittlerweile ausgelschten Arakkoazivilisation benannt, die einst auf den Spitzen dieser Berge lebte.$B$BNun, habt Ihr die Splitter bei Euch?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11025, "deDE", "<Chu'a'lors linker Kopf beginnt zu sprechen.>$B$BDiese sind genau richtig.$B$BDenkt daran, $N, der Wert dieser Splitter liegt nicht in ihrem Verkaufspreis. Es ist nicht einmal das, was sie fr Euch tun knnen, sobald sie ordnungsgem zusammengeschmiedet wurden... auch wenn sich darber streiten lsst.$B$B$B$BDer wahre Wert liegt in dem Unterfangen, das es auf sich zu nehmen gilt, um in den Besitz dieser Splitter zu gelangen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11026, "deDE", "Das wird ihnen eine Lehre gewesen sein! Kommen wir nun zu Eurer Belohnung.$B$BDie Dmonen des Konstruktionslagers: Rache im Norden haben an ihrer sdlichen Grenze einen besonderen Transporter errichtet. Sie verwenden dieses experimentelle Transporttor, um den Einfluss des Lagers weiter auszudehnen. Wir brauchen Euch wohl nicht erst erklren, was passieren wird, wenn sie sich zu weit ausbreiten.$B$BAlso, wenn wir Ihr wren, wrden wir diese Dunkelrune zu Gahk schaffen und ihn Fragen, wie Ihr helfen knnt. Geht es langsam mit ihm an, $N, er ist ebenfalls neu hier.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11027, "deDE", "Die Dmonen von dem Camp da machen die Transporter immer wieder ganz. Gahk sagt $N muss die Dmonen besser plattmachen diesmal!$B$BAber Gahk braucht noch 'ne Dunkelrune um zu machen eine kristallgeschmiedete Rune.$B$BWenn klein $R nicht haben Dunkelrune, Du sprechen mit Kronk! Er versuchen Dunkelrunen zu machen jeden Tag! Vielleicht Du bekommen eins aus Kronks Grabbelsack?!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11027, "deDE", "Haha, gut! Gahk mgen Dunkelrunen! Ihr nehmen kristallgeschmiedete Dunkelrune, die Gahk krzlich gemacht haben.$B$BNicht vergessen, Tokru, erst beherrschen Zerrtter und dann zerschlagen magisches Schild von Shartuuls Transporter. Andere Dmon wird frei sein und dann Ihr auch ihn bekmpfen und beherrschen! Dann Ihr mssen mehr Dmonen bekmpfen und kontrollieren, bis keine mehr kommen!$B$BDann Ihr Ogri'la erretten!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11030, "deDE", "Habt Ihr das magische Flschchen schon bekommen? Es wrde Torkus Junior alles bedeuten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11030, "deDE", "Ihr seid ein wahrer Lebensretter, $N!$B$BUnsere Jungs werden von dieser Neuigkeit begeistert sein!$B$BOkay, eines geschafft und eines fehlt noch. Wir haben Euch doch bestimmt von dem anderen Flschchen erzhlt, das sie bentigen, nicht wahr?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11036, "deDE", "Ist das die Lieferung vom alten Orok?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11036, "deDE", "<Der Raketenchef berprft den Inhalt der Kiste.>$B$BSieht soweit gut aus. Wieder etwas, das ich von der Liste streichen kann. Nun mssen wir noch nur dafr sorgen, dass die Rakete vom Boden abhebt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11047, "deDE", "<Dama hrt sich Agadais Bitte an.>$B$BIch kann nicht dafr garantieren, dass wir ber gengend Windreiter verfgen, aber ich werde sehen, was ich tun kann. In der momentanen Situation haben wir Schwierigkeiten, die Nachfrage der verschiedenen Kufer und Auenposten abzudecken.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11051, "deDE", "Jemand mit Eurer Erfahrung drfte keine Probleme damit haben, ein paar Dmonen zu bannen. Ihr habt doch keine Schwierigkeiten damit, oder, $R?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11051, "deDE", "Wir wnschten uns, der Herstellungsprozess fr Runen der Dunkelheit wre narrensicher, $N. Unglcklicherweise ist er das nicht. Nehmt bitte diesen Sack und seht mal, was wir heute hergestellt haben. Wir haben es nicht bers Herz gebracht, selbst nachzusehen.$B$BWenn Ihr eine Rune der Dunkelheit erhaltet, vergesst nicht, Gahk aufzusuchen und sie bei ihm gegen eine kristallgeschmiedete Rune der Dunkelheit einzutauschen. Mit ihr knntet Ihr Shartuuls Transporter erneut ausschalten.$B$BOh, und $N, denkt daran, morgen wiederzukommen und den Bannkristall abzuholen. Es sind genug Dmonen da, die gebannt werden mchten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11057, "deDE", "Man hat Euch von hoch oben zu mir geschickt? Wahrlich, meine Mission ist gesegnet! Ich versuche die Blutschlger vor der Ausrottung durch den Speerspieerklan und Gruuls Shne zu bewahren.$B$BIch nehme an, dass Ihr unsere geheime Organisation untersttzen und uns die Freiheit bringen werdet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11058, "deDE", "<Chu'a'lors Kpfe nicken Euch anerkennend zu.>$B$BIhr habt einen scharfen Verstand, $N. Wir haben erheblich lnger gebraucht, um das Relikt erfolgreich zu benutzen.$B$BNun, da Ihr wisst wie, knnt Ihr es so oft Ihr wollt verwenden. Denkt jedoch daran, dass fr die Aktivierung eines Relikts immer ein Apexissplitter erforderlich ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11059, "deDE", "Wir verabscheuen Gewalt, aber wenigstens ist das Konstrukt, das Ihr vernichten sollt, kein lebendiges Wesen.$B$BWo wir schon davon sprechen, habt Ihr den Kopf?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11059, "deDE", "<Chu'a'lor mustert Euch voller Respekt.>$B$BEure und die geistige Gewandtheit Eurer Freunde ist wahrlich beeindruckend. Dafr, dass Ihr uns und unsere Lebensweise verteidigt habt, stehen wir, nein, stehen ganz Ogri'la und die Himmelswache in Eurer Schuld.$B$BDenkt daran $N, Ihr knnt jederzeit mit gesammelten Apexissplittern zurckkehren, um die groen Apexismonumente zu benutzen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11060, "deDE", "Kleiner $R, nicht vergessen, wenn Ihr einen Zerrtter kontrollieren, Ihr mssen seinen Hammer dazu benutzen, um die Schilde von Warptor zu zerschlagen und den gefangenen Dmonen freizulassen.$B$BDann Ihr mssen zerschlagen diesen Dmon und ihn beherrschen. Dann kommen neuer Dmon, den Ihr auch beherrschen mssen, bis der Aufseher der Eredar auftauchen.$B$BDann Ihr auch ihn erschlagen werdet!!!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11061, "deDE", "Dieses Flschchen des Zauberers wird meine Jungs sicherlich noch intelligenter machen, als sie es ohnehin schon sind. Damit sollten ihnen die tglichen Pflichten in der Himmelswache der Sha'tari etwas leichter fallen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11061, "deDE", "Wir knnen Euch gar nicht genug danken, $N!$B$BSo instabil diese zwei Flschchen auch sein mgen, mit ihrer Hilfe knnen wir nun einen unserer Jungs nach Norden zum Auenposten der Himmelswache schicken.$B$BWartet einen kleinen Augenblick, whrend wir einen von ihnen rufen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11062, "deDE", "Willkommen im Auenposten der Himmelswache. Wisst Ihr, ich habe den gesamten Posten mit meinen eigenen Hnden errichtet.$B$BSchon richtig gehrt, alles was Ihr hier vor Euch seht!$B$B<Dem Himmelskommandanten scheint es ernst zu sein.>$B$BIhr msst ja ganz schn scharf auf einen Kampf sein, wenn Ihr extra hierhergekommen seid, um mit mir zu sprechen. Es war nett vom alten Chu'a'lor, Euch vorbeizuschicken. Wir knnen hier immer etwas Frischfleisch gebrauchen.$B$BWenn es Euch nichts ausmacht, wrde ich vorschlagen, Ihr unterhaltet Euch ein wenig mit meinem Himmelsoffizier Vanderlip. Strt Euch nicht an ihrem Gebaren, ihre Zunge ist womglich schrfer als das Breitschwert, das sie bei sich trgt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11065, "deDE", "Habt Ihr schon ein paar therrochen gebndigt? Ihr seht so aus, als knntet Ihr mit einem Lasso umgehen. Sicher habt Ihr Euren Auftrag schnell abgeschlossen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11065, "deDE", "Juhuu! Gute Arbeit, $N! Diese Rochen werden ein paar hervorragende Reittiere abgeben!$B$BBleibt ruhig noch ein wenig, die Himmelswache der Sha'tari kann Mnner mit Euren Talenten immer gut gebrauchen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11066, "deDE", "Ihr leistet hervorragende Arbeit fr uns, $N! Ich mchte Euch nur wissen lassen, dass wir Eure Bemhungen, uns mit frischen Reittieren zu versorgen, sehr zu schtzen wissen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11066, "deDE", "Ihr erstaunt mich immer wieder! Ich sage Euch was, warum lasst Ihr das Lasso nicht fr heute ruhen und wir sehen uns morgen wieder?$B$B<Khatie lacht.>$B$BNochmals vielen Dank, $N!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11078, "deDE", "Wie geht es voran, $N? Ich brauche Euch sicherlich nicht extra zu sagen, wie wichtig die ganze Angelegenheit fr unsere Luftberlegenheit ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11078, "deDE", "Ja! Diese Zhne sehen scharf aus, als knnten sie beinahe alles wie Butter durchschneiden!$B$BErlaubt mir, Euch und Eure Freunde dazu zu ermuntern, weiterhin so gute Arbeit zu leisten! Diese Drachen sind gefhrliche Raubtiere, die unseren Himmel lange genug unsicher gemacht haben.$B$BEuch sind sicherlich die Drachenschuppen aufgefallen. Wie ich hrte, kann man aus ihnen einen mchtigen Umhang herstellen, sofern man eine Schuppe von jedem der Drachen besitzt. Sollte Euch der Umhang nicht zusagen, bin ich jederzeit bereit, ihn gegen einen Apexiskristall einzutauschen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11079, "deDE", "Ihr haben Peitsche?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11079, "deDE", "Gahk mgen Peitsche. Vielleicht Gahk nehmen Peitsche und bearbeiten ein paar Dmonen!$B$BMich sagen zu $R, dass $R kann jederzeit beschwren Dmonenanfhrer, solange $R haben viele Freunde und 'ne Menge Splitter.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11080, "deDE", "Euer Geschick im Umgang mit den Relikten erstaunt uns immer wieder, $N. Drften wir Euch vorschlagen, mindestens einmal am Tag den Gebrauch der Relikte zu ben, damit Euer Verstand seine Schrfe bewahrt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11091, "deDE", "Hey, Mann, das ist toll! Wir haben viel von Euren guten Taten gehrt. Das ist fantastisch. Wir haben da so ein Gefhl, dass Ihr fr Ogri'la alles zum Guten wenden werdet. Ihr werdet uns beschtzen und alles wird heiter und sonnig!$B$BHier, nehmt das. Es ist ein kleines berlebenspaket, dass wir zum Dank fr Euch zusammengestellt haben.$B$BWerft doch bitte einen Blick auf unsere Waren. Wir haben eine reichhaltige Auswahl der erlesensten Gegenstnde, von denen Euch sicherlich das eine oder andere Stck interessieren wird.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11119, "deDE", "Egal, was Keller dazu meint, ich bevorzuge es, unsere Einstze als Forschungsexkursionen zu betrachten.$B$BEs stimmt allerdings, dass wir dort Vorrte fr die Himmelswache beschaffen, jedenfalls, wenn wir lange genug dort bleiben knnen.$B$BIhr seht so aus, als wret Ihr bereit, uns den Rcken gegen die Astralen freizuhalten. Wenn Ihr uns die Astralen lange genug vom Leib haltet, werden wir Euch alle zustzlich an der Kristallschmiede hergestellten Gegenstnde zum Verkauf anbieten, also bringt Eure Apexissplitter und Kristalle mit!$B$BWir ziehen in 2 Stunden los.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10173, "deDE", "Ich werde nicht ruhen, bis Erzmagier Vargoth seinen Stab zurck hat. Konntet Ihr ihn den Klauen des Dmons entreien?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10173, "deDE", "Ah, Ihr habt es also geschafft, den Stab zurckzuholen. Beeindruckend, $N. Vielleicht seid Ihr genau der $C, den ich suche.$B$BOh ja, Eure Belohnung...$B$B$B$BIhr habt sicherlich mehr erwartet, aber das kommt schon noch mit der Zeit. Frs Erste soll die Mglichkeit Eure Belohnung sein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10174, "deDE", "<Erzmagier Vargoth betrachtet Euch, als Ihr ihm Ravandwyrs Nachricht bermittelt.>$B$BRavandwyr hat alles, was ein Meister sich von einem Lehrling wnschen kann. Als ich ihm den Stab gab und ihm befohlen habe, zu fliehen, habe ich gehofft, dass er ihn benutzen wrde, um jemanden zu finden, der mich nach dem Abzug von Kael'thas Truppen aus dem Turm befreien kann. Seitdem habe ich viel ber den Fluch herausgefunden.$B$BEr kann gebrochen werden, aber dazu brauche ich Eure Hilfe.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10176, "deDE", "Das habt Ihr gut gemacht, $N. Ohne das Dmpferfeld wird es viel leichter fr mich, die Magie vorzubereiten, mit der ich den Fluch des Turms endlich aufheben kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10182, "deDE", "Gut gemacht, $N. Ich kann keine Rastlosigkeit mehr in Dathrics Geist spren. Ich wrde viel dafr geben, wenn ich noch einmal mit ihm sprechen knnte, aber es ist schon gut genug, dass er nun in Frieden ruht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10183, "deDE", "Stress, Stress. Keine Zeit verschwenden! Es gibt ein Raketenschiff, das gebaut werden will!$B$BWas wollt Ihr?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10183, "deDE", "Oh, Kupfernickel hat sich also endlich entschlossen, seine Entdeckungen zu berichten, was? Faule Wissenschaftler!$B$BWas ist das?$B$B<Der Chef blttert ein paar Mal durch die Notizen mit einem deutlich skeptischen Gesichtsausdruck.>$B$BEr sagt, dass die gewaltigen Netherenergien, die durch den Nethersturm flieen, alles in nur ein paar Monaten in Stcke reien werden.$B$BDas ist ungnstig - jetzt mssen wir den Zeitplan fr das Raketenschiff etwas straffen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10184, "deDE", "<Hter Dierwert schttelt langsam den Kopf.>$B$BSie haben das nicht verdient, aber es ist das Beste, was wir tun knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10185, "deDE", "<Der Hter sieht bestrzt aus.>$B$BIch kann die nchste Welle schon ber den Horizont kommen sehen. Sie auszumerzen hilft vielleicht fr den Moment, aber wir brauchen langfristige Manahmen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10186, "deDE", "Sieht es etwa aus, als wre die X-52 Netherrakete bereit zum Abflug? Durchquere ich den Wirbelnden Nether in meinem bequemen Kommandostuhl? Entdecke ich fremde neue Welten? Suche nach neuen Technologien und Geschftsgelegenheiten? Dringe in Galaxien vor, die nie ein Goblin zuvor gesehen hat?$B$BNein!$B$BAlso hoffe ich, dass Ihr die Kristalle habt, nach denen ich Euch geschickt habe.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10186, "deDE", "Pah! Die sehen aber grob aus. Vielleicht knnen wir sie in einer tertiren Antriebsmehrfachkammer einsetzen?$B$BTrotzdem gute Arbeit, Juniortechniker dritten Grades! Ihr werdet es sicher bald zum zweiten Grad schaffen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10188, "deDE", "Wenn ich vom Fluch des Turmes frei sein will, muss ich das Siegel haben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10188, "deDE", "Ah, es ist so wundervoll, wie ich es in Erinnerung hatte. Knnt Ihr die Macht, die davon ausgeht, spren?$B$BNatrlich ist noch viel mehr zu tun, aber Ihr habt meinen tiefsten Dank dafr, dass Ihr mir das Siegel zurckgebracht habt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10189, "deDE", "Habt Ihr bekommen, worum ich Euch gebeten habe, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10189, "deDE", "Ausgezeichnet! Mit dieser Stammrolle knnen wir bessere Entscheidungen treffen, wenn wir die Anhnger Kaels bei den Manaschmieden angreifen.$B$BEigentlich schade, ich mochte es irgendwie, seine Lakaien wahllos zu tten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10190, "deDE", "Habt Ihr die Batterie schon aufgeladen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10190, "deDE", "He, passt auf, auf wen Ihr mit dem Ding zeigt! Ihr habt es auf tten gestellt!!!$B$BGut, es sieht so aus, als httet Ihr die Batterie gengend aufgeladen. Ich baue sie gleich in Nr. V ein.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10191, "deDE", "Ihr habt es geschafft! Ihr habt es geschafft! Ich knnte Euch kssen!$B$BOh verdammt... die Batterie von Maxx E. Million Nr. V ist schon wieder leer. Naja, er hat seine Arbeit erledigt, und das ist das Wichtigste.$B$BRaketenchef Doppeldecker wird sich freuen, wenn er diese Draeneimaschinen in der X-52 Netherrakete einbauen kann! Das bedeutet eine Befrderung fr mich! Ich bin so glcklich, ich muss Euch einfach belohnen!$B$BHier, sucht Euch etwas davon aus.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10192, "deDE", "Habt Ihr das Buch besorgt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10192, "deDE", "<Das Abbild von Erzmagier Vargoth scheint das Kompendium zu begutachten.>$B$BEs ist ein wenig mitgenommen, aber wenn man bedenkt, was es durchmachen musste, ist es ein Wunder, dass es berhaupt berlebt hat. Bewahrt es auf, $N. Ohne das Buch haben wir kaum eine Chance, den Schutzschild des Turms zu durchbrechen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10193, "deDE", "Ihr seid ein zuverlssiges Mdel. $N. Wenn es etwas gibt, das ich noch mehr mag, als Kaels Schohunde zu tten, dann ist es, wenn das jemand anderes fr mich erledigt.$B$BSo habe ich weniger Blut auf meinen Klamotten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10194, "deDE", "Euren Flug zu organisieren war nicht gerade billig. Ich hoffe, ihr habt das Zeug zu diesem Auftrag.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10197, "deDE", "Habt Ihr die Kleidungsstcke, um die ich Euch gebeten habe?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10197, "deDE", "Ausgezeichnet! Wenn ich mit Euch fertig bin, werdet Ihr Euch unbemerkt unter die Rnge des Sonnenzorns mischen knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10198, "deDE", "Wie geht es mit der Bespitzelung voran, $N? Ihr solltet Euch lieber beeilen. Ich werde hier schon ganz kribbelig.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10198, "deDE", "Die Manaschmiede Duro ist in Schwierigkeiten? Ich bin mir sicher, dass Thalodien sich ber diese Neuigkeiten freuen wird. Lasst uns zunchst diese Aufgabe hier fertigstellen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10199, "deDE", "Wenn ich das Gift von gengend vielen Netherrochenstacheln extrahieren kann, bekommen wir vielleicht den explosivsten Kraftstoff, der gemischt wurde!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10199, "deDE", "Wow! Ok, jetzt stimmt die Sache.$B$BLasst mich nur schnell die Boshaftigkeit aus diesen kleinen Schnheiten quetschen. Das sollte ein Raketentreibstoff werden, der alle von den Socken haut!$B$BHier, lasst mich das Schieferl und das Stachelgift mischen, und dann schauen wir mal, was passiert...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10200, "deDE", "Ihr berrascht mich immer wieder, $N. Das war beeindruckende Spionagearbeit, die Ihr da in der Manaschmiede Coruu geleistet habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10202, "deDE", "Es ist an der Zeit, dass Ihr auftaucht. Ich habe mich schon gefragt, ob die Seher berhaupt interessiert sind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10203, "deDE", "Ihr msst derjenige sein, der uns die ganze Ausrstung von den Ruinen hergebeamt hat! Ein Hoch auf Euch!$B$BIch habe die Ausrstung bereits an unsere Grabungsorte weiterverteilt. Jetzt knnen wir unsere Ausgrabungen im groen Stil berall auf der Insel durchfhren! Die Netherrakete wird in null Komma nichts fertig werden!$B$BHmm, merkwrdig. Wartet mal, habt Ihr gesagt, Ihr habt uns vier Dinge zurck geschickt? Ich habe nur drei bekommen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10204, "deDE", "Hmmm... wie ich erwartet habe.$B$BSolch rohen Energien ausgesetzt zu sein erhht unsere angeborene Sucht nach Magie gewaltig.$B$BIch bin nicht berrascht, dass Kael mit seinen Taten ungestraft davon kommt, wenn seine Angestellten praktisch die ganze Zeit betrunken sind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10205, "deDE", "Ich bin erleichtert zu hren, dass dieser Abschaum endlich vernichtet wurde. Ihr macht Eure Arbeit gut, $N... darf ich so vermessen sein, Euch beim Namen zu nennen?$B$BWenn Ihr mchtet, habe ich eine weitere Aufgabe fr Euch. Eine, die besonders wichtig ist. Jemand mit so offensichtlichen Talenten wie Ihr wird sie sicher leicht bewltigen knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10206, "deDE", "So schnell schon zurck? Nehmt mich besser nicht auf den Arm, $C.$B$BDer Erfolg des X-52-Projekts hngt davon ab, dass wir das Zeug so schnell wie mglich bekommen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10206, "deDE", "Hehe, astrale Deppen!$B$BDanke, dass Ihr uns die Teile gebracht habt, $C. Ich wette, wir knnen diese Babys gleich in die Rakete einbauen. Ich frage mich, was sie wohl bewirken? Wird sicher lustig, es herauszufinden!$B$BHm, lasst mich mal schauen ob ich noch ein wenig Wechselgeld fr Euch in der Hosentasche habe.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10209, "deDE", "Habt Ihr den Stein von Gletscharius bekommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10209, "deDE", "<Ihr wisst nicht, ob es nur eine Verzerrung ist, aber der Erzmagier scheint zu lcheln.>$B$BJetzt, da ich all meine Besitztmer zurck habe, habe ich alles, um Kael'thas' Magie zu durchbrechen. Ich werde den Zauber gleich wirken.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10221, "deDE", "Habt Ihr uns vor den Verwstungen durch Dr. Bumm gerettet? Ich verspreche Euch, dass es diesmal keine explodierenden Wagen gibt, auch wenn das wirklich spaig war!$B$BWir sollten das wirklich fter tun!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10221, "deDE", "Ich werde Sparky immer vermissen. <Schnief> Aber jetzt, da wir wissen, dass Dr. Bumm uns nicht mehr tyrannisieren kann, werden wir alle besser schlafen.$B$BLasst Euch das eine Lehre sein. Kommt hier nicht ohne Netherhelm runter!$B$BNun, wie kann ich Euch dafr belohnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10222, "deDE", "Was Ihr getan habt, kann die Gefallenen nicht zurckbringen, aber vielleicht kann es ihre Geister beruhigen. Obwohl ehrlich gesagt kein Blut der Welt meinen Zorn ber Kael'thas Taten stillen kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10223, "deDE", "<Ihr seid Euch nicht sicher, aber es sieht so aus, als wrde Hter Dierwert gleich anfangen zu weinen.>$B$BEndlich ist es geschafft. Wenn ich knnte, wrde ich meine Existenz selbst auslschen, aber die Wahrheit ist, dass ich nicht loslassen kann. Fr die Leute im Dorf ist es schwer, aber zumindest ehrlich. Ich schtze, das ist der Preis, den wir dafr bezahlen mssen, dass wir uns so sehr an diesen Ort gebunden haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10224, "deDE", "Habt Ihr schon alle Essenzen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10224, "deDE", "Wow, das sind viele Essenzen!$B$BGut, schauen wir mal, was passiert, wenn ich sie mit den Astrallithiummatrixkristallen mische.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10225, "deDE", "h, hallo. Ich bin hier ein wenig beschftigt. Was habt Ihr da?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10225, "deDE", "Oh, Astrallithiummatrixkristalle! Ja, die klingen wichtig.$B$BLasst sie uns dort drben hinstellen und ich kmmere mich gleich um sie.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10226, "deDE", "Habt Ihr die Energie von diesen Elementaren schon? Oh, keine Hektik oder so. Hngt ja nur mein Leben und meine Karriere davon ab.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10226, "deDE", "Das ist besser! Gut, ich glaube, jetzt sind wir im Geschft.$B$BLasst mich dieses Zeug nur kurz in das Zau-ba-Ding hier tun, und ich bin sicher, dass es gleich einen voll funktionsfhigen Phasenantriebskern ausspuckt.$B$BOh, he, ich sollte Euch vielleicht dafr belohnen, dass Ihr meine Haut gerettet habt. Hehe.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10232, "deDE", "Nun, das sind gute und schlechte Nachrichten zugleich. Wir haben ihnen einen Schlag versetzt, aber ich wei nicht, wie es weitergehen soll, wenn die Brennende Legion berall im Nethersturm frei herumrennt - und nher kommt.$B$BIch habe das Gefhl, dass wir einen Gang hochschalten mssen. Ich hoffe, Ihr knnt uns dabei helfen, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10233, "deDE", "<Zaubererleutnant Morran nickt grimmig.>$B$BDie Kirin Tor heien Zerstrung im Allgemeinen nicht gut, aber um unseren Feind zu vernichten berwinden wir jeden Skrupel. Sogar im Tod.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10234, "deDE", "Habt Ihr die Teile schon? Wir haben nur wenig Zeit, da die Legion berall gleichzeitig in unser Gebiet vorrckt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10234, "deDE", "Ich denke, die mssen reichen. Wenn wir sie nur fortjagen knnten!$B$BHe, da fllt mir etwas ein!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10235, "deDE", "Schon mit Verdammnisklaue abgerechnet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10235, "deDE", "Er, h... er hat was gesagt? SIE WOLLEN AREA 52 ANGREIFEN?$B$BOh, das ist nicht gut... nicht gut!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10237, "deDE", "Ha, Ihr macht Witze, oder? Die Brennende Legion gibt uns die Schuld daran?$B$BAber IHR wart das!$B$BOh, ich habe Kopfschmerzen, die so gro wie diese Rakete sind! Ok, ok, wir mssen uns einen anderen Plan einfallen lassen.$B$BWenn die Brennende Legion denkt, dass sie hierher kommen, unser Dorf zerstren und meinen Traum von einer Reise durch den Wirbelnden Nether ruinieren kann, dann hat sie sich aber getuscht.$B$BUnd ich kenne genau den richtigen Goblin, der uns dabei helfen kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10239, "deDE", "Konntet Ihr ein paar ihrer Energiequellen sammeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10239, "deDE", "<Morran dreht einen der Wrfel vorsichtig in der Hand.>$B$BIch glaube nicht, dass ich je zuvor so etwas gesehen habe. Es steckt sicher eine Menge Energie darin, aber es ist rohe Energie. Wir mssen vorsichtig damit sein, aber ich glaube, dass wir einen Weg finden knnen, wie man sie zur Abwehr der Manakreaturen einsetzen kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10240, "deDE", "Habt Ihr alle Runen aktiviert? Ich bin so gespannt, ob wir die Methode der Blutelfen hier verbessern knnen. Was einer von Kael'thas' Leuten tun kann, kann ein Kirin Tor noch besser!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10240, "deDE", "Ausgezeichnete Arbeit. Drckt die Daumen, $N. Ich werde den Schild jetzt aktivieren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10241, "deDE", "Ausgezeichnete Arbeit, $N. Die Seher haben dank Eures Ablenkungsmanvers wichtige Informationen sammeln knnen. Vielleicht teilen sie sie dieses Mal tatschlich mit uns.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10243, "deDE", "Die Symbole sehen fremdartig und unlesbar aus.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10243, "deDE", "Ihr ffnet die Rolle und bereitet Euch vor, die merkwrdigen Symbole abzuschreiben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10245, "deDE", "Wollen wir mal schauen, was Ihr da habt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10245, "deDE", "Gut gemacht, $N. Gebt mir ein paar Augenblicke, um die Abschrift zu lesen.$B$BIhr habt den Dank der Priesterschaft, $C.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10246, "deDE", "Ausgezeichnete Arbeit, $N. Mit Eurem Einsatz schaffen wir es sicher, den Feind zu besiegen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10247, "deDE", "Wie es scheint, haben wir groes Glck, junger Mann.$B$BIch habe gerade neulich meine neuste Erfindung, den Schrotthscher X6000, fertig stellen knnen!$B$BUnd Ihr habt die Ehre, mir dabei zu helfen, ihn zu testen. Dann knnen wir ihn zur Verteidigung von Area 52 einsetzen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10248, "deDE", "Was meint Ihr? Muss noch ein wenig optimiert werden, was?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10248, "deDE", "Nun, mein Junge, das war aber aufregend! Und meiner Meinung nach hat der Schrotthscher X6000 trotz seines Grnschnabelpiloten gut funktioniert.$B$BHehe, seid nicht beleidigt, ich mache nur Witze. Schlielich habt Ihr gerade Area 52 und die X-52 Netherrakete vor dem sicheren Untergang bewahrt!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10249, "deDE", "$N! Oder sollte ich sagen Juniortechniker ersten Grades $N?! Ach, wem mache ich etwas vor. Einen Grad zu berspringen ist keine angemessene Belohnung fr das, was Ihr getan habt. Ich ernenne Euch zu meinem Ersten Offizier!$B$BIhr habt uns alle gerettet, und noch viel wichtiger, die X-52 Netherrakete! Ich knnte Euch kssen... aber das tue ich nicht.$B$BLasst mich Euch stattdessen die Dankbarkeit von Area 52 zeigen. Whlt etwas aus!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10256, "deDE", "<Ihr beschreibt, was Ihr gesehen habt.>$B$BKommandantin Sarannis sagt Ihr? Hmm...$B$BSie ist eine von Kael'thas Beratern, aber ganz sicher keine Magierin. Ich kenne sie von dem Angriff auf Kirin'Var. Ich glaube, Ihr habt Recht, $N. Sie mag den Fluch vielleicht nicht selbst aufrechterhalten, aber sie hat den Schlssel zum Schild.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10257, "deDE", "Habt Ihr den Schlsselstein?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10257, "deDE", "<Die Augen des Erzmagiers leuchten auf, als Ihr ihm den Schlsselstein gebt.>$B$BErstaunlich! Nach dem, was ich von Farahlons Schicksal erblickt habe, bin ich mir nicht sicher, ob ich hinaus will, aber es ist immerhin besser, als fr den Rest meiner Tage hier eingesperrt zu sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10261, "deDE", "Ich hoffe, Ihr kommt wegen meiner Suchanzeige!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10261, "deDE", "Juhuu! Wisst Ihr, wie schwer man an diese Dinge herankommt? Es ist ja nicht so, als ob ich hier drauen tglich eine Teilelieferung bekommen wrde.$B$BWisst Ihr was? Als Belohnung lasse ich Euch etwas aus meiner Kramkiste nehmen. Es ist sicher etwas darin, was Ihr brauchen knnt. Wenn nicht, verkauft es einfach weiter.$B$BIn Ordnung, lasst mich das Euch abnehmen und in meinen langhalsigen Spektrumanalysator einbauen, bevor der alte Servo noch explodiert!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10262, "deDE", "Ist es vollbracht? Habt Ihr den Abschaum der Zaxxis vom Angesicht der Scherbenwelt ausgerottet und mir ihre Insignien gebracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10262, "deDE", "Diese Insignien sind ein guter Anfang.$B$BUnglcklicherweise haben meine anderen Agenten bei den Zaxxis berichtet, dass Sphrenruber Nesaad immer noch am Leben ist!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10263, "deDE", "Hallo, $R.$B$BIhr steht steht nicht zufllig fr eine wichtige Bergungsmission zur Verfgung?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10264, "deDE", "Hallo, $R.$B$BIhr steht steht nicht zufllig fr eine wichtige Bergungsmission zur Verfgung?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10265, "deDE", "Ich habe den Eindruck, dass der Prinz nach einem ganz bestimmten Kristall sucht, aber ich bin mir nicht sicher, welcher das ist.$B$BHabt ihr dem Schreckenslord schon seinen abgenommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10265, "deDE", "Lasst mich ihn mal ansehen.$B$BHmm, er scheint nicht besonders zu sein. Ich bezweifle, dass er das ist, obwohl Eure Beschreibung der Vorgehensweisen der Legion in den Ruinen den Anschein erweckt, dass auch sie nach etwas ganz Bestimmten suchen.$B$BWollen wir hoffen, dass es nicht der Kristall ist, den der Nexusprinz so unbedingt haben will.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10266, "deDE", "Ja, es ist wahr. Die Blutelfen weigern sich auch dieses Mal, die Waren, die ihnen schon geliefert wurden, zu bezahlen.$B$BDa Netherpirscher Khay'ji Euch geschickt hat, hoffe ich, dass Ihr mir bei dieser Angelegenheit helfen knnt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10267, "deDE", "Habt Ihr die Vermessungsausrstung, um die uns die Blutelfen betrogen haben?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10267, "deDE", "Nun, ich bin froh, dass wir dieses kleine Geschft abgeschlossen haben. Ich kann mich nicht erinnern, dass das Konsortium je Geschfte mit solch flegelhaften und unserisen Wesen gemacht htte.$B$BIhr scheint jedoch vertrauenswrdig zu sein, $N. Vielleicht wollt Ihr mir ja noch einen Gefallen tun?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10268, "deDE", "Tretet nher.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10268, "deDE", "Ich heie Euch in der Sturmsule willkommen, $C. Bitte geniet die Gastfreundschaft des Konsortiums.$B$BDie Ausrstung knnt Ihr irgendwo abstellen. Ich nehme an, dass Ihr sie schon sehr bald wieder mitnehmen werdet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10269, "deDE", "Hallo, Reisender. Seid Ihr zu Hazzin gekommen, weil Ihr meine Waren begehrt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10269, "deDE", "<Hndler Hazzin senkt seine Stimme zu einem Flstern.>$B$BAh, ich wei von Eurem Auftrag. Er ist fr Nexusprinz Haramad uerst wichtig.$B$BJetzt, da wir die Position des ersten Triangulationspunktes herausgefunden haben, mssen wir schnell den zweiten suchen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10270, "deDE", "Seid Ihr bereit ber das Geschft zu reden, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10270, "deDE", "Diese Energiezelle sollte fr unsere Zwecke ausreichen. Sobald das Geld zu flieen beginnt, werden wir ihn noch weiter verbessern.$B$BDoch genug davon. Jeder mit Diskussionen verschwendete Augenblick bedeutet verlorenen Gewinn.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10271, "deDE", "Man stelle sich nur vor, dass dieser erbrmliche Astrale diese Essenzen wie Sigkeiten an die Blutelfen verkaufen wollte...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10272, "deDE", "Konntet Ihr die Eier sammeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10272, "deDE", "Die sollten ihren Zweck erfllen, $N. Vielen Dank, dass Ihr uns geholfen habt.$B$BBevor wir weitermachen knnen gibt es jedoch noch etwas anders, worber wir sprechen mssen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10273, "deDE", "Jetzt, da dieser lstige Astrale beseitigt ist, knnen wir uns darauf konzentrieren, die Netherdrachen besser verstehen zu lernen. Ich konnte die Eier untersuchen und habe dabei einiges herausgefunden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10274, "deDE", "Habt Ihr Veraku schon herausgefordert und gettet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10274, "deDE", "Gut gemacht, $N. Wirklich gut gemacht. Die Netherdrachkin werden zu Anfang keinen blauen Drachen in ihren Reihen akzeptieren, aber wenn ich meine Autoritt beweise, werden sie sich fgen. Zerschlagen, isoliert und ohne Fhrung sind sie leichte Beute fr bsartige Mchte. Wir mssen daher regelmig ein Auge auf sie werfen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10275, "deDE", "Wollt Ihr kaufen oder verkaufen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10275, "deDE", "Sehr gut, in der Tat. Der Nexusprinz wird zufrieden sein.$B$BJetzt haben wir den zweiten Punkt, der uns den dritten und damit die Umgebung, in der sich der Kristall befindet, verraten wird.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10276, "deDE", "Willkommen zurck, Freund. Wie geht es mit der Suche nach dem Kristall voran?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10276, "deDE", "In meinen wildesten Trumen habe ich nicht gedacht, dass Ihr den Kristall tatschlich beschaffen knnt!$B$BUnd Ihr gebt mir ihn sogar! Habt Ihr eine Ahnung, wie mchtig er ist?$B$BEure Selbstlosigkeit ist sehr inspirierend und unvergleichlich, $N! Ihr habt mir viel zum Nachdenken gegeben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10280, "deDE", "<Das musikalische Klingen der Naarusprache erfllt Euren Geist.>$B$BIch spre, dass Ihr einen Gegenstand von groer Macht bei Euch tragt, $R. Was bringt Ihr mir?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10280, "deDE", "Ihr habt mir den Gesang der Geister zurckgebracht! Prophet Velen hat den Kristall von Ata'mal zur Sicherheit bei seinem Volk zurckgelassen, bevor er auf seine schicksalstrchtige Mission zur Exodar ging.$B$BMerkwrdig, dass er nicht vorhergesehen hat, dass er in die Hnde der Brennenden Legion fallen wrde, wenn auch nur fr kurze Zeit.$B$BOder vielleicht hat er das ja? Es knnte sein, dass er all dies vorhergesehen hat, und ihn dort lie, damit Ihr ihn mir bringen knnt.$B$BIch spre, dass dies die Wahrheit ist. Ihr seid ein groer Held unseres Volkes, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10281, "deDE", "Mein Name ist Tyrygosa von Malygos' Brut, aber nennt mich einfach Tyri. Mein Begleiter ist der Paladin Jorad Knpp.$B$BDie Kunde von den merkwrdigen Drachkin hier verbreitet sich schnell, und ich frchte, dass sehr viele kommen werden, um sie auszubeuten, bevor sie berhaupt ihre wahre Natur erkannt haben. Wir hoffen, dass wir mit Eurer Hilfe diese Kreaturen kennenlernen knnen, bevor andere von ihnen erfahren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10290, "deDE", "Ich bin mir bewusst, dass das Sammeln von Farahlit sehr gefhrlich ist, aber die Mglichkeiten sind gewaltig. Ein Hndler, der nicht bereit ist, Risiken auf sich zu nehmen, kann seine Tren gleich schlieen und sich den rger ersparen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10290, "deDE", "Ah ja, genau die Qualitt, die ich mir erhofft habe. Wenn wir gengend Nachschub von den Riesen bekommen, brauchen wir die Kristalle nicht mehr abbauen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10293, "deDE", "Ihr seid immer noch am Leben, was bedeutet, dass Ihr entweder den Kern besorgt oder Eure Meinung gendert habt. Was davon stimmt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10293, "deDE", "Gut gemacht! Ich kann kaum abwarten, es auszuprobieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10299, "deDE", "Wart Ihr bei Eurer Aufgabe erfolgreich, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10299, "deDE", "Das Licht ist Euch wohlgesinnt, $N. Wir haben alle fr Euren Erfolg gebetet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10300, "deDE", "Habt Ihr schon ein paar geeignete Kristalle fr ein neues Kopfstck des Stabs gefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10300, "deDE", "<Ravandwyr untersucht die Kristalle genau und whlt dann einen aus.>$B$BDieser Kristall sollte funktionieren. Ich werde den Sockel des Stabs umformen, damit das neue Kopfstck passt.$B$BErzmagier Vargoth wird enttuscht sein, wenn er ihn sieht, aber ich bin bereit, die Enttuschung meines Meisters in Kauf zu nehmen, wenn er nur endlich wieder frei ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10301, "deDE", "Habt Ihr das Oculus besorgt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10301, "deDE", "Ausgezeichnet, $N. Jetzt haben wir fast alles, was wir bentigen, um Kael'thas' Magie zu durchbrechen.$B$BUnd wenn die Blutelfen unter dem Verlust ihres Kommandanten hier leiden, dann umso besser.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10305, "deDE", "Ja, ich habe davon gehrt, dass Bannzauberin Belmara ein altes Buch mit Geschichten hatte, aber ich wusste nicht, dass sie es mit in die Scherbenwelt gebracht hatte. Ich bin mir sicher, dass ihr Geist nun, da Ihr es gefunden und zurckgebracht habt, ruhen kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10306, "deDE", "Wir haben immer Scherze darber gemacht, dass Luminrath zu allem diesen Mantel tragen wrde. Fr ihn passte Rot genauso gut zu Wei wie zu Schwarz, Violett, Blau, Gelb, Orange und Grn. Er htte niemals zwei Tage hintereinander dieselbe Robe getragen, aber der Mantel war immer dabei...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10307, "deDE", "<Der Hter lchelt, als Ihr Cohliens Name erwhnt.>$B$BIhr habt absolut Recht mit der Kappe. Er trug sie die ganze Zeit und lies sie nicht aus den Augen.$B$BEin paar Mal hat sich einer seiner Kollegen die Kappe ausgeliehen und sie zum Scherz versteckt. Ich brauche nicht zu erwhnen, dass die Androhung eines Frostblitzes von Cohlien ihn schnell zum Umdenken bewegt hat.$B$BIch werde den kleinen Cohlien vermissen. Er war sehr beliebt bei den Elfendamen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10308, "deDE", "Das Konsortium ist sehr daran interessiert, alle Rebellen der Zaxxis beim Hgel im Sden auszulschen.$B$B$C, wenn Ihr Euch bei uns noch beliebter machen wollt, dann geht zurck und besorgt noch mehr ihrer Insignien.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10308, "deDE", "Verrter, alle!$B$BGute Arbeit, $C! Wenn Ihr nochmals dort hinunter gehen solltet, bin ich immer noch auf der Suche nach weiteren Insignien fr meine Sammlung.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10309, "deDE", "Meine Augen mssen von der ganzen Netherstrahlung hier schlechter werden. Habt Ihr schon das Herz des Teufelshschers?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10309, "deDE", "Wow, schaut Euch das an... ein schlagendes Teufelshscherherz! Nun, es schlgt nicht wirklich, aber Ihr wisst schon, was ich meine.$B$BIch kann es kaum abwarten, dieses Ding zu benutzen. Oder ich verkaufe es. Ich kenne ein paar Depp... h Kunden, die daran interessiert sein knnten.$B$BHier, Ihr habt Euch die Belohnung verdient!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10310, "deDE", "Das sind ausgezeichnete Neuigkeiten, mein Freund!$B$BIhr und Drijya haben uns einen groen Dienst erwiesen. Jetzt, da das Warptor auer Gefecht ist, knnen die Dmonen in der Nhe keine Verstrkung mehr holen.$B$BBitte erlaubt mir, Euch in der Art des Konsortiums zu belohnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10311, "deDE", "Ich hoffe, dass Gahruj Euch hierher geschickt hat, um mir bei meinem Auftrag zu helfen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10312, "deDE", "Konntet Ihr das Stadtregister finden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10312, "deDE", "<Hter Dierwert nimmt Euch das Buch ab und blttert es durch.>$B$BHmm...$B$BSieht aus, als htte ich Recht gehabt. Es scheint ein Muster zu geben.$B$BDie aggressivsten und gefhrlichsten Geister stimmen mit den mchtigsten und hochrangigsten Kirin Tor, die in dem Dorf gelebt haben, berein. Dagegen scheint man die Dorfleute und Hndler bersehen zu haben. Was knnte das bedeuten?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10313, "deDE", "Das sind schlimme Nachrichten, $N. Die Warpenergien, die aus den Rohren herausstrmen, sind gefhrlich hoch.$B$BWir mssen einen Weg finden, um die Manaschmieden abzuschalten, oder der kleine Rest, der noch von der Scherbenwelt brig ist, wird in tausend Stcke gesprengt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10314, "deDE", "Was habt Ihr herausgefunden, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10314, "deDE", "<Wchter Dierwert untersucht die berreste.>$B$BDas Gert, das Ihr beschreibt, klingt nach einem nekromantischen Fokus... Ich htte wissen sollen, dass dieser bsartige Lehrling sich dazu herablassen wrde. Ich dachte, seine Ausbildung wrde ihn daran hindern, so weit zu gehen, aber ich schtze, ich habe mich getuscht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10315, "deDE", "Alles, was wir tun, um sie von ihren Taten in den Manaschmieden abzuhalten, ist gut.$B$BMan kann offenbar nicht vernnftig mit ihnen reden. Und was auch immer ihre Anfhrer vorhaben, sie mssen verrckt sein.$B$BAls Belohnung fr Eure treuen Dienste fr die Ziele des Konsortiums mchte ich Euch in Eurer Landeswhrung bezahlen, wenn Ihr nichts dagegen habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10316, "deDE", "Eine leuchtende Kugel liegt auf einem quadratischen Podest. Auf den ersten Blick scheint sie ein Behltnis fr magische Energie zu sein, aber welche Art von Energie und zu welchem Zweck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10317, "deDE", "<Der Groknecht flstert.>$B$BSeid Ihr hier, um mich zu tten? Hmm. Nun, ich glaube, die Astralen wissen nicht alles, was?$B$BIch bin nicht der, den Ihr sucht, glaubt mir.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10318, "deDE", "Ein Verdammniswachenbermeister? Das ist merkwrdig! Was macht denn die Brennende Legion dort?$B$BHm, einige der Blutelfen werden also abtrnnig. Wir werden das im Hinterkopf behalten, falls wir uns je entschlieen sollten, den Ort mit Gewalt einzunehmen. Zumindest sind das ein paar gute Nachrichten, oder?$B$BBitte, $N, nehmt dieses Zeichen meiner Dankbarkeit.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10319, "deDE", "Wir mssen erst Naberius' Phylakterium beschaffen, bevor wir ihn angreifen knnen. Er ist sonst unverwundbar!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10319, "deDE", "Ausgezeichnete Arbeit, $N. Der Besitz des Phylakteriums ist der Schlssel zu unserem Sieg. Wenn Ihr gegen Naberius in die Schlacht zieht, werde ich es zerstren und ihn so fr Eure Angriffe verwundbar machen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10320, "deDE", "Der Sieg ber Naberius ist eine groe Leistung, $N. Ich kann mir nicht helfen, aber ich glaube, wir htten eine Chance gegen Kael'thas' Streitkrfte gehabt, wenn der Lehrling auf der richtigen Seite gestanden htte.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10321, "deDE", "Habt Ihr Eure Aufgabe schon erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10321, "deDE", "Ausgezeichnete Arbeit. Jetzt sind nur noch zwei Manaschmieden aktiv.$B$BBald wird Kael'thas von seiner Hauptenergiequelle abgeschnitten sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10322, "deDE", "Habt Ihr Eure Aufgabe schon erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10322, "deDE", "Ihr seid von den Naaru gesegnet, $N. Euer Einsatz fr die Sache wird nicht unbemerkt oder unbelohnt bleiben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10323, "deDE", "Habt Ihr die Aufgabe, die ich Euch gegeben habe, schon erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10323, "deDE", "Die Brennende Legion? Verbndet mit Kael'thas Sonnenwanderer?$B$BDas sind erschtternde Nachrichten. Das erklrt vieles, was wir hier und in Schattenmond beobachtet haben.$B$BWir mssen Shattrath sofort informieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10328, "deDE", "Hattet Ihr Glck mit den Anweisungen der Manaschmiede Duro?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10328, "deDE", "Ausgezeichnete Arbeit, $N! Wollen wir mal schauen, was uns diese Anweisungen verraten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10329, "deDE", "Hat der Plan funktioniert? Ehrlich gesagt habe ich fast befrchtet, dass Ihr in die Luft gejagt werdet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10329, "deDE", "Natrlich hat es funktioniert! Ich bin Spionagemeister, meine Informationen sind immer genau.$B$BWie auch immer, da wartet ein brandneuer und spannender Auftrag auf Euch. Wollt Ihr davon hren? Natrlich wollt Ihr das.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10330, "deDE", "Habt Ihr Eure Aufgabe schon erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10330, "deDE", "Wir haben den Aufruhr gehrt und wollten gerade gehen. Ich bin froh, dass Ihr nicht allzu viel Prgel einstecken musstet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10331, "deDE", "Hattet Ihr Glck bei der Suche nach meinem Hammer?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10331, "deDE", "Dem Himmel sei Dank! Jetzt kann ich zurck an die Arbeit. Ich wei, dass Ihr denkt, dass ich verrckt bin, so weit von einer richtigen Schmiede an meinem Gesellenstck zu arbeiten, aber ich darf nicht riskieren, dass die anderen Lehrlinge meine Entwrfe stehlen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10332, "deDE", "Meister Rhonsus hat mir alles beigebracht, was ich ber mein Handwerk wissen muss. Ich werde seine Erinnerung in Ehren halten, indem ich den Leuten von Kirin'Var mit meinem Geschick diene.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10333, "deDE", "Was? Er hat mich seine Partnerin genannt? Ich bin seine Frau!$B$BUnd er sagt, ich htte eine scharfe Zunge. Oh, wenn ich zurck nach Area 52 komme, werde ich ihm die groen grnen Ohren langziehen!$B$BAber ich bin froh, dass Ihr hier seid. Mit einer Sache hatte er Recht; ich kann ganz sicher Eure Hilfe brauchen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10334, "deDE", "<Die Kuh schaut Euch misstrauisch an, bleibt aber stehen.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10334, "deDE", "<Als Ihr die Glocke aus Eurer Tasche holt, lutet Ihr sie ein paar Mal und zeigt sie Bessy, die sie zu erkennen scheint. Die Kuh lsst zu, dass Ihr ihr die Glocke um den Hals hngt und schaut Euch erwartungsvoll an.>", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10335, "deDE", "Das ging aber schnell. Ihr msst sehr effektiv arbeiten, wenn Ihr alle drei Markierungen schon aufgestellt habt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10335, "deDE", "Ihr wollt mir keinen Bren in die Bandagen binden, oder? Das sind ausgezeichnete Nachrichten!$B$BJetzt, da Ihr die Routinearbeiten fr die Vermessung fr mich ausgefhrt habt, kann ich mein Team in die Ruinen fhren und schauen, was wir bergen knnen.$B$BIhr habt meinen Dank, $C.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10336, "deDE", "Wirklich ausgezeichnete Arbeit.$B$BJetzt, da die Dmonen von Culuthas tot sind, kann Zephyrion mit seinem Ausgrabungsteam sicher in die Ruinen von Farahlon zurckkehren.$B$BNehmt dies als Zeichen meiner Dankbarkeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10337, "deDE", "Bessy, bist Du das?$B$B<Der Bauer dreht sich zu Euch um.>$B$BVielen Dank, dass Ihr mir meine Bessy zurckgebracht habt, $N. Ich knnte ohne sie nicht leben!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10338, "deDE", "Habt Ihr die Konsole abgeschaltet? Ihr solltet Euch beeilen, bevor ich meine Meinung dazu ndere.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10338, "deDE", "Jetzt habt Ihr schon drei Manaschmieden abgeschaltet, $N. Kael'thas muss sich gerade furchtbar ber Euch aufregen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10339, "deDE", "Wir mssen alle Daten, die sie in diesem Gebiet aufbewahren, sammeln.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10341, "deDE", "Das habt Ihr gut gemacht, $N. Kaels Armee wird sich nicht so leicht von diesem Schlag erholen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10342, "deDE", "Habt Ihr schon genug Schiefer? Ich brauche eine ganze Menge, wenn ich genug l fr den Raketentreibstoff der X-52 herstellen will.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10342, "deDE", "Ok! Zutat Nummer eins erledigt.$B$BWenn Ihr noch Lust habt, besorgen wir jetzt etwas, das dem Schieferl den kleinen Extrakick gibt, um die Rakete in den Wirbelnden Nether zu katapultieren... oder in Stcke zu sprengen, was immer zuerst geschehen mag.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10343, "deDE", "Habt Ihr die berreste der Waffe, die Kael'thas gegen das Dorf eingesetzt habt, gefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10343, "deDE", "Eine Manabombe?$B$B<Morran dreht das Fragment in den Hnden.>$B$BEs ist unglaublich! Sogar die hellsten Kpfe in Dalaran htte eine Waffe wie diese nicht entwerfen knnen. Das schiere Ausma der Explosion muss einen Riss im Nether geffnet und diesen Kreaturen einen Zugang zu unserem Land verschafft haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10345, "deDE", "Konntet Ihr Sechs-Uhr finden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10345, "deDE", "Ihr habt meine Bandagen gerettet, $N. Dass Protektorat wird erfahren, was Ihr heute getan habt, Freund.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10348, "deDE", "Konntet Ihr die Pflanzen sammeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10348, "deDE", "Interessante Wahl, oder? Die heilende Wirkung der Pflanze muss berragend sein. Ich bekomme eine Menge Geld fr eine Blume, die eine der unspektakulrsten der Kuppeln ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10353, "deDE", "Und Sechs-Uhr? Hat er es geschafft?$B$BWollen wir verdammt noch mal hoffen. Ich konnte keine Verstrkung zu ihnen schicken... Das Astraleum hat nun die ganze Gegend in Beschlag genommen.$B$BEntschuldigt, fast htte ich Eure Belohnung vergessen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10365, "deDE", "Habt Ihr die Aufgabe, die ich Euch gegeben habe, schon erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10365, "deDE", "Die Brennende Legion? Unter einer Decke mit Kael? Daran wird Voren'thal sicher seine Freude haben.$B$BDenkt ihr, dass Tausende von Pilgern von Azeroth hierher kommen, um sich mit der Brennenden Legion zu verbnden? Ich glaube kaum!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10380, "deDE", "Es ist also vollbracht. Wir kommen im Auftrag unserer Herren hierher, jagen und tten sie. Es gibt kein Pardon.$B$BManchmal gibt das einem zu denken.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10381, "deDE", "Die schlechten Nachrichten, die Ihr mir bringt, erfllen mein Herz mit Traurigkeit. Zwei unserer tdlichsten Feinde schlieen sich zusammen und einer der teuersten Shne Shattraths hat seinen Glauben verloren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10384, "deDE", "Habt Ihr die Datenzelle sichergestellt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10384, "deDE", "<Ihr haltet die Datenzelle so, dass Ameer die Plne lesen kann.>$B$BNichts... Verdammt! Sie sind klug, aber nicht so klug wie Ameer!$B$BWorum ich Euch nun bitte, wird etwas gefhrlich werden. Ich kann verstehen, wenn Ihr nicht weitermachen wollt, Fleischling.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10385, "deDE", "Habt Ihr die Melderdaten schon besorgt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10385, "deDE", "Der Existenzgrund des Protektorats ist nun vollkommen besttigt.$B$BDas Astraleum ist nicht hier, um Dimensius zu jagen! Sie sind hier, um Leere zu werden!$B$BSie manipulieren die Leerenergien bei dieser Manaschmiede um ihre eigenen Energien an eine Kreatur namens Dunkelpirscher zu binden.$B$BSie benutzen sicherlich einen Protobeschleuniger um die Umwandlung zu vervollstndigen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10405, "deDE", "Habt Ihr die Bandage besorgt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10405, "deDE", "Ausgezeichnet! Jetzt kann ich das Gert einstellen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10406, "deDE", "Dank Euch sind ihre Plne gescheitert! Jetzt bleibt nur noch Eines brig... Salhadaar.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10407, "deDE", "Habt Ihr Socrethars Teleportationsstein schon besorgt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10407, "deDE", "Eure Aufopferung fr unsere Sache ist beispielhaft, $N. Jetzt, da wir Socrethars Teleportationsstein in unserem Besitz haben, gibt es nur noch Eines zu tun.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10408, "deDE", "Ist... Ist er wirklich tot? Vernichtet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10408, "deDE", "Seit tausend Jahren, vielleicht mehr, fhrt Salhadaar nun seinen Kreuzzug, bei dem er alles vernichtet, was ihm im Weg steht. Ich kann kaum glauben, dass wir ihn nun zum letzten Mal gesehen haben... Ihr habt uns einen groen Dienst erwiesen, $N, und dafr sollt Ihr belohnt werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10409, "deDE", "Das Licht sei mit Euch, $C.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10409, "deDE", "Es ist vollbracht, $N. Socrethar wurde vernichtet.$B$BIch und die anderen werden uns von unseren Wunden erholen, $N. Der Zorn der Magie Socrethars war jedoch zu viel fr den jungen Kaylaan. Nicht einmal meine strksten Gebete konnten ihn wiederbeleben.$B$BEs ist unendlich schade, dass jemand, der so jung ist, in der Schlacht sterben musste. Am Ende war jedoch sein Wille stark genug, um sich von der Verderbtheit der Legion zu erholen. Dies soll uns ein Trost sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10410, "deDE", "Was bereitet Euch Kummer, Kind?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10410, "deDE", "Socrethar... Ich erinnere mich an den Namen. Er war einst ein berhmter Krieger des Lichts. Sein einziger Fehler war sein Stolz.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10411, "deDE", "Sowohl der Schlick als auch der Abschaum sind extrem giftig. Wenn wir uns nicht um sie kmmern, verwandeln sie sich in erstarrte Leerenschrecken!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10411, "deDE", "Ich hoffe, es ist nicht zu spt. Habt Ihr irgendwelche abnormal groe Schleime in der Gegend des Leerenabschaums gesehen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10413, "deDE", "Hm, Ihr seht dreckig aus... Was ist geschehen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10413, "deDE", "WAS!? Ihr habt gegen einen Leerenschrecken gekmpft? Und berlebt?! Unglaublich!$B$BDiese Probe ist ihr Gewicht in Gold wert. Ein Fund wie dieser verlangt eine Belohnung.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10417, "deDE", "Habt Ihr die Diagnoseergebnisse?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10417, "deDE", "<Mehrdad nickt und betrachtet die Diagnoseergebnisse.>$B$BDas ist beunruhigend. Der Schaden ist grer, als ich befrchtet habe. Ich kann mir aus der Sturmsule einige Werkzeuge schicken lassen, um Reparaturen durchzufhren, aber das ist sinnlos, wenn wir nicht einmal wissen, was den Schaden verursacht hat.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10418, "deDE", "Das Gebiet sieht schon aufgerumter aus. Die Kuppeln sind sehr hilfreich, aber ein groer Nachteil ist der unkontrollierte Wuchs, den sie an manchen Orten begnstigen, der die einheimischen Arten schneller wachsen und sich schneller vermehren lsst.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10421, "deDE", "Habt Ihr in letzter Zeit ein paar teuflische Waffen gefunden? Lasst das Licht seine Wirkung tun, $N. Erlaubt diesen verderbten Dingen nicht, in dieser Welt zu verweilen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10421, "deDE", "Die teuflischen Materialien wurden aus dieser Welt entfernt. Nur das Licht bleibt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10422, "deDE", "Danke, $N. Tyralius hat schwere Wunden erlitten, aber wir denken, dass er es schaffen wird.$B$BEr hat ganze Felder voller Gefngnisse erwhnt, die so hnlich sind wie das, aus dem Ihr ihn errettet habt. Gefngnisse voller Kreaturen aus dem ganzen Universum!$B$BWir werden uns bei Euch melden, wenn wir entschlieen sollten, sie zu ffnen. Das Konsortium hat schon angemeldet, dass sie planen, ein paar Teams dorthin zu schicken. Sie arbeiten bereits an einer Technologie, mit der sie die Schlsser des Astraleums aufbrechen knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10423, "deDE", "<Ihr stellt Euch vor und meldet die Ergebnisse von Mehrdads Diagnose bei der Biokuppel Mittelreich.>$B$BMehrdad ist nur ein einfacher Techniker, aber er hat ein gutes Auge frs Detail. Wenn seine Beobachtungen richtig sind, mssen wir auch bei den restlichen Kuppelgeneratoren so schnell wie mglich Diagnosen durchfhren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10424, "deDE", "Habt Ihr die Diagnoseergebnisse?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10424, "deDE", "Diese Ergebnisse entsprechen denen von Mehrdad genau, aber warum? Der einzige Grund, den ich mir vorstellen kann, ist, dass etwas von der Ausrstung der Kuppelgeneratoren kaputtgegangen ist oder fehlt. Das wrde dazu fhren, dass die Kuppeln stark geschwcht werden und aus dem Gleichgewicht geraten, so wie es in der Diagnose deutlich wird.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10425, "deDE", "<Kommandant Ameer scheint zufrieden zu sein.>$B$BGut gemacht, Fleischling! Dieser Soldat ist vielleicht der Schlssel zur Vereitelung aller Plne des Astraleums.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10426, "deDE", "Habt Ihr schon versucht, die Energie der Kuppel zu fokussieren?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10426, "deDE", "Oh je! Die Energie der Kuppel zu bndeln hat zur Folge, dass die Kreaturen bis zu dem Punkt wachsen, an dem sich eine groe Aggressivitt entwickelt. Wenn wir die Mglichkeit bekommen, diese Technologie einzusetzen, mssen wir sie auf jeden Fall richtig einstellen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10427, "deDE", "Konntet Ihr ausreichend viele Talbuks markieren?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10427, "deDE", "Ausgezeichnete Arbeit. Ich werde die, die Ihr markiert habt, genau im Auge behalten, um zu beobachten, ob sie genauso schnell wie die anderen Lebensformen in der Kuppel wachsen.$B$BMit der richtigen berwachung knnten wir diese Technologie benutzen, um die zerstrten Gegenden in der Scherbenwelt und Azeroth wiederzubeleben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10429, "deDE", "Habt Ihr eine Probe von der monstrsen Hydra?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10429, "deDE", "<Aurine nimmt das Herz an sich.>$B$BSogar fr eine Kreatur von dieser Gre ist das Herz viel zu gro. Es musste sich sehr anstrengen, um mit dem riesigen Krper Schritt halten zu knnen. Ich habe keine Zweifel, dass sie nach der Hlfte ihrer normalen Lebenserwartung gestorben wre.$B$BTrotz dieser Risiken bin ich entschlossen, die Astralen davon zu berzeugen, ihre Technologie mit anderen zu teilen. Es ist zu viel versprechend, als dass wir jetzt aufgeben sollten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10430, "deDE", "Sieht aus, als httet Ihr den Prototyp. Hat Ghabar Euch geschickt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10430, "deDE", "Er sieht ein wenig merkwrdig aus. Ghabar hatte sicher nicht den Nethersturm im Hinterkopf, als er ihn entwickelt hat.$B$BSchaut nicht so berrascht drein, $N. Der Nethersturm ist harmlos im Vergleich zu einigen Orten, an denen ich gearbeitet habe. Doch eine kaputte Kuppel ist immer ein Grund zur Sorge.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10431, "deDE", "Seid Ihr die ganze Hilfe, die Orelis schickt? Er muss eine hohe Meinung von Euch haben.$B$BLasst es uns hinter uns bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10432, "deDE", "Nun? Was hatte Theledorn zu sagen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10432, "deDE", "Das ist eine groe Sache, $N. Sehr gro!$B$BVoren'thal mchte sicher so frh wie mglich davon erfahren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10433, "deDE", "<Shauly hebt die Stimme, um die Aufmerksamkeit der Astralen in der Nhe zu erregen.>$B$BHabt Ihr ein paar Pelze fr mich?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10433, "deDE", "<Shauly gibt vor, die Pelze zu bewundern.>$B$BSie sind wundervoll, wirklich wundervoll! Ich werde Euch den besten Preis dafr bezahlen! Was kosten mich Eure Dienste?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10434, "deDE", "<Ihr stellt Euch vor und sagt, dass Shauly Euch geschickt hat.>$B$BAh, ja, wir haben jemanden erwartet, der uns dabei hilft, h - ein paar, mh, Gter zu transportieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10435, "deDE", "Habt Ihr alles gesammelt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10435, "deDE", "Ausgezeichnete Arbeit! Der schwierigste Teil unseres Projekts ist damit erledigt. Jetzt mssen wir nur noch einen Weg finden, diese hier zurck zu Area 52 zu bringen und auch das Interesse der Expedition des Cenarius zu wecken. Es sollte hier genug geben, um unsere eigenen Interessen zu decken und noch ein wenig Gewinn zu machen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10436, "deDE", "Gut gemacht. Wie es aussieht, ist alles in Ordnung. Wir knnen anfangen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10437, "deDE", "Habt Ihr gengend Fragmente gesammelt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10437, "deDE", "Ausgezeichnet. Mit diesen Fragmenten knnen wir die Bombe einstellen, die die Leerenergieversorgung auf der Manaschmiede Ultris zerstren kann. Dann wird Dimensius' Verbindung zur Leere unterbrochen und er kann sich nicht mehr verteidigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10438, "deDE", "Es ist Zeit zuzuschlagen! Nun, da der Riss fort ist, kann Dimensius mit Kraft und Magie angegriffen werden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10439, "deDE", "Seit tausend Jahren kennen wir nur noch Krieg. Wir haben unser Volk gegen die Angriffe der Leerwesen und der Astralen verteidigt.$B$BJetzt ist einer unserer schlimmsten Feinde gefallen - endlich.$B$BIch berbringe Euch den Dank von Milliarden von Astralen, Fleischling.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10440, "deDE", "Ich setzte meine Techniker sofort auf die Reparaturen an.$B$BTashars Vermutung, dass es sich um Sabotage handelt, macht mir jedoch Sorgen. Ich kann mir nicht vorstellen, dass jemand waghalsig genug wre, unsere Technologie zu stehlen. Zumindest niemand, der wei, wozu wir fhig sind. Vielleicht ist es an der Zeit, die Sturmsule abzuriegeln.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10507, "deDE", "Ist es Erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10507, "deDE", "Ich verfgt ber unglaubliche Kraft, $N. Die Seher knnen sich glcklich schtzen, Euch als Verbndeten zu haben.$B$BWenn sich die Nachricht von Kaels neuem Bndnis verbreitet, werden mehr Leute die Sache wie wir sehen. Die Tatsache, dass wir einen solch entscheidenden Sieg ber Kael'thas errungen haben, bringt uns in eine uerst gnstige Lage.$B$BIch werde Eure Taten nicht vergessen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10508, "deDE", "Habt Ihr Socrethars Teleportationsstein schon besorgt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10508, "deDE", "Ihr erstaunt mich immer wieder, $N! Gut, dass Ihr auf unserer Seite steht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10509, "deDE", "Ihr habt keine Ahnung, was das fr die Seher bedeutet, $N. Ihr msst mich entschuldigen, whrend ich nach Atem ringe und die Situation einschtze.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10652, "deDE", "Willkommen, $C. Bemht Euch, unsere Position nicht zu verraten.$B$BWenn Kaels Spiegesellen uns entdecken, leben wir nicht mehr lange.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10653, "deDE", "Tragt Ihr irgendwelche Insignien des Feindes bei Euch, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10653, "deDE", "Sich den Dmonen der Brennenden Legion und den wahnsinnigen Kultisten, die sie verehren, zu stellen ist keine geringe Tat, $N. Ihr habt viel Hingabe und Mut bewiesen. Macht so weiter, und Euer Ruf bei den Aldor wird weiter steigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10654, "deDE", "Beweist Euch weiterhin als wrdig, $N. Besiegt die Feinde des Lichts, wo immer Ihr sie findet. Eure Taten sollen angemessen belohnt werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10655, "deDE", "Ihr beweist Euch weiterhin als wrdig, $N. Besiegt die Feinde des Lichts, wo immer Ihr sie findet. Eure Taten sollen angemessen belohnt werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10656, "deDE", "Los, $N. Sprecht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10656, "deDE", "Kaels Armee des Sonnenzorns ist sehr gut ausgebildet. Ihr habt beeindruckende Arbeit geleistet, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10658, "deDE", "Was habt Ihr in letzter Zeit fr uns getan, $N? Der Krieg gegen Kael'thas kmpft sich nicht von alleine, verstanden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10658, "deDE", "Ausgezeichnet! Das wird Kael'thas zeigen, dass unsere Macht nicht zu unterschtzen ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10659, "deDE", "Sogar der kleinste Beitrag zu unserer Sache wird bemerkt, $N. Unsere Feinde sind stark, aber wir werden siegen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10659, "deDE", "Kaels Armee wird bald fallen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10701, "deDE", "Oh! Oh... Ihr habt Netherbrock erledigt. Das sind gute Nachrichten!$B$BJetzt, da dieser lstige Bergriese von der Bildflche verschwunden ist, knnen wir die Rohstoffe der Schwindenden Weiten leichter ausbeut... h, verwalten.$B$BIch bin froh, dass diese armen, unschuldigen Elementare da unten jetzt nicht mehr von ihm zerquetscht werden.$B$BWie kann ich Euch je dafr entlohnen?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10704, "deDE", "<A'dals Stimme summt melodisch in Eurem Geist.>$B$BIhr seid zurck. Habt Ihr die beiden Fragmente des Schlssels zur Arkatraz bei Euch?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10704, "deDE", "Ich bin beeindruckt, $N, auch wenn ich von $Gdem Helden, der:der Heldin, die; den Kristall von Ata'mal zurckgebracht hat, nichts anderes erwartet habe.$B$BGebt mir die Fragmente, damit ich sie zu ihrer endgltigen Form zusammenfgen kann. Dann werdet Ihr Zugang zur Arkatraz haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10822, "deDE", "Sogar der kleinste Beitrag zu unserer Sache wird bemerkt, $N. Unsere Feinde sind stark, aber wir werden siegen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10822, "deDE", "Sieg fr die Seher! Kaels Streitmchte werden bald fallen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10823, "deDE", "Wie verluft der Kampf gegen Keals Streitmacht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10823, "deDE", "Ihr macht Euch einen Namen, $N. Weiter so!", 26972);
