INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10826, "deDE", "Sich den Dmonen der Brennenden Legion und den wahnsinnigen Kultisten, die sie verehren, zu stellen ist keine geringe Tat, $N. Ihr habt viel Hingabe und Mut bewiesen. Macht so weiter, und Euer Ruf bei den Aldor wird weiter steigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10827, "deDE", "Beweist Euch weiterhin als wrdig, $N. Besiegt die Feinde des Lichts, wo immer Ihr sie findet. Eure Taten sollen angemessen belohnt werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10828, "deDE", "Ihr beweist Euch weiterhin als wrdig, $N. Besiegt die Feinde des Lichts, wo immer Ihr sie findet. Eure Taten sollen angemessen belohnt werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10850, "deDE", "Als Ihr den falschen Treibstoff einfllt, beginnt der Motor des Teufelshschers zu rumpeln und zu stottern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10855, "deDE", "Das sind ausgezeichnete Nachrichten, $N. Jetzt, da wir wissen, wie wir mit ihnen zu verfahren haben, kann ich ein paar Leute anheuern, um die brigen Hscher zu sabotieren.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10856, "deDE", "Gute Arbeit, $N. Macht weiter so, dann werde ich beim Nexusprinzen ein gutes Wort fr Euch einlegen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10857, "deDE", "Ist es vollbracht? Sind die Teleporter auer Gefecht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10857, "deDE", "Dank Euch kann ich wieder etwas durchatmen, $N. Das meine ich natrlich im bertragenen Sinne!$B$BIch nehme an, Ihr wollt nun eine Art Belohnung, oder?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10882, "deDE", "<Das kristallene Klingen von A'dals Stimme fliet in einer deutlich ruhigeren Melodie als die letzten beiden Male, in denen Ihr miteinander gesprochen habt.>$B$BWieder einmal habt Ihr Euren Eifer bewiesen. Wenn Herold Horizontiss ausgebrochen wre, wren Tausende dem Konflikt zum Opfer gefallen, genauso, wie es bei einem anderen Diener der alten Gtter, Skeram, auf Eurer Welt Azeroth geschehen ist.$B$B$N, was ich Euch als Belohnung anbiete, scheint im Vergleich erbrmlich zu sein, aber seid Euch bewusst, dass die ewige Dankbarkeit der Sha'tar damit verbunden ist.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10924, "deDE", "War Zeppit freundlich genug, das Blut fr Euch zusammeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10924, "deDE", "Das sollte gengend Vorrat fr meine Zwecke sein.$B$BIch hoffe, der Wichtel hat Euch nicht allzu viel rger gemacht. Er hat manchmal Haare auf den Zhnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10969, "deDE", "Ja, das stimmt. Doch das Konsortium ist nicht die einzige Gruppierung, die Verluste zu vermelden hat.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10970, "deDE", "Hattet Ihr Glck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10970, "deDE", "Tretet zurck, $N. Es knnte auch eine Falle des Astraleums sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10971, "deDE", "Habt Ihr ein paar Identifizierungsmarken finden knnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10971, "deDE", "Es scheint sich hierbei um mehr als eine einfache Identifizierungsmarke zu handeln. Auf den Marken ist eine Art Code zu sehen. Ich stehe kurz davor, ihn zu entschlsseln. Wenn ich doch nur mehr Marken htte!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10972, "deDE", "Ich brauche mehr Identifikationsmarken, $N. Ich konnte bisher herausfinden, dass sie nicht nur die Gefangenen katalogisieren, sondern auch Aussagen ber ihre Aufenthaltsorte machen!$B$BSobald wir diesen Code entschlsselt haben, knnen wir genau feststellen, wo unsere Verbndeten festgehalten werden und sie mit minimalem Gewalteinsatz befreien.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10972, "deDE", "Wir sind der Wahrheit auf der Spur, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10973, "deDE", "<Ameer fgt die Schlssel geschickt zusammen und betrachtet die Markierungen auf der so hergestellten Schlsseltafel.>$B$B\"Tausend Welten zerschlagen, tausend weitere noch zu bezwingen... So lautet der Wille des Nexusknigs.\"$B$B<Ameer dreht die Tafel um und liest die Rckseite.>$B$BStasiskammern von Bash'ir?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10975, "deDE", "Wir sitzen in der Sackgasse, $N. Die Daten, die Ihr gesammelt habt, haben uns gengend Informationen geliefert, um unsere Verbndeten aus den kleineren Gefngnissen des Nethersturms retten zu knnen. Wir mssen uns jedoch dringend um die Bedrohung kmmern, die von den Kammern hier im Ladeplatz von Bash'ir ausgeht.$B$BSchliet Euch dem Kampf gegen das Astraleum an! Bringt mir die Gefngnisschlssel des Astraleums, damit ich Stasiskammerschlssel fr die Stasiskammern von Bash'ir fr Euch herstellen kann. ", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10975, "deDE", "Es gibt immer noch mehr Arbeit, die erledigt werden muss, $N. Begebt Euch nun auf die Suche nach einer Stasiskammer und vernichtet, was auch immer darin lauern mag!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10977, "deDE", "Ich knnte verstehen, wenn Ihr lieber nicht weitermachen wollt, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10977, "deDE", "Ich habe von Euren Heldentaten erfahren, $N. Haramad und ich sind uns beide einig, dass Ihr dafr eine groartige Belohnung verdient habt.$B$BObwohl... Ach, Ihr habt schon genug getan. Ich kann Euch nicht guten Gewissens um noch mehr bitten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11036, "deDE", "Ist das die Lieferung vom alten Orok?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11036, "deDE", "<Der Raketenchef berprft den Inhalt der Kiste.>$B$BSieht soweit gut aus. Wieder etwas, das ich von der Liste streichen kann. Nun mssen wir noch nur dafr sorgen, dass die Rakete vom Boden abhebt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11037, "deDE", "<Der Raketenchef hrt Euch aufmerksam zu, als Ihr ihm von der Vision des Sehers erzhlt.>$B$BNun, das ist alles vllig neu fr mich, aber Eure Hilfe knnen wir allemal gebrauchen. Seht Euch die Rakete hinter mir an! Das ist die X-52...$B$BIst sie nicht das Schnste, was Ihr je gesehen habt?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11040, "deDE", "Diese Kiste sieht aus, als stamme sie von einem Goblinmeisteringenieur. Hat Bossi Euch geschickt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11040, "deDE", "Ich wei nicht einmal, was einige dieser Teile bewirken! Einige von ihnen werden mir sicherlich sehr nutzen. Ich htte niemals gedacht, dass ich mal lernen msste, wie ein Gnom zu denken!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11042, "deDE", "<Der Raketenchef hrt Euren Ausfhrungen ber die Vision des Sehers aufmerksam zu.>$B$BNun, das ist alles vllig neu fr mich, aber Eure Hilfe knnen wir allemal gebrauchen. Seht Euch das Ungetm hinter mir an. Das ist die X-52...$B$BIst sie nicht das Schnste, was Ihr je gesehen habt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10451, "deDE", "Gelobt sei die Erdenmutter! Ich bin mir sicher, dass Wilda nach Hause finden wird. Bitte nehmt dies als Zeichen unserer Wertschtzung.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10458, "deDE", "Ich frchte, dass es keine Hoffnung fr dieses Land gibt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10458, "deDE", "Wenn alle Elementarseelen dieser Gegend in diesem Totem eingefangen sind, werden wir mit ihnen reden und herausfinden, ob wir etwas fr das Schattenmondtal tun knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10480, "deDE", "Wo auch immer Ihr die Naga findet, knnt Ihr sicher auch die gequlten Wassergeister finden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10480, "deDE", "Und zurck bleibt nichts als Luft, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10481, "deDE", "Bald werden wir mit den Geistern sprechen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10481, "deDE", "Erlaubt mir, dass ich Euch fr diese wagemutige Tat belohne, Held.$B$BJetzt mssen wir uns an die Arbeit machen! Bitte tretet zurck, damit ich das Totem aufstellen und die Seelen freilassen kann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10513, "deDE", "Litanei der Verdammnis? Nie davon gehrt. Hrt auf meinen Rat und lasst die Toten ruhen.$B$BAber wenn Ihr schon hier seid, knnt Ihr mir ja gleich bei ein paar Dingen helfen. Was meint Ihr?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10514, "deDE", "Holt mir diese Knollen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10514, "deDE", "Ihr sagt, da waren viel mehr Felshetzer, als Ihr erwartet habt? Sieht aus, als wre es an der Zeit, die Situation etwas zu entschrfen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10515, "deDE", "Das wird den verdammten, bsartigen Tieren zeigen, dass man meine Eber nicht tten sollte! Gut gemacht, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10519, "deDE", "Versteht Ihr, was getan werden muss? Findet die Shne Oronoks. Findet meine Jungs...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10521, "deDE", "<Grom'tor grunzt.>$B$BGut, dass Ihr hier seid. Diese Made hat gerade geplaudert. Jetzt haben wir sie!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10522, "deDE", "Hattet Ihr Glck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10522, "deDE", "Das msst Ihr sofort zurck zu Vater bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10523, "deDE", "Hattet Ihr Erfolg?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10523, "deDE", "Wenn die Litanei der Verdammnis wieder zusammengefgt ist, werden wir diesen Fluch beenden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10527, "deDE", "Ar'tors Krper hngt leblos in der Luft. Ihr wisst nicht, wie lange er schon tot ist, aber Euch berkommt groe Traurigkeit fr Oronok.$B$BVielleicht solltet Ihr versuchen, seine Leiche zu seinem Vater zu bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10528, "deDE", "Ar'tor schwebt leblos vor Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10528, "deDE", "Ihr steckt den Schlssel in die ffnungen im unteren Teil jedes dmonischen Kristalls.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10537, "deDE", "Frchtet Euch nicht, Held. Wenn die Zeit gekommen ist, werde ich Euch helfen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10537, "deDE", "Der Name bedeutet in meiner Sprache 'Heldenruh'. Und auch jetzt, in einer Gegend, in der all unsere Helden gestorben sind, bewahrheitet er sich.$B$BUnd doch scheint einer sich immer wieder zu erheben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10540, "deDE", "Die Litanei muss gefunden werden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10540, "deDE", "<Ar'tors Silhouette wird unscharf und wieder schrfer.>$B$BEs scheint, als wre meine Zeit bald zu Ende, Held.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10541, "deDE", "<Eine einzelne Trne luft ber Oronoks Wange.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10541, "deDE", "<Oronok wischt sich die Trne aus dem Auge und lchelt Euch an.>$B$BDanke. Ich verspreche Euch, Held, dass ein ehrenhaftes Lied ber Euch erklingen wird.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10546, "deDE", "<Borak ist fr einen Moment still.>$B$BIch beobachte diese Blutelfen schon seit Wochen und versuche herauszufinden, wo sie den dritten Teil der Litanei der Verdammnis aufbewahren.$B$BSo weit habe ich jedoch keine brauchbaren Hinweise sammeln knnen. Ich wei nur, dass Illidan einmal am Tag einen Entsandten vom Schwarzen Tempel herschickt.$B$BDieser Entsandte ist unantastbar. Ich habe ihn eine Woche lang berwacht und nach einem Weg gesucht, ihn von seiner Leibwache zu trennen. Die Leibwache verlsst jedoch unglcklicherweise nie seine Seite.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10547, "deDE", "<Tobias hebt eine Augenbraue.>$B$BBlutdistel? Nie davon gehrt... Seid Ihr ein Friedensbewahrer? Ihr wisst, dass Ihr mir sagen msst, wenn Ihr einer seid... Ich habe schlielich Rechte!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10547, "deDE", "Ihr habt ein verfaultes Arakkoaei? Nun, warum habt Ihr das denn nicht gleich gesagt? Ich denke, wir sind im Geschft.$B$B<Tobias lchelt und entblt dabei eine Reihe verfaulter und vergilbter Zhne.>", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10550, "deDE", "Habt Ihr die Blutdisteln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10550, "deDE", "Jetzt mssen wir die Falle stellen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10562, "deDE", "Danke, dass Ihr meine Mnner untersttzt habt, $N. Mit Eurer Hilfe und der Hilfe anderer, knnen wir vielleicht noch lnger durchhalten. Unglcklicherweise ist Durchhalten leider nicht genug. Wir mssen zum Angriff bergehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10563, "deDE", "Was habt Ihr herausgefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10563, "deDE", "<Schwadronskommandant Nuainn hrt sich Euren Bericht an und wird mit jedem Satz nervser.>$B$BIhr sagt also, dass sie gengend Hllenbestien haben, um die Angriffe noch zu verstrken? Wenn wir sie nicht aufhalten, bevor sie die nchste Phase ihres Plans umsetzen knnen, ist die Wildhammerfeste verloren!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10564, "deDE", "Habt Ihr einen Weg gefunden, um den Vorrat an Hllenbestien der Legion zu zerstren?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10564, "deDE", "Gut gemacht, $N! Ich wnschte, ich knnte behaupten, dass die Legion nach einem Rckschlag wie diesem aufgeben wird, aber das wrdet Ihr mir ohnehin nicht glauben. Die Todesschmiede, von der Ihr erfahren habt, wird unser nchstes Ziel sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10568, "deDE", "Habt Ihr die Schrifttafeln schon gefunden, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10568, "deDE", "Die Schrifttafeln von Baa'ri! Ja, die werden uns sehr wichtige Informationen liefern.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10569, "deDE", "Was habt Ihr entdeckt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10569, "deDE", "<Kieran wirft einen kurzen Blick auf die Tagebuchseiten.>$B$BVieles davon ist unleserlich, aber ich kann hier und da ein paar Worte verstehen. Es geht um eine fehlgeschlagene Vorbereitung eines Zaubers, Opfer und eine sich verschlechternde Situation. Hier steht auch irgendetwas Merkwrdiges ber einen 'Deserteur'.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10570, "deDE", "Beeilt Euch, Junge!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10570, "deDE", "<Borak nimmt den Schriftwechsel und beginnt zu lesen.>$B$BGerissene Hunde! Dies ist eine Anordnung von Illidan, die besagt, wo die Litanei der Verdammnis als Nchstes versteckt werden soll. Offensichtlich ndern sie den Aufenthaltsort stndig. Aber wir wissen leider nicht, wo sie sich im Augenblick befindet... Wenn wir diesen Brief doch nur abliefern knnten, jetzt, da wir wissen, wo sie die Litanei verstecken werden.$B$BWie schade, dass das Siegel nun zerbrochen und der Brief damit nutzlos ist.$B$BHmm... Es sei denn...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10571, "deDE", "Gibt es schon Neuigkeiten, $N? Wir haben nicht viel Zeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10571, "deDE", "Akamas Handschrift. Es ist wirklich sehr traurig, was aus jemandem werden konnte, der so weise und beliebt war wie er.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10572, "deDE", "<Ihr reicht dem Schwadronskommandanten die Materialien.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10572, "deDE", "<Der Schwadronskommandant setzt den Kraftkern in die Rstplatten und versiegelt nickend das Gert.>$B$BDas sollte klappen. Und es sollte auch eine ziemlich groe Explosion von sich geben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10573, "deDE", "<Der Zwerg nickt.>$B$BVerstrkung ist gut. Wir haben bisher grundlegende Informationen ber die Plne des Schattenrats hier gesammelt, aber ich wre sehr froh, wenn wir endlich gegen sie vorgehen knnten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10574, "deDE", "Habt Ihr die vier Fragmente des Medaillons, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10574, "deDE", "Ja! Die vier Fragmente des Medaillons. Und nun setzt sie zusammen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10575, "deDE", "Wir haben Euch erwartet, $N. Akama sagte, dass Ihr frher oder spter kommen wrdet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10576, "deDE", "Das ist viel zu kompliziert, $R. Ihr wrdet es nicht verstehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10576, "deDE", "Gut gemacht, $N. Ich hoffe, Ihr seid ein guter Schauspieler. Der nchste Schritt erfordert etwas Theatralik!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10577, "deDE", "Jetzt msst Ihr nur noch die Litanei besorgen. Einfach, oder?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10578, "deDE", "Ihr msst schnell handeln!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10578, "deDE", "Endlich! Das Fragment der Litanei gehrt uns!$B$B<Borak verstaut das Fragment in einer Schliekassette.>", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10579, "deDE", "$N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10579, "deDE", "Sobald alle drei Teile der Litanei zusammengefgt sind, wird es wieder bekannt.$B$BDieses Dokument zu besorgen war zweifellos eine groe Herausforderung. Fr einen solchen Heldenmut msst Ihr belohnt werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10582, "deDE", "Ich wnschte, ich htte mit Euch gehen knnen! Ich brenne schon die ganze Zeit darauf, meinen Hammer benutzen zu knnen, aber mein Auftrag ist es, zu beobachten, nicht zu tten.$B$BJetzt, da Ihr ein Loch in ihre Verteidigung geschlagen habt, lasst uns die Produktion in der Todesschmiede zum Stillstand bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10583, "deDE", "Habt Ihr ein Zeichen von Flanis in der Todesschmiede finden knnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10583, "deDE", "<Ewan schttelt den Kopf und nimmt Euch den Beutel ab.>$B$BIch glaube nicht, dass Flanis sich im Klaren darber war, wie schwer die Todesschmiede bewacht ist. Jetzt, da wir wissen, was tatschlich dort vor sich geht, mache ich ihnen keinen Vorwurf. Ich wnschte nur, dass Flanis es gewusst htte, als er dort hinein gegangen ist.$B$BIch kann nur hoffen, dass irgendetwas in diesem Beutel uns dabei helfen kann, die Todesschmiede ein fr alle Mal auszuschalten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10585, "deDE", "Wir knnen nicht zulassen, dass sie weiter produzieren. Habt Ihr dem Beschwrungsritual ein Ende gesetzt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10585, "deDE", "Gut gemacht, $N. Die Beschwrung des Schattenrats zu unterbrechen bringt uns einen Schritt nher an die Abschaltung der Todesschmiede.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10586, "deDE", "Bringt Ihr Neuigkeiten aus der Todesschmiede?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10586, "deDE", "<Ihr erstattet dem Schwadronskommandanten Bericht und bergebt die gefundenen Befehle.>$B$BKriegshetzer Razuun zu besiegen sollte eigentlich das Ende unserer Probleme mit der Legion bedeuten, aber in diesen Befehlen steht deutlich mehr, als ich befrchtet hatte. Ich glaube, wir haben noch viel zu tun.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10587, "deDE", "Habt Ihr die Aufgabe, die ich Euch gegeben habe, schon erledigt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10587, "deDE", "Wollen wir uns diese Waffen einmal genauer ansehen, $N. Hmmm... das ist ziemlich beunruhigend.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10588, "deDE", "Ist es vollbracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10588, "deDE", "<Erdheiler Torlok nimmt Euch die Litanei der Verdammnis ab.>$B$BIhr habt getan, worum Euch die Elemente gebeten haben. Dafr sind wir sehr dankbar. Ihr werdet belohnt werden, doch der rger lauert noch immer auf uns. Die Geister des Feuers sind in Aufruhr. Es wurde eine Forderung vorgebracht. Ein weiterer kennt die Litanei der Verdammnis.$B$BIch wei, das klingt sehr kryptisch, aber das ist alles, was die Geister mir mitgeteilt haben. Das und dieses Symbol...$B$B<Erdheiler Torlok dreht sich um und zeigt gen Himmel.>$B$BWas knnte das wohl bedeuten?", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10589, "deDE", "Wir mssen einen Weg finden, um diese Hllenbestien zu vernichten, $N. Wir sind schon weit gekommen, aber ein Fehlschlag wrde uns alles kosten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10589, "deDE", "Lasst mich schauen, ob ich herausfinden kann, wie das funktioniert...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10595, "deDE", "Ich bin beeindruckt, dass Ihr dieses Gemetzel berlebt habt. Ihr seid vielleicht genau die Person, die wir fr einen gefhrlichen Auftrag brauchen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10596, "deDE", "Was habt Ihr herausgefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10596, "deDE", "<Blutwache Gulmok hrt sich Euren Bericht an und wird mit jedem Satz nervser.>$B$BWas meint Ihr mit sie wollen ihre Angriffe noch verstrken? Das knnen wir nicht zulassen. Kehrt dorthin zurck und kmmert Euch darum, solange in Schattenmond noch ein Stein auf dem anderen steht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10597, "deDE", "<Ihr reicht Gulmok die Materialien.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10597, "deDE", "<Gulmok platziert den Kraftkern in dem Gehuse und versiegelt es.>$B$BDas sollte funktionieren. Ich mchte nicht in der Feste sein, wenn das Ding hochgeht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10598, "deDE", "Habt Ihr einen Weg gefunden, um den Vorrat an Hllenbestien der Legion zu zerstren?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10598, "deDE", "<Gulmok nickt grimmig.>$B$BWir haben keine Zeit zu feiern, $N. Noch sind wir nicht aus der Schusslinie. Ich habe neue Befehle fr Euch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10599, "deDE", "<Der Orc nickt.>$B$BVerstrkung ist gut. Wir haben zwar schon grundlegende Informationen darber gesammelt, was der Schattenrat dort drin vorhat, doch es ist Zeit zuzuschlagen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10600, "deDE", "Ich wre ja selbst dorthin gegangen und htte ein paar Schdel zertrmmert, aber ich habe strengste Anweisungen, mich nicht auf einen direkten Kampf mit dem Feind einzulassen.$B$B<Der Spher grummelt und imitiert Blutwache Gulmoks Stimme.>$B$BLasst uns die Produktion in der Schmiede zum Stillstand bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10601, "deDE", "Habt Ihr ein Zeichen von Kagrosh in der Todesschmiede finden knnen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10601, "deDE", "<Zagran schttelt den Kopf und nimmt Euch den Beutel ab.>$B$BIch habe Euch doch gesagt, dass er ein Dummkopf ist. Ich hoffe, dass in diesem Beutel irgendetwas ist, die Zeit, die Ihr mit der Suche nach ihm verbracht habt, rechtfertigt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10602, "deDE", "Wir knnen nicht zulassen, dass sie weiter produzieren. Habt Ihr dem Beschwrungsritual ein Ende gesetzt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10602, "deDE", "Gut. Wir machen Fortschritte. Das bringt uns einen Schritt nher an die Abschaltung diese Monstrositt. Bald knnen wir sicher nach Schattenmond zurckreisen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10603, "deDE", "Bringt Ihr Neuigkeiten aus der Todesschmiede?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10603, "deDE", "<Ihr erstattet der Blutwache Bericht und bergebt die gefundenen Befehle.>$B$BGute Arbeit, $N, aber denkt nicht, dass Ihr dafr einen Orden verliehen bekommt. Die Nachtelfen mgen vielleicht ihre hbschen kleinen Uniformen dekorieren. Echte Krieger teilen jedoch ihre Geschichten von der Schlacht bei einem khlen Krug Bier.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10604, "deDE", "Die Anwesenheit der Legion in unserer Mitte ist nicht akzeptabel! Wir mssen einen Weg finden, damit fertigzuwerden, bevor sie ihre Angriffe erneuern knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10604, "deDE", "Endlich haben wir ein Mittel, mit dem wir die Legion aus Schattenmond vertreiben knnen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10606, "deDE", "Habt Ihr das Handbuch?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10606, "deDE", "Ah, genau was wir brauchen. Die Legion ist zwar nicht fr ihre Meisterwerke der Weltliteratur bekannt, aber es sollte seinen Zweck erfllen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10611, "deDE", "Habt Ihr das Handbuch?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10611, "deDE", "Das enthlt alle ntigen Informationen. Nur noch ein paar Handgriffe, und Ihr habt in null Komma nichts die Kontrolle ber einen Teufelshscher.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10612, "deDE", "Erstaunlich! Ihr habt geschafft, was keiner der Greifenreiter der Wildhammerfeste bewerkstelligen konnte! Ihr habt den Vormarsch der Brennenden Legion aufgehalten und uns vor der Vernichtung durch diese schrecklichen Hllenbestien gerettet. Ihr habt unseren tiefsten Dank fr Eure Dienste, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10613, "deDE", "Ich gebe zu, $N, ich bin von Euren Errungenschaften beeindruckt. Jetzt, da die Legion nicht lnger die Mittel hat, uns in Schattenmond anzugreifen, knnen wir uns anderen Fronten zuwenden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10619, "deDE", "Ehre den Naaru, $N. Ihr habt den Willen des Lichts ausgefhrt und Illidans Anhnger vernichtet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10621, "deDE", "Was habt Ihr da?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10621, "deDE", "<Ordinn nimmt Euch den Splitter der Waffe ab.>$B$BSo etwas habe schon seit langer Zeit nicht mehr gesehen. Die Waffe wurde aus einem sehr seltenen Erz geschmiedet, aber es handelt sich dabei nicht um eine gewhnliche Klinge, $N. Sie ist mit einer Magie erfllt, die Dmonen tten soll. Wenn Ihr sie von der Legion habt, muss sie eine Art 'Geheimwaffe' sein, die sie gegen Illidan einsetzen wollen. Welch erstaunlicher Fund!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10622, "deDE", "Sehr gut, $N. Eure Gesinnung steht nun auer Frage. Ihr drft jetzt zu Akama.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10623, "deDE", "Was habt Ihr da?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10623, "deDE", "<Grokom nimmt Euch den Splitter der Waffe ab.>$B$BSo etwas habe schon seit langer Zeit nicht mehr gesehen. Die Waffe wurde aus einem sehr seltenen Erz geschmiedet, aber es handelt sich dabei nicht um eine gewhnliche Klinge, $N.$B$BDiese Runen erfllen die Klinge mit der Macht, Dmonen zu vernichten. Wenn die Legion ihre eigenen Truppen mit Dmonen zerstrenden Waffen ausrstet, muss ihr Hass auf Illidan unendlich gro sein. Das knnten wir zu unserem Vorteil ausnutzen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10624, "deDE", "Was meint Ihr? Ich werde natrlich etwas aus der Asche herstellen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10624, "deDE", "Eure Welt wird sich bald ndern, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10625, "deDE", "Wenn Ihr Eure Brille verliert, kann ich Euch eine neue herstellen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10625, "deDE", "Blutschatten? T... Teron Blutschatten? Aber... Wie?$B$BWie kann Blutschatten denn hier sein? Das ist nicht mglich!$B$BDie Legende von Teron Blutschatten lsst sogar die Verlassenen vor Angst erzittern. Nach dem, was man hrt, war Blutschatten ein $C Gul'dans und sehr mchtig. Als Blutschatten von Orgrim Schicksalhammer erschlagen wurde, nahm Gul'dan die Seele des gefallenen Hexenmeisters und pflanzte sie in die zerschlagene Hlle eines gefallenen Ritters von Sturmwind. Und so wurde der erste Todesritter der Horde geboren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10626, "deDE", "Habt Ihr die Prototypen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10626, "deDE", "Sie sind perfekt. Jetzt wollen wir sie ein eine etwas praktischere Form bringen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10627, "deDE", "Habt Ihr die Prototypen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10627, "deDE", "Sie sind perfekt. Jetzt wollen wir sie ein eine etwas praktischere Form bringen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10628, "deDE", "Seid gegrt, $N. Wir haben viel zu besprechen.$B$BIch hoffe, Euer Wesen ist aufgeschlossen. Nicht viele Leute verstehen meine Entscheidungen. Nur meine engsten Mitarbeiter wissen von den bsen Omen, die ich sehe, und den Zeichen, auf die ich warte.$B$BAls ich mich mit Illidan zusammenschloss, geschah dies, weil ich vorausgesehen hatte, dass mein Volk sonst vernichtet wrde. Ich habe geduldig auf die anderen Zeichen und Visionen gewartet... Velens Auszug... die ffnung des Dunklen Portals... und nun... Eure Ankunft.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10633, "deDE", "Blutschatten? Diesen Namen habe ich schon seit ewigen Zeiten nicht mehr gehrt!$B$BNach dem Zweiten Krieg kehrte Blutschatten mit seinen Todesrittern hierher zurck. Als er nicht mit der Schrecklichkeit seiner neuen Form zurechtkam und ohne die Hilfe seines geliebten Anfhrers, Gul'dan, hilflos war, nahm Blutschatten sich das Leben.$B$BIch wei nichts davon, dass er nun als Geist in diesen Weiten herumwandeln soll. Aber vielleicht kann ich das mit Eurer Hilfe herausfinden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10634, "deDE", "Fliegt zur Netherschwingenscherbe und beschafft die Rstung.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10634, "deDE", "Ich muss alle Gegenstnde besitzen, bevor ich Blutschattens Schicksal mit Hellsicht herausfinden kann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10635, "deDE", "Ihr msst die Geisterbrille benutzen, um die Geister von Schattenmond sehen zu knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10635, "deDE", "Wenn ich alle drei Gegenstnde in meinem Besitz habe, kann ich mit Hellsicht versuchen, den Aufenthaltsort von Blutschatten zu bestimmen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10636, "deDE", "Ihr msst die Geisterbrille benutzen, um die Geister von Schattenmond sehen zu knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10636, "deDE", "Nur mit allen drei Gegenstnden zusammen kann ich versuchen, die Geheimnisse, die Ihr zu lften wnscht, hellzusehen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10637, "deDE", "Wollen wir hoffen, dass diese Ablenkung uns die Zeit verschafft, die wir brauchen, um alles herauszufinden, was wir ber die Ausbildungsgelnde von Karabor wissen mssen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10639, "deDE", "Was habt Ihr getan? DUMMKOPF!$B$BIhr msst dieses Unrecht wieder gutmachen. Schlielich war es Euer Fehler. Nehmt einen dieser Helme. Sie sind mit der Macht der ewig brennenden Asche, die Ihr mir gebracht habt, erfllt. Viel wichtiger ist jedoch, dass Ihr damit die Geister Schattenmonds sehen knnt, auch Teron Blutschatten.$B$BWenn Ihr ihn jemals findet, vernichtet ihn.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10642, "deDE", "Ja, es besteht eine Dualitt... Ich kann es fhlen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10642, "deDE", "<Zorus beginnt damit, etwas aus den Aschenproben herzustellen.>$B$BEs ist vollbracht. Seht her, eine Geisterbrille!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10643, "deDE", "Wenn Ihr Eure Brille verliert, kann ich Euch eine neue herstellen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10643, "deDE", "Ihr sagt, Ihr habt die Geister zu Euch flstern hren? Und sie sprachen ber Teron Blutschatten?$B$B<Zorus schaudert.>$B$BJa... Ich kenne Teron Blutschatten. Oder besser gesagt kannte ihn. Er ist schon vor langer Zeit gestorben. Wovon knnen diese Geister wohl gesprochen haben?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10644, "deDE", "Blutschatten? Diesen Namen habe ich schon seit ewigen Zeiten nicht mehr gehrt!$B$BNach dem Zweiten Krieg kehrte Blutschatten mit seinen Todesrittern hierher zurck. Als er nicht mit der Schrecklichkeit seiner neuen Form zurechtkam und ohne die Hilfe seines geliebten Anfhrers, Gul'dan, hilflos war, nahm Blutschatten sich das Leben.$B$BIch wei nichts davon, dass er nun als Geist in diesen Weiten herumwandeln soll. Aber vielleicht kann ich das mit Eurer Hilfe herausfinden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10645, "deDE", "Was habt Ihr getan? DUMMKOPF!$B$BIhr msst dieses Unrecht wieder gutmachen. Schlielich war es Euer Fehler. Nehmt einen dieser Helme. Sie sind mit der Macht der ewig brennenden Asche, die Ihr mir gebracht habt, erfllt. Viel wichtiger ist jedoch, dass Ihr damit die Geister Schattenmonds sehen knnt, auch Teron Blutschatten.$B$BWenn Ihr ihn jemals findet, vernichtet ihn.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10647, "deDE", "Willst du die Belohnung kassieren, Mann?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10647, "deDE", "Du bist echt der Brller, was? Gut gemacht! Und du kriegst auch deine Belohnung. Is' doch Ehrensache...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10648, "deDE", "Ich hoffe, Ihr habt etwas Groartiges getan, Mdel. Ansonsten macht Euch vom Acker!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10648, "deDE", "Hier ist Eure Belohnung. Gebt nicht alles auf einmal aus!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10649, "deDE", "Habt Ihr das Buch besorgt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10649, "deDE", "Ich konnte die Macht des Buches schon ber Meilen hinweg spren. Die Versuchung, es einfach zu behalten ist... sehr gro.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10650, "deDE", "Ihr bringt diese Abscheulichkeit eines Buches an einen heiligen Ort? Ich hoffe, dass Ihr dafr eine gute Erklrung habt, $N!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10651, "deDE", "Habt Ihr erledigt, worum ich Euch gebeten hatte, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10651, "deDE", "Ausgezeichnete Arbeit, $N! Euer Sieg ber Varedis ist ein entscheidender Schritt in unserem Krieg gegen Illidan.$B$BIhr habt Euch nicht nur den Aldor, sondern gegenber der gesamten Scherbenwelt als wrdig erwiesen. Bitte gestattet mir, die berreste dieses schrecklichen Buchs zu vernichten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10660, "deDE", "Milzen! Ich brauche Milzen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10660, "deDE", "<Forscher Tiorus nimmt die Milzen an sich und beginnt seine Untersuchung.>$B$BDas drfte eine Weile dauern. Nehmt dies als Bezahlung fr Eure harte Arbeit.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10661, "deDE", "Milzen! Ich brauche Milzen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10661, "deDE", "<Gnomus reit die Milzen hungrig an sich.>$B$BHier ist die versprochene Bezahlung. Ich rufe Euch, wenn ich noch mehr Milzen brauche.$B$BSo lecker... Ich wrde fast meinen 'demilzis'!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10662, "deDE", "Was fhrt Euch hierher, $C? Ich bekomme nicht viel Besuch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10662, "deDE", "Ordinn hat Euch damit zu mir geschickt?$B$B<Der Schmied untersucht die Barren.>$B$BEr hatte Recht. Der Groteil der Magie ist verschwunden, aber ich kann noch immer Spuren davon in dem Metall spren. Mit ein wenig Arbeit und ein paar 'ausgefallenen' Materialien kann ich Euch ein mchtiges antidmonisches Schwert aus diesem Metall schmieden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10663, "deDE", "Was fhrt Euch hierher, $C? Ich bekomme nicht viel Besuch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10663, "deDE", "Ordinn hat Euch damit zu mir geschickt?$B$B<Der Schmied untersucht die Barren.>$B$BIch werde ehrlich mit Euch sein, $C. Ich bin in einer Zeit grogeworfen, in der die Kooperation mit der Horde undenkbar war.$B$BIch bin nicht so blind, dass ich nicht sehen wrde, dass wir keine gemeinsamen Feinde haben. Ich werde Euch helfen, die antidmonische Waffe umzuschmieden, aber es wird einiges an Arbeit bedrfen und ein paar 'ausgefallene' Materialien.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10664, "deDE", "Habt Ihr die Materialien?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10664, "deDE", "<Der Schmied nimmt Euch die Materialien ab und nickt zufrieden.>$B$BDie werden reichen. Der Rest der Materialien, die ich bentige, wird jedoch nicht so leicht zu bekommen sein.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10665, "deDE", "Konntet Ihr eine Manazelle aus der Mechanar schmuggeln?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10665, "deDE", "Sie ist sogar noch mchtiger, als ich erwartet habe! Mit so viel Energie knnte ich ein Dutzend Klingen schmieden!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10666, "deDE", "Habt Ihr das Buch besorgt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10666, "deDE", "<David blttert die Seiten des Buchs durch und schaudert.>$B$BDas ist nichts, mit dem man spielen sollte, $N. Sobald die Waffe fertig ist, werde ich es zerstren. Es gibt schon genug dmonische Macht in dieser Welt.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10667, "deDE", "Habt Ihr den Unterweltlehm bekommen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10667, "deDE", "Das sollte funktionieren. Ich hatte zwar auf etwas mehr gehofft, aber um ehrlich zu sein knnen wir uns glcklich schtzen, hier drauen berhaupt etwas davon in die Finger bekommen zu haben.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10670, "deDE", "Konntet Ihr den Edelstein O'mroggs Klauen entreien?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10670, "deDE", "<David Wayne hlt den Edelstein gegen das Licht und bewundert ihn.>$B$BEr ist absolut makellos und genau das, was wir brauchen. Ein fehlerhafter Edelstein wrde den Trger der Waffe in Gefahr bringen und die Magie gegen ihn richten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10672, "deDE", "Wir haben keine Zeit zu verschwenden. Ich habe die markierten Dimetrodonten beobachtet und herausgefunden, dass sie alle einem riesigen, nein, GIGANTISCHEN Dimetrodon namens Teufelsflosse dem Groen unterstellt sind.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10673, "deDE", "Kommt in die Gnge! Der Appetit dieses Gnoms kennt keine Grenzen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10673, "deDE", "Endlich bin ich vollkommen vor der Lava geschtzt! Natrlich knnt auch Ihr etwas von dieser Technologie abhaben, $N. Ihr habt es Euch verdient!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10676, "deDE", "<David wischt sich den Schwei von der Braue.>$B$BDer grte Teil der Arbeit ist getan. Um die Magie jedoch zum Leben zu erwecken, muss das Schwert erst gehrtet werden.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10677, "deDE", "Tagesmen: Muskelmagen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10677, "deDE", "Das ist fantastisch! Aber ich habe gerade unglaubliche Nachrichten erhalten!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10678, "deDE", "Tagesmen: Teufelsflosses Balg", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10678, "deDE", "Ich brauche jetzt etwas Zeit fr mich, $N. Vielen Dank fr Eure Hilfe!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10679, "deDE", "Habt Ihr das Schwert in der Lava der Hand von Gul'dan gehrtet?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10679, "deDE", "Gut gemacht!$B$B<Der Schmied untersucht die Klinge sehr genau.>$B$BIch wrde sagen, dass sie deutlich besser ist, als die grobe Waffe der Legion, mit der wir angefangen haben. Diese Klinge wird nicht nur die Dmonen der Illidari erschlagen, sondern auch die Diener der Legion vernichten knnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10680, "deDE", "Es ist gut, dass Ihr gekommen seid, $R. Wir haben viel zu tun.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10681, "deDE", "Es ist gut, dass Ihr gekommen seid, $R. Wir haben viel zu tun.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10683, "deDE", "Habt Ihr die Schrifttafeln schon gefunden, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10683, "deDE", "Die Schrifttafeln von Baa'ri! Ja, die werden uns sehr wichtige Informationen liefern.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10684, "deDE", "Gibt es schon Neuigkeiten, $N? Wir haben nicht viel Zeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10684, "deDE", "Akamas Handschrift... wollen wir mal sehen, was wir daraus erfahren knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10685, "deDE", "Habt Ihr die vier Fragmente des Medaillons, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10685, "deDE", "Ja! Die vier Fragmente des Medaillons. Und nun setzt sie zusammen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10686, "deDE", "Wir haben Euch erwartet, $N. Akama sagte, dass Ihr frher oder spter kommen wrdet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10687, "deDE", "Habt Ihr die Aufgabe, mit der ich Euch betraut habe, erfllt, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10687, "deDE", "Wollen wir uns diese Waffen einmal genauer ansehen, $N. Hmmm... das ist ziemlich beunruhigend.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10688, "deDE", "Das sollte uns genug Zeit verschaffen... hoffentlich reicht sie aus, um weitere Informationen zu sammeln.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10691, "deDE", "Viele wrden fr das Buch, das Ihr bei Euch tragt, einen hohen Preis bezahlen. Es ist eine Schande, dass wir es zerstren mssen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10692, "deDE", "Habt Ihr erledigt, worum ich Euch gebeten hatte, $N?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10692, "deDE", "Ausgezeichnete Arbeit, $N! Euer Sieg ber Varedis ist ein entscheidender Schritt in unserem Krieg gegen Illidan.$B$BNach dieser Tat wird euer Name sicher die Runde machen.$B$BBitte erlaubt mir, die berreste des Buchs der teuflischen Namen an mich zu nehmen. Ich werde dafr sorgen, dass es ordnungsgem... entsorgt wird.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10702, "deDE", "Fantastisch! Wenn ich noch mehr Routinearbeiten zu erledigen habe, komme ich auf Euch zurck!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10703, "deDE", "Jep, gute Arbeit! Hier habt Ihr ein wenig Bares fr Eure Mhen. Wenn ich noch mehr Drecksarbeit zu erledigen habe, wei ich ja, wen ich rufen soll.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10705, "deDE", "Der Seher der Aschenzungen ist schon lange tot. Als Ihr Euch ber seinen Krper beugt, erkennt Ihr, dass auf dem Boden etwas geschrieben steht.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10706, "deDE", "Ja... natrlich! Jetzt wird alles klar.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10707, "deDE", "Ihr seid zurck, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10707, "deDE", "Der Kristall! Seine Macht... in meinen Hnden!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10708, "deDE", "<Als Ihr A'dal das Medaillon bergebt, verschwindet es mit einem Lichtblitz in seinem Geist.>$B$B<Fr einen kurzen Moment erscheint eine Szene vor Euch...$B$Bvielleicht aus der Zukunft. Ihr seht, wie Akama und Maiev aus dem Kerker des Wchters heraustreten und sich zum Schwarzen Tempel durchschlagen. Eine dritte, in Schatten gehllte Gestallt hilft ihnen dabei. Als Ihr genauer hinschaut, erkennt Ihr, dass Ihr diese Gestalt seid.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10744, "deDE", "<Kurdran hrt Euren Bericht an.>$B$BDas sind in der Tat gute Nachrichten! Noch vor ein paar Tagen htte ich mir nie trumen lassen, dass wir der Belagerung der Legion ein Ende machen knnten.$B$BIhr habt nicht nur ihren Angriff verhindert, sondern auch noch ihre Maschinerie vernichtet! Fr diesen Heldenmut habt Ihr den Dank der Wildhmmer, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10745, "deDE", "<Der Oberanfhrer hrt Euren Bericht an.>$B$BAusgezeichnete Nachrichten! Den Angriffen der Legion standzuhalten nahm einfach viel zu viele Leute in Anspruch. Ich habe zwar nie geglaubt, dass meine Leute versagen wrden, aber sie standen unter groer Belastung.$B$BIhr habt heute bewiesen, wie wichtig Ihr fr die Horde seid. Die Wache der Kor'kron wird sich jederzeit geehrt fhlen, an Eurer Seite zu kmpfen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10750, "deDE", "Diese lstigen Hunde planen etwas Heimtckisches. Wir mssen weitere Nachforschungen anstellen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10751, "deDE", "Ihr werdet es nicht glauben, Grunzer!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10759, "deDE", "<Der Arakkoa kichert.>$B$BJa, ich bin der Deserteur, den der Kommandant in seinem Tagebuch erwhnt. Ich habe auf den Tag gewartet, an dem auch andere die Bedrohung erkennen, die von meinen einstigen Brdern ausgeht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10760, "deDE", "Was habt Ihr herausgefunden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10760, "deDE", "<Der Unteroffizier untersucht die Tagebuchseiten.>$B$BHier steht nicht viel Sinnvolles, aber ein paar Eintrge knnten sich als ntzlich erweisen. Wie es aussieht waren die Arakkoa dabei, ein kompliziertes magisches Ritual vorzubereiten, bis sie starben, aber warum? Hier steht auch etwas Merkwrdiges ber einen 'Deserteur'.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10761, "deDE", "<Der Arakkoa kichert.>$B$BJa, ich bin der Deserteur, den der Kommandant in seinem Tagebuch erwhnt. Ich habe auf den Tag gewartet, an dem auch andere die Bedrohung erkennen, die von meinen einstigen Brdern ausgeht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10765, "deDE", "Was habt Ihr zu berichten, Soldat?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10765, "deDE", "Sie versuchen also, sich die Gunst der Riesen zu erschleichen, was? Ich habe einen Plan, der diese unheilige Verbindung ganz schnell wieder zerreien wird.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10766, "deDE", "Es freut mich, Euch kennen zu lernen, $N. Mein Name ist Plexi.$B$BIch werde Euch nicht mit Hflichkeiten langweilen. Wir wissen beide, warum wir hier sind. Und nun an die Arbeit.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10767, "deDE", "Ihr msst derjenige sein, von dem Blutwache Gulmok gesprochen hat. Ich habe bereits einen Plan, aber ich brauche Eure Hilfe, um die Vorbereitungen abzuschlieen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10768, "deDE", "Was habt Ihr zu berichten, Soldat?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10768, "deDE", "Aha! Die werden funktionieren.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10769, "deDE", "Ihr seid zurck!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10769, "deDE", "Gut, gut, gut... Wenn das nicht der Grunzer ist, den ich zu den Feldern geschickt habe. Ihr seid heil zurck.$B$B<Or'barokh nickt.>$B$BIch wusste, dass Ihr mich nicht enttuschen wrdet, Soldat. Ihr habt ein entscheidendes Vorrcken der Illidari aufgehalten und Euch damit eine Belohnung verdient.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10772, "deDE", "Diese lstigen Hunde planen etwas Heimtckisches. Wir mssen weitere Nachforschungen anstellen...", 26972);
