INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10776, "deDE", "Gut, gut, gut... Wenn das nicht der Soldat ist, den ich zu den Feldern geschickt habe. Ihr seid heil zurck.$B$B<Yoregar nickt.>$B$BIch wusste, dass Ihr mich nicht enttuschen wrdet, Soldat. Ihr habt ein entscheidendes Vorrcken der Illidari aufgehalten und Euch damit eine Belohung verdient.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10777, "deDE", "Habt Ihr das Totem von Asghar beschafft?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10777, "deDE", "<Parshah untersucht das Totem.>$B$BGut. Dieses alte Totem hat seine Macht ber die Jahre nicht eingebt, doch es alleine reicht noch nicht aus, um den Dunklen Rat aufzuhalten.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10778, "deDE", "Konntet Ihr die Rute beschaffen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10778, "deDE", "Gut gemacht. Ein solcher Schatz verdient besseres, als in einer dunklen Kiste weggesperrt zu werden. Wir werden ihn fr einen guten Zweck einsetzen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10780, "deDE", "Habt Ihr die Federn fr den Stab schon gesammelt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10780, "deDE", "<Parshah nimmt Euch die aschebestubten Federn ab und schttelt den Kopf.>$B$BEs wre besser gewesen, wenn ich an diesem Tag gestorben wre, aber die Dinge sind niemals so einfach. Dass Gul'dans Magie sie in etwas verwandelt hat, das sie aufzuhalten versuchten, ist ein grausames Spiel des Schicksals.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10781, "deDE", "Wir werden weitere Nachforschungen ber das Purpursiegel anstellen. Sie stellen eine Bedrohung fr alles Leben in Draenor dar. Ich sehe einen Tag voraus, an dem ihr dunkler Herr fr seine Verbrechen geradestehen muss. Vielleicht seid Ihr ja diejenige, der ihn der Gerechtigkeit zufhren wird, $N.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10782, "deDE", "Seid Ihr mit dem magieerfllten Kopfstck zurck?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10782, "deDE", "<Parshah nimmt Euch das Kopfstck ab und hlt es behutsam in den Hnden.>$B$BDieses Kopfstck enthlt die Macht, die wir brauchen, um die Plne des Dunklen Rats im Basislager der Sketh'lon zunichtezumachen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10793, "deDE", "<General Auralion schwebt in der Mitte des Kristalls.>$B$BWa... Was tut Ihr hier?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10793, "deDE", "Ihr habt ihren Eroberer gettet? Dann haben wir nicht viel Zeit...$B$BDas Purpursiegel besteht aus der Elite von Illidans Dienern. Sie stehen am oberen Ende der Befehlskette fr Illidans Armeen auerhalb des Schwarzen Tempels. Diese Soldaten stehen in direktem Kontakt mit dem Verrter und fhren seine Anweisung ohne Fragen zu stellen aus.$B$BSie sind diejenigen, die ohne mit der Wimper zu zucken einen Angriff auf diese Welt starten und dabei alles, was ihnen in Wege steht, dem Erdboden gleichmachen wrden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10804, "deDE", "Ihr seid ein freundlicher und mitfhlender $R, mein Freund.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10807, "deDE", "Ehre den Sehern, $N. Ihr habt den Willen des Lichts ausgefhrt und Illidans Anhnger vernichtet.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10808, "deDE", "Ist es vollbracht, $N? Habt Ihr den Dunklen Rat davon abgehalten, das Ritual zu vollenden?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10808, "deDE", "Ich habe jahrelang nach einem Weg gesucht, um den Dunklen Rat aufzuhalten. Ihr habt geschafft, wobei ich versagt habe. Die finsteren Rituale des Dunklen Rats werden nie wieder zu einer Bedrohung fr uns.$B$BIch fhle mich oft schuldig, weil ich meine Kameraden vor all diesen Jahren verlassen habe, aber nun wei ich endlich, dass es die richtige Entscheidung war. Htte mein Gewissen damals nicht zu mir gesprochen, wre ich nun vielleicht ein Schatten unter ihnen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10811, "deDE", "Meine Kinder haben mir von Eurer heldenhaften Tat erzhlt, $R. Es gibt noch mehr, was Ihr tun knnt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10814, "deDE", "Werdet Ihr uns also helfen?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10816, "deDE", "Ausgezeichnete Arbeit, $C. Mge das Licht Euch beschtzen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10817, "deDE", "Ihr habt meinen hchsten Respekt verdient, $N.$B$BEinen Hllenorc alleine zu tten ist schon keine leichte Aufgabe. Eine ganze Meute von ihnen zu tten ist, nun... heroisch.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10836, "deDE", "Gut gemacht, $N. Ich habe einen Weg gefunden, wie wir die versklavten Drachen in der Festung des Drachenmals befreien knnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10837, "deDE", "Ich brauche Netherrankenkristalle.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10837, "deDE", "<Neltharaku nimmt Euch die Kristalle ab.>$B$BIch werde nun die Kristalle mit meiner Magie erfllen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10854, "deDE", "Verschwendet keine Zeit, wir haben viel zu tun!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10854, "deDE", "<Neltharaku lacht.>$B$BDie Orcs mssen einen ganz schnen Schreck bekommen haben, als die Drachen sich pltzlich gegen sie gewendet haben. Gut gemacht, $N. Vielleicht seid Ihr wirklich der Held unseres Schwarms.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10858, "deDE", "Es sind die Ketten. Sie berauben mich meiner Macht. Der einzige Ausweg ist, den Schlssel von Zuluhed zu beschaffen und mich loszuketten.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10866, "deDE", "Ich bin frei! Ihr habt uns gerettet, $N!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10870, "deDE", "Ihr habt Euch als Verbndeter der Netherschwingen erwiesen. Meine Mutter ist frei und meine Brder und Schwestern gerettet!$B$BVielleicht werde ich Euch eines Tages erneut rufen. Wenn dieser Tag kommt, werdet Ihr mit offenen Armen begrt werden, als Bruder des Drachenschwarms der Netherschwingen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10883, "deDE", "Die Festung der Strme ist eine Erfindung der Naaru, Sterbliche. Zutritt zu der Festung zu gewhren lag schon immer alleine im Ermessen der Naaru. Eine Rckkehr zur Festung der Strme erschien uns jedoch unntig. Vielleicht mag das fr eine Sterbliche Rasse wie Stolz aussehen, doch wir Naaru halten nur wenig von solchen Dingen.$B$BWir gehen nun, weil wir es mssen. Kael'thas darf die Litanei der Verdammnis niemals vorlesen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10884, "deDE", "<A'dal grt Euch.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10884, "deDE", "Gut gemacht, $N. Ihr habt die Prfung des Erbarmens abgeschlossen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (10885, "deDE", "<A'dal grt Euch.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10885, "deDE", "Gut gemacht, $N. Ihr habt die Prfung der Strke abgeschlossen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10886, "deDE", "Gut gemacht, $N. Ihr habt die Prfung der Zuverlssigkeit abgeschlossen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10888, "deDE", "Ihr habt Euch als Wesen mit groer Macht bewiesen. Die Naaru gewhren Euch Einlass in die Festung der Strme.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10944, "deDE", "Ich danke Euch, $N. Olum ist einer meiner engsten Freunde und meiner treuesten Anhnger. Sein Beitrag zu unserem Werk ist fr unsere Plne unverzichtbar.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10949, "deDE", "<Xi'ri scheint Eure Anwesenheit zu bemerken. Offensichtlich konzentriert er sich auf die Schlacht in der Umgebung.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (10985, "deDE", "<Xi'ri pulsiert vor Energie, Ihr fhlt, wie ein Gegenstand in Euren Hnden Gestalt annimmt. Es ist das Medaillon, das Ihr A'dal im Namen von Akama gegeben habt. Seine Macht hat sich durch die Magie der Naaru noch verstrkt. Die Sha'tar haben Euch das Medaillon von Karabor anvertraut, und damit den Zugang zum Schwarzen Tempel.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11012, "deDE", "Unser Land ist verloren. Man entfhrt unsere Kinder in der Nacht, versklavt sie - oder schlimmer...$B$B<Mordenais Stimme wird angespannt.>$B$BEs ist an der Zeit, mein Freund. Zeit, uns wieder das zu nehmen, was einst unser war! Ihr habt Euch als tapferer Kmpfer und verstndnisvolle Seele erwiesen.$B$BWerdet Ihr schwren, dem Drachenschwarm der Netherschwingen beim Kampf um unser Land und den Wiederaufbau unserer Heimsttten zu helfen? Werdet Ihr schwren, ber den Schwarm zu wachen, damit unser Fleisch und Blut aufs Neue zum Leben erwachen kann? Schwrt und lasst uns im Namen unserer Heimat und unserer Familien kmpfen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11013, "deDE", "<Mor'ghor grunzt.>$B$BIhr seid spt...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11013, "deDE", "<Mor'ghor berfliegt das Schreiben und lacht.>$B$BEin Todesritter? Ihr knnt einem Todesritter nicht mal das Wasser reichen, Wurm.$B$B<Mor'ghor mustert Euch scharf.>$B$BNun gut... Ihr knntet es vielleicht noch zum Peon schaffen. Im schlimmsten Fall werfe ich Euch in die Grube und lasse Euch dort verrotten...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11014, "deDE", "Varkule findet schon was fr Euch, Grunzer. Euch steht eine Aufgabe zu, die Euren Fertigkeiten am besten gerecht wird und unserer Kristallfrderung zu Gute kommt. Ihr msst Euren Arbeitsauftrag einmal am Tag ausfllen.$B$BJa, es gibt da einen Haufen Drecksarbeit zu erledigen, aber auch die Ahnen haben mal als Peon begonnen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11015, "deDE", "Kristalle der Netherschwingen werden fr vielerlei Dinge verwendet. Hauptschlich zur Herstellung von Waffen und Rstungen...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11015, "deDE", "Das war's fr heute, Kindchen. Kommt morgen wieder, wenn Ihr noch am Leben seid.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11016, "deDE", "Wir verwenden die Blge von Schindern zum Ausfttern von Rstungen und die Herstellung von Planen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11016, "deDE", "Das war's fr heute, Kindchen. Kommt morgen wieder, wenn Ihr noch am Leben seid.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11017, "deDE", "Wie ich schon sagte, Netherstaubpollen werden von unseren Zauberern verwendet.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11017, "deDE", "Das war's fr heute, Kindchen. Kommt morgen wieder, wenn Ihr noch am Leben seid.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11018, "deDE", "Gug dort drben verwendet das Erz zum Verstrken unserer Waffen und Rstungen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11018, "deDE", "Das war's fr heute, Kindchen. Kommt morgen wieder, wenn Ihr noch am Leben seid.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11019, "deDE", "Ihr habt doch nicht etwa geglaubt, dass wir Euch den Armeen der Hllenorcs ganz alleine berlassen wrden, oder?$B$B<Yarzills Augen flackern in einem durchscheinenden Blau.>$B$BGenau, wir sind hier an Eurer Seite, $N. Bei jedem Dienst, den Ihr den Hllenorcs bei Eurem Versuch, die Weiten der Netherschwingen zurckzuerobern, erweist, werden wir dafr sorgen, dass sie einen ausreichenden Rckschlag erleiden.$B$BIhr msst Euch sehr unauffllig verhalten, um keinerlei Aufmerksamkeit whrend Euren Sabotageeinstzen auf Euch zu ziehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11020, "deDE", "Es ist eine schmutzige Arbeit, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11020, "deDE", "<Yarzill flstert ganz leise.>$B$BAusgezeichnete Arbeit, $N. Verhaltet Euch frs Erste ruhig. Kommt morgen wieder zu mir, dann knnen wir das Ganze noch einmal machen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11035, "deDE", "Tote Orcs singen nicht, $N. Stellt sicher, dass keiner der Transporter des Drachenmals entkommt.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11035, "deDE", "<Yarzill flstert.>$B$BIhr habt den Netherschwingen einen groen Dienst erwiesen, $N. Der Fall des Drachenmals rckt in greifbare Nhe. Von innen heraus breitet sich nun eine Krankheit aus - eine unheilbare Krankheit...$B$BSolltet Ihr wieder an Arbeit bei dem Himmelspfads des Drachenmals interessiert sein, so kommt morgen wieder.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11041, "deDE", "Wer wagt es, an Mor'ghor heranzutreten? Ich hoffe fr Euch, dass es von Wichtigkeit ist.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11041, "deDE", "Ihr habt also bei den berresten einer meiner Aufseher eine Anordnung gefunden und die Sache selbst in die Hand genommen? Ihr habt es geschafft, Mor'ghor zu beeindrucken. Gut gemacht...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11043, "deDE", "<Brunns Gesicht errtet, bis es nicht lnger von seinem Bart zu unterscheiden ist.>$B$BEr mchte WAS mit meinen Greifen anstellen?$B$BWenn diese kleine Ratte auch nur versucht, irgendetwas anderes als einen Sattel auf meinen Greifen zu befestigen, reise ich persnlich zum Schergrat und stopfe ihm mit seinen Raketen das Maul!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11044, "deDE", "Eine Vision sagt Ihr? Es interessiert mich nicht, ob er es in einer Vision gesehen, in einem Brief gelesen oder es aus dem Mund eines Grubenlords vernommen hat, aber er hat den Nagel auf den Kopf getroffen! Was fhrt Euch hierher, Freund? Oh, und wir sind fr jede Untersttzung dankbar!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11047, "deDE", "<Dama hrt sich Agadais Bitte an.>$B$BIch kann nicht dafr garantieren, dass wir ber gengend Windreiter verfgen, aber ich werde sehen, was ich tun kann. In der momentanen Situation haben wir Schwierigkeiten, die Nachfrage der verschiedenen Kufer und Auenposten abzudecken.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11048, "deDE", "<Gulmok beginnt zu murren, als er von Kroghans Entscheidung hrt.>$B$BIch verstehe durchaus seinen Standpunkt, doch unsere Schtzengrben besetzen sich nicht von selbst! Die Legion bombardiert uns Tag und Nacht mit Hllenbestien. Wenn Kroghan sich versptet, knnt Ihr ja seinen Platz auf dem Schlachtfeld einnehmen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11049, "deDE", "Was macht die Eierjagd?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11049, "deDE", "Neltharaku wird hchst erfreut sein, $N. Gut gemacht.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11050, "deDE", "Diese Eier drfen auf keinen Fall in die falschen Hnde geraten, $N. Wenn Ihr noch mehr Eier findet, bringt sie mir.$B$BVerhaltet Euch auf jeden Fall unauffllig und erregt blo keinen Verdacht!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11050, "deDE", "Ihr seid ein wahrer Held, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11053, "deDE", "Ich habe Eure Handlungen mit einem wachsamen Auge verfolgt, $N. Ihr habt bewiesen, dass Ihr ein unschtzbarer Gewinn fr das Drachenmal seid. Daher werde ich Euch zum Aufseher befrdern. Ein neuer Rang bedeutet natrlich auch neue Verantwortungen, denen Ihr gerecht werden msst.$B$BErhebt Euch, Aufseher $N! Erhebt Euch und sucht diejenigen auf, die Eure Hilfe hier im Basislager brauchen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11054, "deDE", "Es gilt Peons zur Ordnung zu rufen, $N! Beeilt Euch!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11054, "deDE", "Ahh! Vielleicht habt Ihr ja doch das Zeug zum Aufseher. Diese bergroe Echse war schon das Leid vieler Orcs!$B$B<Chefvorarbeiter Lehmklump beginnt mit der Herstellung der Waffe.>", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11055, "deDE", "Der Trick liegt im Handgelenk, $N.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11055, "deDE", "Ein Tag harter Arbeit! Es zahlt sich aber aus, oder? Beim Anblick der schuftenden Taugenichtse bei den Kristallen wird einem richtig Warm um's Herz... Nun ja, das ist alles fr heute. Kommt morgen wieder, dann knnt Ihr ein weiteres Mal den Schuhmerang schwingen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11063, "deDE", "Ihr mchtet also zu den Himmelsteilern gehren, ja? Ihr habt Euch mit Sicherheit als fhiger Anhnger des Drachenmals bewiesen, aber in den Lften der Scherbenwelt ist dies bedeutungslos. Es gibt eine Frage zu klren: KNNT IHR FLIEGEN?$B$BUnd ich frage nicht etwa, ob Ihr auf einen Netherdrachen steigen und wie diese Transportertrottel eine Ladung Kristalle zu der Festung liefern knnt. Ich rede davon, der Beste zu sein... der Toporc...$B$BDort drben im Westen gibt es sechs Reiter. Besiegt sie alle und ich werde Euch Flgel der Himmelsteiler geben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11064, "deDE", "Ein Kinderspiel? Was ist nur aus den Helden geworden... Ich bin mir sicher, dass die anderen Reiter eine grere Herausforderung sein werden.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11067, "deDE", "Interessant... Euer nchstes Rennen wird sich zweifellos als viel schwieriger erweisen. Corlok ist kein Weichei.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11068, "deDE", "Die nchsten zwei Reiter sind die besten Piloten, die die Zwerge und Orcs von Azeroth zu bieten hatten. Ihr habt keine Chance!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11069, "deDE", "Ich glaube es nicht! Mulverick ist Eure nchste Herausforderung. Dieses Rennen werdet Ihr auf keinen Fall berleben.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11070, "deDE", "Nun Grunzer, es sieht ganz so aus, als gbe es jetzt nur noch Euch und den Hauptmann. Viel Glck! Ihr werdet es brauchen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11071, "deDE", "Ich htte es mir nie trumen lassen, dass es jemanden gibt, der talentiert genug ist und Himmelsdonner besiegen kann. Wisst Ihr was das bedeutet, ja? Ihr seid jetzt der Toporc! Ihr msst Euren Titel verteidigen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11075, "deDE", "Die Minenarbeiter leisten harte Arbeit, aber keiner von ihnen wagt es, einen Schritt tiefer in die Minen zu setzen. Sie sterben lieber, als dass sie sich tiefer in die Minen zwingen lassen! Es wird Eure Aufgabe sein, die von ihnen vernachlssigten Pflichten zu erfllen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11076, "deDE", "Habt Ihr die Fracht eingesammelt?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11076, "deDE", "Gut gemacht, $N. Kommt morgen fr einen weiteren Einsatz in den Tiefen der Mine wieder.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11081, "deDE", "Was ist das?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11081, "deDE", "Ausgezeichnete Arbeit, Aufseher. Man wird Mor'ghor von Euren herausragenden Taten fr das Drachenmal berichten.$B$BLasst uns jetzt wieder auf diese Hunde der Finsterblut zu sprechen kommen. Sie planen ihre Flucht, ja? Sie wagen es, sich dem einzig wahren Frsten der Scherbenwelt zu widersetzen? Ihre Meister werden dafr bezahlen!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11082, "deDE", "Nun? Warum dauert das so lange?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11082, "deDE", "Nichts zu sagen? Vielleicht erinnert er sich ja, wenn er auf dem Boden dieser verfluchten Mine zu Tode blutet... Vergesst nie, was es bedeutet, die Mchte Illidans herauszufordern!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11083, "deDE", "Ja, ich verstehe das nicht... Man wrde doch meinen, dass sie nach zwei Tagen nicht derart ausgehungert sein knnen. Finsterblut...$B$B<Ronag schttelt den Kopf.>$B$BSo was von dmlich...", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11084, "deDE", "<Mor'ghor nickt.>$B$BGanz recht, ich habe Hauptmann gesagt. Ihr habt Euch eine weitere Befrderung verdient, $N.$B$BIhr arbeitet wirklich wie keiner der Hllenorcs, die ich bis jetzt gesehen habe. Macht weiter so, dann schafft Ihr es vielleicht eines Tages zum Oberanfhrer.$B$BZieht jetzt los und bernehmt das Kommando. Ich schlage vor, dass Ihr versucht, Euch Eure Flgel der Himmelsteiler zu verdienen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11086, "deDE", "Ausgezeichnete Arbeit, Hauptmann! Frst Illidan wird von Euren Heldentaten erfahren!", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11089, "deDE", "Habt Ihr die bentigten Bauelemente fr die Anfertigung der Seelenkanone?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11089, "deDE", "<Balthas fgt die Elemente, die Ihr ihm gebracht habt, geschickt zusammen.>$B$BIhr seid jetzt bereit fr den zweiten Teil des Plans, Hauptmann.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11090, "deDE", "Ist es vollbracht?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11090, "deDE", "Er wird zurckkehren, aber fr den Moment hat der Krieg der Legion gegen Frst Illidan eine Schwche erfahren. Man wird ihm von Euren Taten berichten, Hauptmann.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11092, "deDE", "Frst Illidan hat mich hchstpersnlich mit Eurer Befrderung zum Kommandanten beauftragt! So etwas hat es noch nie gegeben. Ihr seid der Erste des Drachenmals, der solch einen Rang auerhalb des Schwarzen Tempels erlangt hat.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11094, "deDE", "Beruhigt Euch, Kind.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11094, "deDE", "Es war gut, dass Ihr zu uns gekommen seid. Vielleicht knnen wir diesen Angriff des Drachenmals zu unserem Vorteil nutzen und den Spie umdrehen.", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11095, "deDE", "<Hobb nickt.>", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11095, "deDE", "<Hobb grinst.>$B$BDie tdlichste Falle aller Zeiten...", 26972);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (11097, "deDE", "Habt Ihr sie mit Euren Fusten zerschmettert, Kommandant?", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11097, "deDE", "<Mor'ghor lacht.>$B$BHAHA! Ich habe gewusst, dass ich auf Euch zhlen kann, Kommandant! Ich mchte, dass Ihr das Sanktum der Sterne mindestens einmal pro Tag berfallt. Sie werden keine andere Wahl haben und das Schattenmondtal verlassen mssen!", 26972);
