INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11101, "deDE", "<Mor'ghor lacht.>$B$BHAHA! Ich habe gewusst, dass ich auf Euch zhlen kann, Kommandant! Ich mchte, dass Ihr den Altar der Sha'tar mindestens einmal pro Tag berfallt. Sie werden keine andere Wahl haben und das Schattenmondtal verlassen mssen!", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11107, "deDE", "Ihr seid der erste Hochlord des Drachenmals, $N. Der Meister hat beschlossen hchstpersnlich hierher zu kommen, um Euch zu befrdern.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11108, "deDE", "Ich war der Erste, wisst Ihr. Das verlassene Kind eines Monsters...$B$B<Barthamus schttelt seinen Kopf.>$B$BWir haben Euch alles zu verdanken, $N. Ganz allein habt Ihr das Reich des Drachenmals zerschmettert und dabei genug Informationen gesammelt, die meine Brder mehr als ein Leben lang beschftigen werden.$B$BEs ist zwar nicht viel, aber es gibt da etwas, das wir Euch anbieten knnen... Ein jeder meines Schwarms hat sich dazu bereit erklrt, Euch bei Euren Abenteuern in der Scherbenwelt zu begleiten. Fragt sie einfach und sie werden mit Euch einen Bund eingehen.", 26972);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (11109, "deDE", "Es ist eine Ehre, Euch zu treffen, $N. Wir alle haben Eure Abenteuer mit groer Aufmerksamkeit verfolgt. Wir sind uerst dankbar fr alles, was Ihr fr den Drachenschwarm der Netherschwingen getan habt.$B$BWenn Ihr mich zu Eurem Weggefhrten macht, werde ich Euch bis ans Ende dieser Welt und wieder zurck fliegen. Was auch immer Ihr von mir verlangt, ich werde mein Bestes tun, um Euren Wunsch zu befolgen.$B$BIhr sollt jedoch wissen, dass Ihr nur einen von uns fr Eure Reisen in der Scherbenwelt erwhlen knnt. Habt Ihr Euch einmal entschieden, so knnt Ihr Eure Meinung nicht mehr ndern.", 26972);
