INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14022, "deDE", "Wenn Ihr an der Zubereitung traditioneller Pilgerfreudengerichte interessiert seid, kann ich Euch helfen. Falls Ihr noch keine Kochkenntnisse besitzt, braucht ihr nur ein wenig zu ben und im Nu seid Ihr ein richtiger Koch!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14023, "deDE", "Wir knnen immer etwas von der kstlichen Gewrzbrotfllung gebrauchen, sie ist ausgesprochen beliebt.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14023, "deDE", "Ausgezeichnet. Genau was wir brauchen! Es ist harte Arbeit, dafr zu sorgen, dass die Tische niemals leer werden, doch es lohnt sich!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14024, "deDE", "Mehr Krbiskuchen? Genau rechtzeitig.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14024, "deDE", "Der Krbiskuchen ist hier oben ausgesprochen beliebt. Ich habe noch nie zuvor gesehen, dass Zwerge ber ein Gemsegericht so aus dem Huschen geraten!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14028, "deDE", "Rieche ich da etwa Moosbeerenchutney?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14028, "deDE", "Endlich, das versprochene Moosbeerenchutney! Ihr glaubt ja nicht, wie schnell die Feiernden das immer verputzen!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14030, "deDE", "Seid Ihr gekommen, um am Pilgerfreudenfestmahl teilzunehmen?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14030, "deDE", "Vielen Dank fr alles! Ich hatte schon begonnen, mir Sorgen zu machen.$B$BDa Ihr schon mal hier seid, knntet Ihr gleich auch noch Euer Glck mit der Zubereitung kandierter Skartoffeln versuchen.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14033, "deDE", "$N, wie schn, Euch zu sehen.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14033, "deDE", "Aaah, kandierte Skartoffeln! Meine Leibspeise! Hat Isaac Euch davon erzhlt?", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14035, "deDE", "Was hat Euch denn wieder nach Darnassus verschlagen, $N?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14035, "deDE", "Die sind grandios! Und sie kommen genau rechtzeitig, um hungrige Muler zu stopfen. Danke, $N! Ihr habt wahrlich alle Gnge eines traditionellen Pilgerfestmahls gemeistert.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14036, "deDE", "Wenn Ihr an der Zubereitung traditioneller Pilgerfreudengerichte interessiert seid, kann ich Euch helfen. Falls Ihr noch keine Kochkenntnisse besitzt, braucht ihr nur ein wenig zu ben und im Nu seid Ihr ein richtiger Koch!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14037, "deDE", "Die Tafel knnte wirklich noch etwas mehr Gewrzbrotfllung vertragen.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14037, "deDE", "Dafr zu sorgen, dass die ganze Festtafel immer schn gefllt bleibt, ist keine einfache Aufgabe. Und wenn man bedenkt, dass wir eigentlich gar nicht mehr zu essen brauchen, kommt es einem besonders eigenartig vor.$B$B<William zuckt mit den Achseln.>$B$BTradition ist Tradition.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14040, "deDE", "Wir knnen immer frischen Krbiskuchen gebrauchen.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14040, "deDE", "Der Kuchen geht weg wie warme Semmeln. Ich glaube ja, die Leute halten sich gar nicht erst mit dem Hauptgericht auf, sondern gehen gleich ber zum Nachtisch. Habt Ihr den Kuchen denn schon selbst einmal versucht?", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14041, "deDE", "Wie gefallen Euch die Pilgerfreuden, $C?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14041, "deDE", "Ah, endlich, mehr Moosbeerenchutney! Es ist unfassbar, wie schnell die Feiernden es vertilgen.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14043, "deDE", "Willkommen zurck, $N.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14043, "deDE", "Dokin hat Euch also dazu berredet, mit den kandierten Skartoffeln auszuhelfen?$B$B<Francis lchelt.>$B$BEs ist der einzige Weg, wie er mithalten kann. Danke, dass Ihr sie gebracht habt.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14044, "deDE", "Wie gut, Euch wiederzusehen, $N.", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14044, "deDE", "Vielen Dank fr alles, was Ihr gebracht habt. Einige der Tische machen schon langsam einen etwas kahlen Eindruck.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14047, "deDE", "Was hat Euch denn wieder nach Orgrimmar verschlagen, $N?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14047, "deDE", "Die sehen kstlich aus. Vielen Dank, $N. Ihr habt wahrlich alle Gnge eines traditionellen Pilgerfestmahls gemeistert.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14048, "deDE", "Habt Ihr ein paar Truthhne aufgesprt?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14048, "deDE", "Genau das Richtige, vielen Dank, $N. Ihr habt es genau getroffen.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14051, "deDE", "Wie sieht es mit der Fllung aus?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14051, "deDE", "Ein Glck, Ihr habt es geschafft! Ohne Gewrzbrotfllung wre das ganze Festmahl ein einziger Reinfall.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14053, "deDE", "Konntet Ihr etwas Moosbeerenchutney auftreiben?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14053, "deDE", "Danke. Das sieht wirklich kstlich aus, und Ihr seid ein ganzes Stck schneller als Jasper - aber verratet Ihm nicht, dass ich das gesagt habe.", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14054, "deDE", "Wie geht es mit dem Kuchen voran?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14054, "deDE", "Die sind wunderbar. Isaac wird begeistert sein, und nicht nur er!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14055, "deDE", "Habt ihr die kandierten Skartoffeln dabei?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14055, "deDE", "Die sind genau richtig! Vielen Dank fr Eure Hilfe, $N. Das Festmahl wird rechtzeitig fertig sein, und Ellen wird sich sicher freuen!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14058, "deDE", "Habt Ihr die kandierten Skartoffeln?", 28153);
