INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14059, "deDE", "Vielen Dank fr Eure Hilfe, und gebt mir Bescheid, falls Ihr jemals hinter das Geheimnis des stndig verschwindenden Chutneys kommen solltet!", 28153);
INSERT INTO `quest_request_items_locale` (`ID`, `locale`, `CompletionText`, `VerifiedBuild`) VALUES (14060, "deDE", "Wie steht es mit dem Kuchen?", 28153);
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14060, "deDE", "Die sind fantastisch. Danke fr Eure Hilfe, $N.", 28153);
