INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (14061, "deDE", "Prchtige Truthhne, genau was ich brauche! Ihr habt mir wirklich aus der Klemme geholfen.", 28153);
