INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (13, "deDE", "Glckwunsch", "Viele Glckwunsche bezglich eurer berzeugung, auch die 80. Saison der Abenteurer zu erreichen. Ihr seit zweifellos dem Ziel ergben, Azeroth vom Bsen zu befreien.$B$BObwohl der Weg hierher keine Kleinigkeit war, steht der wahre Kampf erst noch bevor.$B$BKmpft weiter!$B$BRhonin");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (45, "deDE", "Ihr seid rumgekommen!", "Nun seht Euch mal an!$B$BUnd ich dachte, ich htte hier an diesem eisigen Ort schon einiges gesehen! Diesem Zwerg scheint es offensichtlich, dass das Feuer eines Forschers in Euren Augen brennt.$B$BTrage diesen Wappenrock mit Stolz, dann wissen Eure Freunde, wem sie nach dem Weg fragen mssen, wenn die Zeit gekommen ist!$B$BMacht weiter so!$B$BBrann Bronzebart");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (614, "deDE", "Fr die Allianz!", "Krieg berzieht unsere Lnder. Nur die tapfersten aller Helden wagen es, der Horde dort einen Schlag zu versetzen, wo es wehtut. Ihr gehrt zu eben diesen Helden.$B$BDer Schlag, den Ihr der Fhrung der Horde versetzt habt, bereitet unseren Weg fr den finalen Angriff. Die Horde wird sich vor der Macht der Allianz verbeugen.$B$BEure Taten bleiben nicht ungewrdigt. Reitet mit Stolz!$B$B--Euer Knig");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (1021, "deDE", "Ganz schn erfolgreich", "Mir ist aufgefallen, was fr eine eindrucksvolle Sammlung an Wappenrcken Ihr mit der Zeit gesammelt habt. Ihr knnt diesen hier genauso gut Eurer Sammlung hinzufgen. Er hat in meinem Schrank nur Staub angesetzt.");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (1250, "deDE", "Stinkis neues Zuhause", "Ich habe gehrt, wie gut Ihr Euch um unsere pelzigen Freunde kmmert. Ich hoffe, es macht Euch nichts aus, aber ich muss Stinki ein neues Zuhause verschaffen. Er weigert sich einfach, friedlich mit den Anderen zu spielen.$B$BBitte stellt sicher, dass er zweimal tglich gefttert wird. Und, hm... er hat eine Schwche fr schwarze Katzen.$B$B--Breanni");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (1682, "deDE", "Gre aus Donnerfels", "Eure Erfolge waren tiefgreifend und weitreichend. Azeroth profitiert bei den jngsten Tumulten in groem Mae von jenen, die das Land vom Bsen zu befreien suchen.$B$BNur diejenigen, die sich die Zeit nehmen, unser Land kennen zu lernen, verstehen das Opfer der Gefallenen und den Wert unserer Helden. Ihr seid ein solcher Held. Hoffentlich werdet Ihr Euch noch in vielen Jahren an die Geschichten Eurer Abenteuer erinnern knnen.$B$BHabt Dank, Meister der Lehren.$B$BFr die Horde!$B$B--Cairne Bluthuf");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (1956, "deDE", "Hohe Schule", "Herzlichen Glckwunsch zu Eurem Abschluss in den Schulen der arkanen Magie. In Anerkennung Eurer Hingabe lege ich diesen speziellen Buchband bei, der die Serie vervollstndigt.$B$BIch denke, dass Ihr diesen Folianten sehr unterhaltsam finden werdet. Doch das drft Ihr selbst entscheiden.$B$BHochachtungsvoll,$B$BRhonin");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2096, "deDE", "Ruhm des Helden", "Held,$B$BErzhlungen der groen taten, die Ihr seit eurem Eintreffen in Nordend vollbracht habt, sind bis zum Wyrmruhtempel vorgedrungen.$B$BEure Tapferkeit soll nicht unbemerkt bleiben. Bitte aktzeptiert dieses Geschenk im Namen der Aspekte. Mgen wir zusammen Azeroth ein fr alle Mal vom Bsen befreien.$B$BAlexstrasza, die Lebensbinderin");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2143, "deDE", "Fhren der Kavalerie", "Ich konnte nicht umhin, zu sehen wie gut Ihr mit Tieren umgeht. Bei allem was hier vorgeht, laufen meine Geschfte besser denn je.$B$BIch nehme nicht an, dass es Euch etwas ausmacht, fr mich auf diesen Albinodrachen aufzupassen. Ich habe einfach nicht genug Zeit, mich um all diese Tiere zu kmmern.$B$BViele Gre.$B$BMei");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2144, "deDE", "Ein ums andere Mal", "Dadurch, dass in der Ferne immer die Kriegstrommeln ertnen, vergessen die Bewohner Azeroths nur allzu leicht all die Dinge, die das Leben zu bieten hat.$B$BIhr habt hingegen mit Eurer Fhigkeit, Euch daran zu erinnern, wofr wir letztlich kmpfen, die Erhabenheit der guten Vlker Azeroths bewahrt. Unsere Siege nicht zu feiern wre eine weitere Form der Niederlage. Denkt immer daran, Feiernder.$B$BMgen andere von Eurem Frohsinn inspiriert werden.$B$BAlextrasza, die Lebensbinderin");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2145, "deDE", "Ein ums andere Mal", "Dadurch, dass in der Ferne immer die Kriegstrommeln ertnen, vergessen die Bewohner Azeroths nur allzu leicht all die Dinge, die das Leben zu bieten hat.$B$BIhr habt hingegen mit Eurer Fhigkeit, Euch daran zu erinnern, wofr wir letztlich kmpfen, die Erhabenheit der guten Vlker Azeroths bewahrt. Unsere Siege nicht zu feiern wre eine weitere Form der Niederlage. Denkt immer daran, Feiernder.$B$BMgen andere von Eurem Frohsinn inspiriert werden.$B$BAlextrasza, die Lebensbinderin");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2516, "deDE", "Wie man sich Freunde macht", "Hallo!$B$BWie ich gehrt habe, habt Ihr dem kleinen Stinker ein warmes und liebevolles Zuhause geschaffen... Ich frage mich nun, ob Ihr vielleicht Interesse daran httet, ein weiteres, eigensinniges Waisenkind aufzunehmen?$B$BDieses kleine Kitz ist zwar ein wenig schchtern, doch sollte es Euch dank des mitgesandten Gegenstandes ein leichtes sein, seine Freundschaft zu gewinnen: seinem Lieblingsleckstein!$B$B--Breanni");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2536, "deDE", "Tierisch viele Reittiere", "Ich habe gehrt, dass Eure Stlle inzwischen fast so gro sind wie meine. Beeindruckend! Vielleicht knnten wir einander aushelfen...$B$BIch habe einen Drachenfalken zuviel und hoffe, dass Ihr ihm ein neues Zuhause geben knnt. Natrlich ist es zum Reittier und nicht zum Jagdtier ausgebildet worden und Ihr werdet sehen, dass es Euch genauso loyal und unermdlich wie jedes meiner anderen Tiere zur Seite stehen wird.$B$BMit freundlichen Gren.$B$BMei");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (2537, "deDE", "Tierisch viele Reittiere", "Ich habe gehrt, dass Eure Stlle inzwischen fast so gro sind wie meine. Beeindruckend! Vielleicht knnten wir einander aushelfen...$B$BIch habe einen Drachenfalken zuviel und hoffe, dass Ihr ihm ein neues Zuhause geben knnt. Natrlich ist es zum Reittier und nicht zum Jagdtier ausgebildet worden und Ihr werdet sehen, dass es Euch genauso loyal und unermdlich wie jedes meiner anderen Tiere zur Seite stehen wird.$B$BMit freundlichen Gren.$B$BMei");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (3478, "deDE", "Der vergessene Puter", "Knnt Ihr begreifen, wie es dieser fette Truthahn es lebendig durch den November geschaft hat?!$B$BAlle seine Freunde sind auf den reich gedeckten Tischen serviert worden. Mit Moosbeerenchutney und Gewrzbrotfllung und... OOOH... jetzt bekomme ich Hunger. Egal! Er ist jetzt ganz allein. Deswegen habe ich berlegt, ob Ihr Euch vielleicht um Ihn kmmern wrdet. In meinem Laden ist einfach nicht mehr genug Platz fr ihn!$B$BHaltet ihn nur bitte von Kochstellen fern. Er bekommt in ihrer Nhe immer so einen merkwrdigen Blick...$B$B--Breanni");
