INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (4478, "deDE", "Streuner", "Werter Meister der Geduld, wir mchten Eure Beharrlichkeit belohnen, mit der Ihr immer wieder Dungeons mit Leuten betretet, die Ihr wahrscheinlich noch nie zuvor getroffen habt. Hoffentlich habt Ihr ein paar Jungspunden zeigen knnen, wie der Hase luft.$B$BLange Rede, kurzer Sinn. Wir haben zufllig gehrt, dass Ihr mit Zufallsgruppen herumstreunt. Und wie der Zufall so will ist hier also ein kleiner Streuner, den wir Euch zufallen lassen, um mit Euch zu streunen, whrend Ihr zuflligen Zufllen zufallt, oder so.$B$BKnuddels.$B$BDas Entwickler-Team von WoW");
INSERT INTO `achievement_reward_locale` (`ID`, `Locale`, `Subject`, `Body`) VALUES (4784, "deDE", "Emblem Rstmeister in Dalaran's Silbernen Enklave", "Eure Erfolge in Nordend sind nicht unbemerkt geblieben, Freund.$B$BDie verdienten Embleme knnen benutzt werden, um Gegenstnde bei den verschiedenen Rstmeistern fr Embleme zu kaufen.$B$BIhr findet uns in der Silbernen Enklave, wo jede Emblemart einen eigenen Rstmeister hat.$B$BWir freuen uns auf Eure Ankunft!");
