(7101,0,0,"What is it that you do exactly, Rohan?",12003,1,1,7118,0,0,0,"",0,0),
(7118,0,0,"So what brings you to Light's Hope?",12005,1,1,7119,0,0,0,"",0,0),
(7119,0,0,"What? Bonescythe?",12007,1,1,7120,0,0,0,"",0,0),
