(7104,0,0,"Hello, Rayne. What brings the Cenarion Circle to the Plaguelands?",12015,1,1,7123,0,0,0,"",0,0),
(7123,0,0,"Are these lands not beyond healing? They look pretty beat up.",12017,1,1,7124,0,0,0,"",0,0),
