(5812,0,0,"Are you the Earthshaper capable of creating Elementium?",9576,1,1,5813,0,0,0,"",0,0),
(5813,0,0,"What do you know of it, Franzahl?",9578,1,1,5814,0,0,0,"",0,0),
(5814,0,0,"A fissure?",9580,1,1,5815,0,0,0,"",0,0),
